INSERT INTO `contents` VALUES (1,'Hơn 90% doanh nghiệp Việt xem nhân viên là tài sản giá trị nhất khi chuyển đổi số',1,'Hơn 90% doanh nghiệp Việt xem nhân viên là tài sản giá trị nhất khi chuyển đổi số','<p><a href=\"https://chuyendoiso.vgasoft.vn/tai-lieu/tai-lieu-tham-khao/cong-van-ve-viec-tuyen-truyen-ket-qua-ky-hop-thu-4-quoc-hoi-khoa-xv\">Tại đ&acirc;y</a></p>\n','[{\"file\":\"/images/20221219/vnnic.jpg\",\"extension\":\"jpg\"}]',8,1,NULL,'2022-12-22 13:33:53',1,'hon-90-doanh-nghiep-viet-xem-nhan-vien-la-tai-san-gia-tri-nhat-khi-chuyen-doi-so',NULL,NULL),(2,'Thủ tướng: \"Chuyển đổi số phát huy sức mạnh dân tộc và sức mạnh thời đại\"',3,'Đến hết năm 2022, TP.HCM sẽ triển khai đủ 25 dịch vụ công thiết yếu theo Đề án 06 và là điểm nhấn quan trọng, thuận tiện cho công dân thực hiện các thủ tục hành chính trên dichvucong.hochiminhcity.gov.vn...','Du lịch tp HHCM','[{\"file\":\"/images/20221219/thu_tuong_pmc_551.jpg\",\"extension\":\"jpg\"}]',6,1,'2022-11-22 16:56:27','2022-11-22 16:56:27',1,'thu-tuong-chuyen-doi-so-phat-huy-suc-manh-dan-toc-va-suc-manh-thoi-dai',NULL,NULL),(3,'TP.HCM đặt mục tiêu tăng trưởng kinh tế 7,5-8% trong năm 2023',3,'TP.HCM đặt mục tiêu tăng trưởng kinh tế 7,5-8% trong năm 2023',NULL,'[{\"file\":\"/images/20221219/1670223237132hoi_nghi_thanh_uy_tphcm_lan_thu_19_bi_thu_thanh_uy_nguyen_van_nen.jpg\",\"extension\":\"jpg\"}]',4,1,'2022-12-19 15:11:11','2022-12-19 15:11:11',1,'tphcm-dat-muc-tieu-tang-truong-kinh-te-758-trong-nam-2023',NULL,NULL),(4,'Khởi nghiệp, đổi mới sáng tạo là nguồn lực quan trọng phát triển quốc gia',3,'Khởi nghiệp, đổi mới sáng tạo là nguồn lực quan trọng phát triển quốc gia',NULL,'[{\"file\":\"/images/20221219/1670224351037thu_tuong_pham_minh_chinh_techfest_2022.jpg\",\"extension\":\"jpg\"}]',9,1,'2022-12-19 15:11:52','2022-12-19 15:11:52',1,'khoi-nghiep-doi-moi-sang-tao-la-nguon-luc-quan-trong-phat-trien-quoc-gia',NULL,NULL),(5,'Phát triển đô thị thông minh là chuyển đổi số trong phạm vi đô thị, lấy người dân làm trung tâm',3,'Phát triển đô thị thông minh là chuyển đổi số trong phạm vi đô thị, lấy người dân làm trung tâm',NULL,'[{\"file\":\"/images/ok/167022364516020221201_pg1_TT.jpg\",\"extension\":\"jpg\"}]',2,1,'2022-12-19 15:12:33','2022-12-19 15:12:33',1,'phat-trien-do-thi-thong-minh-la-chuyen-doi-so-trong-pham-vi-do-thi-lay-nguoi-dan-lam-trung-tam',NULL,NULL),(6,'Cải cách hành chính: Còn phiền hà cho người dân',1,'',NULL,'[{\"file\":\"/images/ok/article.jpg\",\"extension\":\"jpg\"}]',3,1,'2022-12-19 15:13:07','2022-12-19 15:13:07',1,'cai-cach-hanh-chinh-con-phien-ha-cho-nguoi-dan',NULL,NULL),(7,'Quét mã QR phản ánh thái độ phục vụ của cán bộ, công chức, viên chức',1,'','<div class=\"singular-content\">\n<p>Sau khi đại diện VKS ho&agrave;n th&agrave;nh việc c&ocirc;ng bố bản c&aacute;o trạng, HĐXX tiến h&agrave;nh x&eacute;t hỏi c&aacute;c bị c&aacute;o trong phi&ecirc;n t&ograve;a sơ thẩm vụ &aacute;n &quot;Vi phạm quy định về đấu thầu g&acirc;y hậu quả nghi&ecirc;m trọng&quot;, xảy ra tại Bệnh viện Đa khoa tỉnh Đồng Nai, C&ocirc;ng ty AIC v&agrave; c&aacute;c đơn vị li&ecirc;n quan.</p>\n\n<div style=\"text-align:center\">\n<figure class=\"image\" style=\"display:inline-block\"><img alt=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 1\" data-adbro-processed=\"true\" data-content-name=\"article-content-image\" data-content-piece=\"article-content-image_2271528\" data-content-target=\"/phap-luat/vu-an-aic-cuu-bi-thu-dong-nai-danh-gia-ba-nhan-thong-minh-quan-he-rong-20221221174004396.htm\" data-height=\"415\" data-original=\"https://icdn.dantri.com.vn/2022/12/21/cong-ty-aic-21122022-1671618474600.jpeg\" data-photo-id=\"2271528\" data-track-content=\"\" data-width=\"665\" src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/cong-ty-aic-21122022-1671618474600.jpeg\" title=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 1\" />\n<figcaption>To&agrave;n cảnh phi&ecirc;n t&ograve;a. (Ảnh: TTXVN).</figcaption>\n</figure>\n</div>\n\n<p><strong>C&aacute;c bị c&aacute;o tại AIC n&oacute;i về Quy tr&igrave;nh hướng dẫn &quot;th&ocirc;ng thầu&quot;</strong></p>\n\n<p>Theo c&aacute;o trạng, &quot;Quy tr&igrave;nh 70 bước thực hiện dự &aacute;n thiết bị kh&ocirc;ng c&oacute; x&acirc;y dựng&quot; được bị c&aacute;o Nguyễn Thị Thanh Nh&agrave;n (Tổng Gi&aacute;m đốc C&ocirc;ng ty AIC) y&ecirc;u cầu c&aacute;c l&atilde;nh đạo, nh&acirc;n vi&ecirc;n c&ocirc;ng ty thực hiện. Cơ quan tố tụng c&aacute;o buộc một số bước trong Quy tr&igrave;nh tr&ecirc;n c&oacute; nội dung thực hiện th&ocirc;ng thầu v&agrave; gian lận trong đấu thầu, tr&aacute;i quy định của Luật Đấu thầu.</p>\n\n<p>Chủ tọa phi&ecirc;n t&ograve;a - thẩm ph&aacute;n Mai Văn Quang đ&atilde; đặt ra c&acirc;u hỏi về Quy tr&igrave;nh n&agrave;y đối với nhiều bị c&aacute;o.</p>\n\n<p>Trong đ&oacute;, Ho&agrave;ng Thị Th&uacute;y Nga (Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty AIC) cho biết, bị c&aacute;o kh&ocirc;ng nắm được quy tr&igrave;nh tr&ecirc;n. Nữ bị c&aacute;o cũng khai quy tr&igrave;nh 70 bước &quot;kh&ocirc;ng phải l&agrave; quy tr&igrave;nh của C&ocirc;ng ty AIC hay C&ocirc;ng ty TCI&quot;.</p>\n\n<p>&quot;Quy tr&igrave;nh n&agrave;y do bị c&aacute;o Ho&agrave;ng Thế Quỳnh (Trưởng nh&oacute;m kỹ thuật) soạn ra. Ngo&agrave;i bị c&aacute;o th&igrave; 6 bị c&aacute;o kh&aacute;c (l&agrave; nh&acirc;n vi&ecirc;n do Nga quản l&yacute;) cũng kh&ocirc;ng nắm được nội dung Quy tr&igrave;nh 70 bước&quot;, Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty AIC khai.</p>\n\n<figure class=\"image\"><img alt=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 2\" data-content-name=\"article-content-image\" data-content-piece=\"article-content-image_2271531\" data-content-target=\"/phap-luat/vu-an-aic-cuu-bi-thu-dong-nai-danh-gia-ba-nhan-thong-minh-quan-he-rong-20221221174004396.htm\" data-height=\"1920\" data-ll-status=\"loaded\" data-original=\"https://icdn.dantri.com.vn/2022/12/21/z3977445099787eb92d5be1c61ccf84e43ce6b2de47193-1671618581366.jpg?watermark=true\" data-photo-id=\"2271531\" data-src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/z3977445099787eb92d5be1c61ccf84e43ce6b2de47193-1671618581366.jpg?watermark=true\" data-track-content=\"\" data-watermark=\"true\" data-width=\"2560\" src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/z3977445099787eb92d5be1c61ccf84e43ce6b2de47193-1671618581366.jpg?watermark=true\" title=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 2\" />\n<figcaption>Bị c&aacute;o Ho&agrave;ng Thị Th&uacute;y Nga. (Ảnh: Hải Nam).</figcaption>\n</figure>\n\n<p>HĐXX ngay sau đ&oacute; chất vấn Nguyễn Tấn Sỹ (nh&acirc;n vi&ecirc;n Ban 1 - thuộc quản l&yacute; của bị c&aacute;o Nga) về chi tiết Quy tr&igrave;nh 70 bước th&igrave; được bị c&aacute;o n&agrave;y x&aacute;c nhận chỉ biết sơ qua chứ kh&ocirc;ng nắm r&otilde; nội dung, chi tiết c&aacute;c bước như thế n&agrave;o.</p>\n\n<p>Bị c&aacute;o Sỹ n&oacute;i, chỉ biết mục đ&iacute;ch c&oacute; Quy tr&igrave;nh 70 bước l&agrave; để hướng dẫn triển khai c&aacute;c dự &aacute;n của c&ocirc;ng ty. Đối với bị c&aacute;o L&ecirc; Ch&iacute; Tu&acirc;n (Trưởng nh&oacute;m Hồ sơ dự thầu - thuộc quản l&yacute; của bị c&aacute;o Nga), người n&agrave;y cũng khai chỉ nghe qua về Quy tr&igrave;nh 70 bước chứ kh&ocirc;ng r&otilde; nội dung b&ecirc;n trong.</p>\n\n<p>HĐXX sau đ&oacute; x&eacute;t hỏi bị c&aacute;o Ho&agrave;ng Thế Quỳnh về nội dung &quot;Quy tr&igrave;nh 70 bước thực hiện dự &aacute;n thiết bị kh&ocirc;ng c&oacute; x&acirc;y dựng&quot;. Bị c&aacute;o n&agrave;y cho biết anh ta được l&atilde;nh đạo c&ocirc;ng ty phổ biến về mục đ&iacute;ch quy tr&igrave;nh l&agrave; để hướng dẫn triển khai dự &aacute;n, hướng dẫn việc phối hợp giữa c&aacute;c ph&ograve;ng ban trong c&aacute;c dự &aacute;n của c&ocirc;ng ty.</p>\n\n<p>&quot;Quy tr&igrave;nh 70 bước được phổ biến rộng r&atilde;i trong c&ocirc;ng ty, bị c&aacute;o chỉ l&agrave; một trong những người được biết v&agrave; cũng chỉ biết thực hiện theo những bước trong quy tr&igrave;nh&quot;, bị c&aacute;o Quỳnh khai.</p>\n\n<p>Về quy tr&igrave;nh tham gia đấu thầu Dự &aacute;n Đầu tư x&acirc;y dựng Bệnh viện Đồng Nai, HĐXX truy vấn bị c&aacute;o Nga về c&aacute;c bước, cũng như vai tr&ograve; của bị c&aacute;o.</p>\n\n<p>Trả lời HĐXX, Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty AIC khai, b&agrave; ta v&agrave; nh&oacute;m bị c&aacute;o thuộc quản l&yacute; của m&igrave;nh sẽ mua Hồ sơ mời thầu khi Chủ đầu tư th&ocirc;ng b&aacute;o mời thầu. Sau đ&oacute;, bị c&aacute;o sẽ đ&aacute;nh gi&aacute; c&aacute;c g&oacute;i thầu xem c&oacute; ph&ugrave; hợp với c&ocirc;ng ty hay kh&ocirc;ng. Với những g&oacute;i thầu ph&ugrave; hợp, bị c&aacute;o sẽ l&agrave;m hồ sơ dự thầu, thực hiện c&aacute;c bước thương thảo, k&yacute; hợp đồng... theo ủy quyền của Tổng Gi&aacute;m đốc.</p>\n\n<figure class=\"image\"><img alt=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 3\" data-content-name=\"article-content-image\" data-content-piece=\"article-content-image_2271533\" data-content-target=\"/phap-luat/vu-an-aic-cuu-bi-thu-dong-nai-danh-gia-ba-nhan-thong-minh-quan-he-rong-20221221174004396.htm\" data-height=\"1201\" data-ll-status=\"loaded\" data-original=\"https://icdn.dantri.com.vn/2022/12/21/img0247-1654168955116160099679-1671618646126.jpeg\" data-photo-id=\"2271533\" data-src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/img0247-1654168955116160099679-1671618646126.jpeg\" data-track-content=\"\" data-width=\"2000\" src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/img0247-1654168955116160099679-1671618646126.jpeg\" title=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 3\" />\n<figcaption>Bệnh viện Đa khoa Đồng Nai.</figcaption>\n</figure>\n\n<p>Theo lời khai của Nga, bị c&aacute;o n&agrave;y được b&agrave; Nh&agrave;n nhiều lần đưa đi gặp c&aacute;c l&atilde;nh đạo tỉnh Đồng Nai, l&atilde;nh đạo c&aacute;c sở, ban, ng&agrave;nh, trong đ&oacute;, &ocirc;ng Th&agrave;nh l&agrave; người hay gặp nhất. Tại những buổi gặp gỡ, Nga c&oacute; vai tr&ograve; giới thiệu c&aacute;c sản phẩm, c&ocirc;ng nghệ mới của c&ocirc;ng ty, b&aacute;o c&aacute;o c&aacute;c c&ocirc;ng việc m&agrave; AIC đ&atilde; thực hiện cho c&aacute;c l&atilde;nh đạo tỉnh, sở ban ng&agrave;nh.</p>\n\n<p>&quot;Mỗi khi gần kết th&uacute;c c&aacute;c buổi gặp gỡ, chị Nh&agrave;n n&oacute;i t&ocirc;i đi ra ngo&agrave;i để chị gặp ri&ecirc;ng c&aacute;c l&atilde;nh đạo&quot;, bị c&aacute;o Ho&agrave;ng Thị Th&uacute;y Nga tr&igrave;nh b&agrave;y v&agrave; khẳng định bản th&acirc;n kh&ocirc;ng tham gia đề nghị l&atilde;nh đạo tỉnh &quot;tạo điều kiện&quot; cho c&ocirc;ng ty.</p>\n\n<p><strong>Cựu B&iacute; thư Tỉnh ủy Đồng Nai từng rất tin tưởng năng lực C&ocirc;ng ty AIC</strong></p>\n\n<p>Tại phi&ecirc;n t&ograve;a, HĐXX đề nghị &ocirc;ng Trần Đ&igrave;nh Th&agrave;nh (cựu B&iacute; thư Tỉnh ủy Đồng Nai) l&ecirc;n bục x&eacute;t hỏi để chất vấn về mối quan hệ giữa bị c&aacute;o với b&agrave; Nh&agrave;n.</p>\n\n<p>Theo &ocirc;ng Th&agrave;nh, bị c&aacute;o quen Tổng Gi&aacute;m đốc C&ocirc;ng ty AIC từ năm 2003. Qua tiếp x&uacute;c, bị c&aacute;o Th&agrave;nh đ&aacute;nh gi&aacute; b&agrave; Nh&agrave;n l&agrave; người hiểu biết, kiến thức rộng, nhanh nhạy, c&oacute; quan hệ rộng r&atilde;i ở nhiều địa phương, ở Trung ương v&agrave; cả nước ngo&agrave;i.</p>\n\n<p>Tiếp tục trả lời HĐXX, &ocirc;ng Th&agrave;nh kể về lần gặp đầu ti&ecirc;n giữa bị c&aacute;o v&agrave; b&agrave; Nh&agrave;n tại trụ sở l&agrave;m việc. Theo đ&oacute;, tại ph&ograve;ng l&agrave;m việc của cựu B&iacute; thư Tỉnh ủy Đồng Nai, b&agrave; Nh&agrave;n giới thiệu c&ocirc;ng ty đang <a data-auto-link-id=\"61322e8efb044100119a1452\" data-content-name=\"article-content-autolink\" data-content-piece=\"article-content-autolink_61322e8efb044100119a1452\" data-content-target=\"/phap-luat/vu-an-aic-cuu-bi-thu-dong-nai-danh-gia-ba-nhan-thong-minh-quan-he-rong-20221221174004396.htm\" data-track-content=\"\" href=\"https://dantri.com.vn/kinh-doanh.htm\">kinh doanh</a> một số thiết bị y tế hiện đại nhất, để cung cấp cho Dự &aacute;n x&acirc;y dựng Bệnh viện Đồng Nai.</p>\n\n<p>Tuy nhi&ecirc;n, thời điểm tr&ecirc;n, &ocirc;ng Th&agrave;nh kh&ocirc;ng c&oacute; chuy&ecirc;n m&ocirc;n v&agrave; thẩm quyền quyết định n&ecirc;n đ&atilde; giới thiệu b&agrave; Nh&agrave;n cho &ocirc;ng Phan Huy Anh Vũ (Gi&aacute;m đốc Bệnh viện Đồng Nai) để tiếp tục trao đổi.</p>\n\n<p>&quot;T&ocirc;i gọi anh Vũ, bảo sắp xếp thời gian để nghe Nh&agrave;n tr&igrave;nh b&agrave;y chứ kh&ocirc;ng hề c&oacute; lời đề nghị để C&ocirc;ng ty AIC tham gia c&aacute;c g&oacute;i thầu. Sau đ&oacute;, t&ocirc;i cũng kh&ocirc;ng biết 2 người c&oacute; gặp nhau kh&ocirc;ng&quot;, &ocirc;ng Th&agrave;nh khai.</p>\n\n<figure class=\"image\"><img alt=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 4\" data-content-name=\"article-content-image\" data-content-piece=\"article-content-image_2271529\" data-content-target=\"/phap-luat/vu-an-aic-cuu-bi-thu-dong-nai-danh-gia-ba-nhan-thong-minh-quan-he-rong-20221221174004396.htm\" data-height=\"461\" data-ll-status=\"loaded\" data-original=\"https://icdn.dantri.com.vn/2022/12/21/bi-cao-tran-dinh-thanh-21122022-1671618474612.jpeg\" data-photo-id=\"2271529\" data-src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/bi-cao-tran-dinh-thanh-21122022-1671618474612.jpeg\" data-track-content=\"\" data-width=\"665\" src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/bi-cao-tran-dinh-thanh-21122022-1671618474612.jpeg\" title=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 4\" />\n<figcaption>Bị c&aacute;o Trần Đ&igrave;nh Th&agrave;nh. (Ảnh: TTXVN).</figcaption>\n</figure>\n\n<p>Khi HĐXX chất vấn bị c&aacute;o Th&agrave;nh l&yacute; do nhờ b&agrave; Nh&agrave;n xin vốn Trung ương hỗ trợ, thời điểm Dự &aacute;n thiếu hụt vốn đầu tư thiết bị chuy&ecirc;n m&ocirc;n, cựu B&iacute; thư Tỉnh ủy Đồng Nai khai v&igrave; biết Tổng Gi&aacute;m đốc C&ocirc;ng ty AIC c&oacute; mối quan hệ rộng r&atilde;i, đặc biệt l&agrave; ở Trung ương.</p>\n\n<p>&quot;Khi Nh&agrave;n n&oacute;i AIC l&agrave; c&ocirc;ng ty lớn, kinh doanh thiết bị y tế, đ&atilde; thực hiện đầu tư ở nhiều tỉnh, thực l&ograve;ng t&ocirc;i rất tin tưởng c&ocirc;ng ty, mong muốn AIC tham gia thầu v&agrave; tr&uacute;ng thầu bằng ch&iacute;nh năng lực của c&ocirc;ng ty nhưng tiếc l&agrave; cuối c&ugrave;ng lại kh&ocirc;ng như thế&quot;, bị c&aacute;o Th&agrave;nh b&agrave;y tỏ.</p>\n\n<p>Đối với h&agrave;nh vi &quot;Nhận hối lộ&quot;, bị c&aacute;o Th&agrave;nh nhận lỗi, khai c&oacute; 6 lần nhận tổng 14,5 tỷ đồng từ C&ocirc;ng ty AIC. Trong đ&oacute; c&oacute; 2 lần bị c&aacute;o n&agrave;y nhận ở trụ sở cơ quan, 4 lần tại C&ocirc;ng ty AIC. Bị c&aacute;o Th&agrave;nh cho biết gia đ&igrave;nh bị c&aacute;o đ&atilde; tự nguyện nộp khắc phục to&agrave;n bộ số tiền tr&ecirc;n.</p>\n\n<figure class=\"image\"><img alt=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 5\" data-content-name=\"article-content-image\" data-content-piece=\"article-content-image_2271530\" data-content-target=\"/phap-luat/vu-an-aic-cuu-bi-thu-dong-nai-danh-gia-ba-nhan-thong-minh-quan-he-rong-20221221174004396.htm\" data-height=\"443\" data-ll-status=\"loaded\" data-original=\"https://icdn.dantri.com.vn/2022/12/21/bi-cao-dinh-cong-quoc-21122022-1671618474646.jpeg\" data-photo-id=\"2271530\" data-src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/bi-cao-dinh-cong-quoc-21122022-1671618474646.jpeg\" data-track-content=\"\" data-width=\"665\" src=\"https://icdn.dantri.com.vn/thumb_w/680/2022/12/21/bi-cao-dinh-cong-quoc-21122022-1671618474646.jpeg\" title=\"Vụ án AIC: Cựu Bí thư Đồng Nai đánh giá bà Nhàn thông minh, quan hệ rộng - 5\" />\n<figcaption>Bị c&aacute;o Đinh Quốc Th&aacute;i. (Ảnh: TTXVN).</figcaption>\n</figure>\n\n<p>Trong phần x&eacute;t hỏi bị c&aacute;o Đinh Quốc Th&aacute;i (cựu Chủ tịch UBND tỉnh Đồng Nai), bị c&aacute;o n&agrave;y cho biết c&oacute; 14 lần nhận tiền từ C&ocirc;ng ty AIC, tổng l&agrave; 14,5 tỷ đồng. Bị c&aacute;o Th&aacute;i khai thường nhận tiền v&agrave;o những dịp lễ, Tết. Theo lời khai trước t&ograve;a, những lần đưa tiền, c&aacute;c bị c&aacute;o kh&ocirc;ng n&oacute;i thẳng việc đề nghị &ocirc;ng Th&aacute;i gi&uacute;p đỡ C&ocirc;ng ty AIC nhưng bản th&acirc;n bị c&aacute;o Th&aacute;i tự &quot;ngầm hiểu&quot; mục đ&iacute;ch tr&ecirc;n.</p>\n\n<p>&quot;Bị c&aacute;o thấy những c&aacute;o buộc của VKS l&agrave; đ&uacute;ng người, đ&uacute;ng tội. Bị c&aacute;o th&agrave;nh khẩn nhận lỗi, đ&atilde; kịp thời khắc phục hậu quả trước khi bị khởi tố&quot;, cựu Chủ tịch UBND tỉnh Đồng Nai n&oacute;i trước HĐXX.</p>\n\n<p>18h, phi&ecirc;n t&ograve;a tạm nghỉ v&agrave; sẽ tiếp tục x&eacute;t xử v&agrave;o ng&agrave;y mai (22/12).</p>\n</div>\n','[{\"file\":\"/images/ok/1670553890001img_0066_jpee.jpg\",\"extension\":\"jpg\"}]',38,1,'2022-12-19 15:13:28','2022-12-19 15:13:28',1,'quet-ma-qr-phan-anh-thai-do-phuc-vu-cua-can-bo-cong-chuc-vien-chuc',NULL,NULL),(8,'Xuất khẩu của ngành công nghệ số năm 2022 đạt 136 tỉ USD',3,'',NULL,'[{\"file\":\"/images/ok/1670554563430logo_image1_16704859646941059063269.jpeg\",\"extension\":\"jpeg\"}]',4,1,'2022-12-19 15:13:57','2022-12-19 15:13:57',1,'xuat-khau-cua-nganh-cong-nghe-so-nam-2022-dat-136-ti-usd',NULL,NULL),(9,'Bộ Công Thương sẽ chuyển đổi IPv6 cho hệ thống công nghệ thông tin',1,'',NULL,'[{\"file\":\"/images/ok/1671073458046ipv6.jpg\",\"extension\":\"jpg\"}]',10,1,'2022-12-19 15:14:22','2022-12-19 15:14:22',1,'bo-cong-thuong-se-chuyen-doi-ipv6-cho-he-thong-cong-nghe-thong-tin',NULL,NULL),(10,'Công nghệ thông tin là một trong những lực lượng quyết định liệu Việt Nam có đi nhanh được hay không',1,'Chuyển đổi số là xu hướng phát triển tất yếu của thời đại. Chuyển đổi số không đơn giản chỉ là quá trình số hóa và nâng cao mức độ ứng dụng công nghệ thông tin mà là việc ứng dụng công nghệ số để thay đổi cách thức vận hành, là việc tư duy lại cách thức tổ chức, sử dụng dữ liệu và quy trình.','','[{\"file\":\"/images/ok/1670553699573cd87ec48c905205b7914.jpg\",\"extension\":\"jpg\"}]',67,1,'2022-12-19 15:14:58','2022-12-19 15:14:58',1,'cong-nghe-thong-tin-la-mot-trong-nhung-luc-luong-quyet-dinh-lieu-viet-nam-co-di-nhanh-duoc-hay-khong',NULL,NULL),(11,'Hội thi giải pháp ứng dụng Trí tuệ nhân tạo',4,'Đây là một trong những tiện ích được Bảo hiểm xã hội hội (BHXH) Việt Nam triển khai thí điểm bắt đầu từ ngày 7.11 tại TP.HCM và Bình Dương.','','[{\"file\":\"/images/20230104/1_455.jpg\",\"extension\":\"jpg\"}]',1,1,'2022-12-19 15:17:16','2022-12-19 15:17:16',1,'hoi-thi-giai-phap-ung-dung-tri-tue-nhan-tao',NULL,NULL),(12,'Chương trình An toàn thông tin',4,'Đây là một trong những tiện ích được Bảo hiểm xã hội hội (BHXH) Việt Nam triển khai thí điểm bắt đầu từ ngày 7.11 tại TP.HCM và Bình Dương.','','[{\"file\":\"/images/20230104/nganh_giao_thong_tap_trung_ung_dung_cong_nghe_uu_tien_trong_cach_mang_4_0_vao_cac_hoat_dong_2.jpg\",\"extension\":\"jpg\"}]',2,1,'2022-12-19 15:17:27','2022-12-19 15:17:27',1,'chuong-trinh-an-toan-thong-tin',NULL,NULL),(13,'Đề án xây dựng TP.HCM trở thành Đô thị thông minh',4,'Đây là một trong những tiện ích được Bảo hiểm xã hội hội (BHXH) Việt Nam triển khai thí điểm bắt đầu từ ngày 7.11 tại TP.HCM và Bình Dương.','','[{\"file\":\"/images/20230104/6__xac_nhan_khoi_dong_du_an.jpg\",\"extension\":\"jpg\"}]',5,1,'2022-12-19 15:17:37','2022-12-19 15:17:37',1,'de-an-xay-dung-tphcm-tro-thanh-do-thi-thong-minh',NULL,NULL),(14,'Chuyển đổi số là gì?',5,'Chuyển đổi số là quá trình thay đổi tổng thể và toàn diện của cá nhân, tổ chức về cách sống, cách làm việc và phương thức sản xuất dựa trên các công nghệ số.','<div class=\"article-content link-primary\">\n<h4>Tr&ecirc;n thế giới, chuyển đổi số bắt đầu được nhắc đến nhiều v&agrave;o khoảng năm 2015, phổ biến từ năm 2017. Ở Việt Nam, chuyển đổi số bắt đầu được nhắc đến nhiều v&agrave;o khoảng năm 2018. Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt Chương tr&igrave;nh Chuyển đổi số quốc gia v&agrave;o ng&agrave;y 03/6/2020.</h4>\n\n<p>Chuyển đổi số l&agrave; bước ph&aacute;t triển tiếp theo của tin học h&oacute;a, c&oacute; được nhờ sự tiến bộ vượt bậc của những c&ocirc;ng nghệ mới mang t&iacute;nh đột ph&aacute;, nhất l&agrave; c&ocirc;ng nghệ số.</p>\n\n<p>Chuyển đổi số l&agrave; qu&aacute; tr&igrave;nh thay đổi tổng thể v&agrave; to&agrave;n diện của c&aacute; nh&acirc;n, tổ chức về c&aacute;ch sống, c&aacute;ch l&agrave;m việc v&agrave; phương thức sản xuất dựa tr&ecirc;n c&aacute;c c&ocirc;ng nghệ số.</p>\n\n<p>Nội h&agrave;m kh&aacute;i niệm chuyển đổi số sẽ tiếp tục được l&agrave;m r&otilde; hơn th&ocirc;ng qua c&aacute;c c&acirc;u hỏi v&agrave; c&acirc;u trả lời ở phần ngay tiếp sau đ&acirc;y.</p>\n\n<h3>Tin học h&oacute;a l&agrave; g&igrave;?</h3>\n\n<p>Tin học h&oacute;a, hay c&ograve;n gọi l&agrave; ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin, l&agrave; việc số h&oacute;a quy tr&igrave;nh nghiệp vụ đ&atilde; c&oacute;. Th&ocirc;ng thường, tin học h&oacute;a kh&ocirc;ng l&agrave;m thay đổi quy tr&igrave;nh đ&atilde; c&oacute; hoặc m&ocirc; h&igrave;nh hoạt động đ&atilde; c&oacute;. Khi tin học h&oacute;a ở mức cao, dẫn đến thay đổi quy tr&igrave;nh hoặc thay đổi m&ocirc; h&igrave;nh hoạt động, th&igrave; gọi l&agrave; chuyển đổi số.</p>\n\n<div class=\"portlet-msg-info\"><cite><span style=\"color:#323247\"><strong>Vận tải h&agrave;nh kh&aacute;ch</strong></span><br />\n<span style=\"color:#8f919e\">Tin học h&oacute;a l&agrave; số h&oacute;a quy tr&igrave;nh vận tải h&agrave;nh kh&aacute;ch truyền thống m&agrave; kh&ocirc;ng l&agrave;m thay đổi n&oacute; một c&aacute;ch đ&aacute;ng kể, cũng như kh&ocirc;ng thay đổi m&ocirc; h&igrave;nh hoạt động. Đối với c&aacute;c doanh nghiệp cung cấp dịch vụ taxi, nếu như trước đ&acirc;y, h&agrave;nh kh&aacute;ch gọi taxi bằng c&aacute;ch vẫy tay ngo&agrave;i đường hoặc gọi đến tổng đ&agrave;i để đặt chuyến. Đến khi c&aacute;c doanh nghiệp như Be, Uber, Grab tham gia thị trường th&igrave; mỗi h&agrave;nh kh&aacute;ch,mỗi l&aacute;i xe c&agrave;i một ứng dụng tr&ecirc;n điện thoại th&ocirc;ng minh. Người c&oacute; nhu cầu v&agrave; người c&oacute; khả năng cung ứng dịch vụ kết nối với nhau một c&aacute;ch dễ d&agrave;ng. Uber hay Grab kh&ocirc;ng sở hữu một chiếc xe taxi n&agrave;o hay một t&agrave;i xế n&agrave;o. Chuyển đổi số đ&atilde; thay đổi m&ocirc; h&igrave;nh hoạt động trong cung cấp dịch vụ taxi.</span></cite></div>\n\n<h3>Qu&aacute; tr&igrave;nh thay đổi tổng thể v&agrave; to&agrave;n diện l&agrave; g&igrave;?</h3>\n\n<p>Tổng thể nghĩa l&agrave; mọi bộ phận. To&agrave;n diện nghĩa l&agrave; mọi mặt. Đ&acirc;y l&agrave; sự s&aacute;ng tạo ph&aacute; hủy.</p>\n\n<div class=\"portlet-msg-info\"><span style=\"color:#323247\"><strong>Sự s&aacute;ng tạo ph&aacute; hủy</strong></span><br />\n<span style=\"color:#8f919e\">Một v&iacute; dụ minh họa r&otilde; n&eacute;t nhất về chuyển đổi số l&agrave; qu&aacute; tr&igrave;nh thay đổi từ con nhộng th&agrave;nh con bướm, khi con nhộng tự vận động, x&eacute; r&aacute;ch c&aacute;i k&eacute;n, th&agrave;nh con bướm bay l&ecirc;n. S&aacute;ng tạo tạo ra thay đổi. C&oacute; những thay đổi diễn ra từ từ, tuyến t&iacute;nh. C&oacute; những thay đổi diễn ra nhanh ch&oacute;ng, to&agrave;n diện v&agrave; thay thế c&aacute;i cũ, gọi l&agrave; s&aacute;ng tạo ph&aacute; hủy. Sự ra đời của điện thoại iPhone năm 2007, của hệ điều h&agrave;nh Android v&agrave;o năm 2008 đ&atilde; tạo n&ecirc;n sự s&aacute;ng tạo ph&aacute; hủy, đưa thị phần to&agrave;n cầu của Tập đo&agrave;n Nokia đang từ 50% về dưới 2%.</span></div>\n\n<h3>Sự kh&aacute;c nhau căn bản giữa chuyển đổi số v&agrave; tin học h&oacute;a l&agrave; g&igrave;?</h3>\n\n<p>Tin học h&oacute;a, hay ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin, l&agrave; số h&oacute;a quy tr&igrave;nh đ&atilde; c&oacute;, theo m&ocirc; h&igrave;nh hoạt động đ&atilde; c&oacute;, để cung cấp dịch vụ đ&atilde; c&oacute;. Chuyển đổi số l&agrave; số h&oacute;a to&agrave;n bộ cả một tổ chức. Chuyển đổi số l&agrave; thay đổi quy tr&igrave;nh mới, m&ocirc; h&igrave;nh tổ chức mới, phương thức cung cấp dịch vụ hoặc cung cấp dịch vụ mới.</p>\n\n<div class=\"portlet-msg-info\"><span style=\"color:#323247\"><strong>Thay đổi nhưng phải giữ được gi&aacute; trị cốt l&otilde;i</strong></span><br />\n<span style=\"color:#8f919e\">Britannica l&agrave; một c&ocirc;ng ty kinh doanh theo m&ocirc; h&igrave;nh b&aacute;n s&aacute;ch b&aacute;ch khoa to&agrave;n thư c&oacute; chất lượng nội dung cao, phục vụ gi&aacute;o dục. Britannica đ&atilde; c&oacute; gần 250 năm tồn tại bằng c&aacute;ch b&aacute;n bộ s&aacute;ch b&igrave;a bọc da d&agrave;y 32 tập. Britannica ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin bằng c&aacute;ch số h&oacute;a t&agrave;i liệu của m&igrave;nh v&agrave; xuất bản dưới dạng đĩa CD. C&ograve;n khi thực hiện chuyển đổi số, Britannica đ&atilde; dừng xuất bản s&aacute;ch, dừng in đĩa CD, thay đổi m&ocirc; h&igrave;nh kinh doanh b&aacute;n sản phẩm th&agrave;nh m&ocirc; h&igrave;nh kinh doanh b&aacute;n dịch vụ truy cập trực tuyến đến kho nội dung của m&igrave;nh. Trong suốt qu&aacute; tr&igrave;nh đ&oacute;, Britannica vẫn giữ được sứ mạng cốt l&otilde;i l&agrave; chất lượng nội dung cao, phục vụ gi&aacute;o dục.</span></div>\n\n<h3>Sự kh&aacute;c nhau căn bản giữa chuyển đổi số với khoa học c&ocirc;ng nghệ v&agrave; đổi mới s&aacute;ng tạo l&agrave; g&igrave;?</h3>\n\n<p>Chuyển đổi số l&agrave; chuyển đổi m&ocirc; h&igrave;nh hoạt động dựa tr&ecirc;n c&ocirc;ng nghệ số, dữ liệu số. Khoa học c&ocirc;ng nghệ v&agrave; đổi mới s&aacute;ng tạo l&agrave; tạo ra gi&aacute; trị mới dựa tr&ecirc;n c&aacute;c đột ph&aacute; về nghi&ecirc;n cứu khoa học v&agrave; ứng dụng c&ocirc;ng nghệ.</p>\n\n<p>Chuyển đổi số v&agrave; đổi mới s&aacute;ng tạo trong bối cảnh cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư về bản chất l&agrave; một, l&agrave; giống nhau.</p>\n\n<h3>C&ocirc;ng nghệ số l&agrave; g&igrave;?</h3>\n\n<p>Trong cuộc sống, con người giao tiếp với nhau bằng t&iacute;n hiệu tương tự, biểu diễn dưới dạng giọng n&oacute;i. C&ograve;n trong m&ocirc;i trường số, c&aacute;c thiết bị t&iacute;nh to&aacute;n giao tiếp với nhau bằng t&iacute;n hiệu số, biểu diễn dưới dạng t&iacute;n hiệu nhị ph&acirc;n l&agrave; 0 v&agrave; 1. C&ocirc;ng nghệ số, hiểu theo nghĩa rộng, l&agrave; c&ocirc;ng nghệ xử l&yacute; t&iacute;n hiệu số, hay c&ocirc;ng nghệ th&ocirc;ng tin.</p>\n\n<p>C&ograve;n trong bối cảnh cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư, c&ocirc;ng nghệ số, hiểu theo nghĩa hẹp, l&agrave; một bước ph&aacute;t triển cao hơn, l&agrave; bước ph&aacute;t triển tiếp theo của c&ocirc;ng nghệ th&ocirc;ng tin, cho ph&eacute;p t&iacute;nh to&aacute;n nhanh hơn, xử l&yacute; dữ liệu nhiều hơn, truyền tải dung lượng lớn hơn, với chi ph&iacute; rẻ hơn. C&ograve;n hiểu theo nghĩa rộng, c&ocirc;ng nghệ số l&agrave; một trong c&aacute;c nh&oacute;m c&ocirc;ng nghệ ch&iacute;nh của cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư, với đại diện l&agrave; c&ocirc;ng nghệ điện to&aacute;n đ&aacute;m m&acirc;y, dữ liệu lớn, tr&iacute; tuệ nh&acirc;n tạo, chuỗi khối, thực tế ảo&hellip; C&aacute;ch hiểu theo nghĩa rộng phổ biến hơn.</p>\n\n<div class=\"portlet-msg-info\"><span style=\"color:#323247\"><strong>Năng lực t&iacute;nh to&aacute;n lại tăng gấp đ&ocirc;i sau 18 th&aacute;ng</strong></span><br />\n<span style=\"color:#8f919e\">Chiếc điện thoại th&ocirc;ng minh hiện nay c&oacute; năng lực t&iacute;nh to&aacute;n cao hơn gấp h&agrave;ng ngh&igrave;n lần so với chiếc m&aacute;y chủ điều khiển ph&oacute;ng t&agrave;u Apollo l&ecirc;n mặt trăng c&aacute;ch đ&acirc;y hơn 50 năm. Ch&iacute;nh sự ph&aacute;t triển đột ph&aacute; n&agrave;y của c&ocirc;ng nghệ đ&atilde; cho ph&eacute;p chuyển đổi số một c&aacute;ch tổng thể v&agrave; to&agrave;n diện m&agrave; trước kia kh&ocirc;ng thể l&agrave;m được. C&ocirc;ng nghệ ng&agrave;y c&agrave;ng ph&aacute;t triển, với chi ph&iacute; ng&agrave;y c&agrave;ng thấp, tạo điều kiện thuận lợi để to&agrave;n d&acirc;n c&ugrave;ng tham gia v&agrave;o chuyển đổi số.</span></div>\n\n<p>Hơn 30 năm qua, ch&uacute;ng ta đ&atilde; v&agrave; đang chứng kiến 3 l&agrave;n s&oacute;ng c&ocirc;ng nghệ, mỗi l&agrave;n s&oacute;ng k&eacute;o d&agrave;i khoảng 15 năm.</p>\n\n<p>L&agrave;n s&oacute;ng thứ nhất, từ năm 1985 đến năm 1999, gắn với sự phổ biến của m&aacute;y vi t&iacute;nh, c&oacute; thể tạm gọi l&agrave; l&agrave;n s&oacute;ng số h&oacute;a th&ocirc;ng tin, chuyển c&aacute;c t&agrave;i liệu từ bản giấy sang bản điện tử.</p>\n\n<p>L&agrave;n s&oacute;ng thứ hai, từ năm 2000 đến năm 2015, gắn với sự phổ biến của Internet, điện thoại di động v&agrave; mạng viễn th&ocirc;ng di động, c&oacute; thể tạm gọi l&agrave; l&agrave;n s&oacute;ng số h&oacute;a quy tr&igrave;nh nghiệp vụ, tin học h&oacute;a c&aacute;c quy tr&igrave;nh nghiệp vụ để n&acirc;ng cao năng suất, hiệu quả.</p>\n\n<p>L&agrave;n s&oacute;ng thứ ba, được cho l&agrave; từ năm 2015 v&agrave; dự b&aacute;o k&eacute;o d&agrave;i đến năm 2030, gắn với sự ph&aacute;t triển đột ph&aacute; của c&ocirc;ng nghệ số, c&oacute; thể tạm gọi l&agrave; l&agrave;n s&oacute;ng chuyển đổi số, đưa to&agrave;n bộ c&aacute;c hoạt động từ x&atilde; hội thực l&ecirc;n kh&ocirc;ng gian mạng, từ m&ocirc;i trường truyền thống l&ecirc;n m&ocirc;i trường số.</p>\n\n<p>Bốn c&ocirc;ng nghệ số ti&ecirc;u biểu th&uacute;c đẩy chuyển đổi số l&agrave; tr&iacute; tuệ nh&acirc;n tạo, Internet vạn vật, dữ liệu lớn, điện to&aacute;n đ&aacute;m m&acirc;y. Ngo&agrave;i ra, chuỗi khối cũng l&agrave; một c&ocirc;ng nghệ số quan trọng của chuyển đổi số.</p>\n\n<h3>C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư l&agrave; g&igrave;?</h3>\n\n<p>C&aacute;ch mạng c&ocirc;ng nghiệp xảy ra khi c&oacute; đột ph&aacute; lớn về c&ocirc;ng nghệ dẫn đến c&aacute;c thay đổi s&acirc;u sắc trong sản xuất v&agrave; x&atilde; hội. C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư được cho l&agrave; bắt đầu từ thập kỷ n&agrave;y với c&aacute;c đột ph&aacute; v&agrave; cộng hưởng của nhiều c&ocirc;ng nghệ, trong đ&oacute; c&oacute; c&ocirc;ng nghệ số v&agrave; tạo ra sản xuất th&ocirc;ng minh.</p>\n\n<p>C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ nhất l&agrave; giai đoạn từ cuối thế kỷ XVIII với sự ph&aacute;t minh ra động cơ hơi nước v&agrave; tạo ra sản xuất cơ kh&iacute;. C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ hai l&agrave; giai đoạn từ đầu thế kỷ XX với sự xuất hiện của điện lực v&agrave; tạo ra sản xuất h&agrave;ng loạt. C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ ba l&agrave; giai đoạn từ những năm 1970 với sự xuất hiện của điện tử, m&aacute;y t&iacute;nh, Internet v&agrave; tạo ra sản xuất tự động.</p>\n\n<h3>Sự kh&aacute;c biệt căn bản của cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư l&agrave; g&igrave;?</h3>\n\n<p>Một cuộc c&aacute;ch mạng c&ocirc;ng nghiệp mới th&igrave; c&oacute; 2 nội dung chủ yếu l&agrave; c&ocirc;ng nghệ g&igrave; v&agrave; chuyển đổi g&igrave;. Với C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư th&igrave; c&ocirc;ng nghệ l&agrave; c&ocirc;ng nghệ số v&agrave; chuyển đổi chủ yếu l&agrave; chuyển đổi số, chuyển đổi m&ocirc; h&igrave;nh hoạt động, vận h&agrave;nh của cơ quan, tổ chức, doanh nghiệp dựa tr&ecirc;n c&ocirc;ng nghệ số.</p>\n\n<p>Ba cuộc c&aacute;ch mạng đ&atilde; qua l&agrave; cơ kh&iacute; h&oacute;a, điện kh&iacute; h&oacute;a, tự động h&oacute;a, l&agrave; m&aacute;y m&oacute;c thay lao động ch&acirc;n tay. Cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư l&agrave; th&ocirc;ng minh h&oacute;a, l&agrave; m&aacute;y m&oacute;c thay lao động tr&iacute; &oacute;c.</p>\n\n<p>C&ocirc;ng nghệ số c&agrave;ng d&ugrave;ng nhiều, c&agrave;ng nhiều người d&ugrave;ng th&igrave; c&agrave;ng rẻ, gi&aacute; tr&ecirc;n đầu người tiệm cận về 0. C&ocirc;ng nghệ số th&igrave; xử l&yacute; dữ liệu nhiều, c&agrave;ng nhiều dữ liệu th&igrave; c&agrave;ng th&ocirc;ng minh, người d&ugrave;ng quyết định sự th&ocirc;ng minh của hệ thống chứ kh&ocirc;ng phải c&ocirc;ng nghệ gốc. Tức l&agrave; người d&ugrave;ng đ&oacute;ng vai tr&ograve; quan trọng hơn l&agrave; người s&aacute;ng tạo ra c&ocirc;ng nghệ gốc. C&ocirc;ng nghệ gốc kh&ocirc;ng quan trọng v&agrave; quyết định như l&agrave; ở c&aacute;c cuộc c&aacute;ch mạng c&ocirc;ng nghiệp trước.</p>\n\n<p>Cuộc C&aacute;ch mạng c&ocirc;ng nghiệp thứ tư l&agrave; sự sẵn s&agrave;ng chấp nhận loại bỏ c&aacute;i cũ kh&ocirc;ng c&ograve;n ph&ugrave; hợp, hoặc tạo ra kh&ocirc;ng gian thử nghiệm cho c&aacute;i mới chưa c&oacute; tiền lệ, chưa được quy định r&otilde; r&agrave;ng. Bởi vậy, cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư l&agrave; cuộc c&aacute;ch mạng về thể chế nhiều hơn l&agrave; cuộc c&aacute;ch mạng về c&ocirc;ng nghệ.</p>\n\n<p>V&iacute; dụ, c&oacute; d&aacute;m chuyển người dạy th&agrave;nh người trợ giảng kh&ocirc;ng? C&oacute; d&aacute;m bỏ hệ thống cấp dưới b&aacute;o c&aacute;o cấp tr&ecirc;n kh&ocirc;ng? C&oacute; d&aacute;m cho ph&eacute;p ng&acirc;n h&agrave;ng số kh&ocirc;ng nh&acirc;n vi&ecirc;n, đại học số kh&ocirc;ng gi&aacute;o vi&ecirc;n kh&ocirc;ng? Vậy n&ecirc;n, chuyển đổi số l&agrave; c&acirc;u chuyện d&aacute;m hay kh&ocirc;ng d&aacute;m đổi mới s&aacute;ng tạo, chấp nhận c&aacute;i mới. V&agrave; v&igrave; thế, chuyển đổi số phụ thuộc v&agrave;o một người, đ&oacute; l&agrave; người đứng đầu. Người đứng đầu m&agrave; kh&ocirc;ng c&oacute; quyết t&acirc;m ch&iacute;nh trị th&igrave; chuyển đổi số sẽ kh&ocirc;ng xảy ra. Với những c&aacute;i mới chưa c&oacute; quy định th&igrave; tập thể cấp ủy, tập thể l&atilde;nh đạo quyết định cho l&agrave;m th&iacute; điểm trước, trong một khoảng kh&ocirc;ng gian v&agrave; thời gian hạn chế.</p>\n\n<div class=\"portlet-msg-info\"><span style=\"color:#323247\"><strong>Dịch vụ ng&acirc;n h&agrave;ng</strong></span><br />\n<span style=\"color:#8f919e\">Trải nghiệm mới đối với c&aacute;c dịch vụ ng&acirc;n h&agrave;ng kh&ocirc;ng bắt đầu từ c&acirc;y ATM hay chi nh&aacute;nh m&agrave; l&agrave; dịch vụ mọi l&uacute;c, mọi nơi. Ng&acirc;n h&agrave;ng lu&ocirc;n b&ecirc;n bạn thay thế việc bạn đến ng&acirc;n h&agrave;ng. Ng&acirc;n h&agrave;ng truyền thống thường c&oacute; một hội sở ch&iacute;nh l&agrave; một t&ograve;a nh&agrave; chọc trời v&agrave; h&agrave;ng ngh&igrave;n chi nh&aacute;nh, điểm giao dịch. Ng&acirc;n h&agrave;ng số kh&ocirc;ng theo c&aacute;ch như vậy. Điện thoại th&ocirc;ng minh c&oacute; thể thay thế cả ATM lẫn chi nh&aacute;nh. Điện thoại th&ocirc;ng minh cũng đ&atilde; trở th&agrave;nh k&ecirc;nh huy động tiền gửi th&agrave;nh c&ocirc;ng nhất thế giới. Visa trung b&igrave;nh c&oacute; 9.000 giao dịch/gi&acirc;y. Alipay trung b&igrave;nh c&oacute; 87.000 giao dịch/gi&acirc;y, 84% qua thiết bị di động. Alipay kh&ocirc;ng c&oacute; chi nh&aacute;nh để nhận tiền gửi, nhưng huy động được t&agrave;i sản tiền gửi gần 200 tỷ USD.</span></div>\n\n<h3>Tr&iacute; tuệ nh&acirc;n tạo l&agrave; g&igrave;?</h3>\n\n<p>Con người nỗ lực l&agrave;m cho m&aacute;y m&oacute;c c&oacute; những năng lực tr&iacute; tuệ của con người v&agrave; gọi đ&oacute; l&agrave; tr&iacute; tuệ nh&acirc;n tạo. X&eacute;t theo nghĩa n&agrave;y, th&igrave; tr&iacute; tuệ nh&acirc;n tạo c&ograve;n phải tiếp tục ph&aacute;t triển l&acirc;u d&agrave;i nữa để tới gần hơn điều đ&oacute;. Nhưng x&eacute;t theo nghĩa hẹp hơn, l&agrave; tr&iacute; tuệ nh&acirc;n tạo nhằm &ldquo;tăng cường năng lực tr&iacute; tuệ của con người&rdquo;, th&igrave; đ&atilde; c&oacute; những bước tiến lớn trong v&ograve;ng 2 thập kỷ vừa qua.</p>\n\n<div class=\"portlet-msg-info\"><span style=\"color:#323247\"><strong>Thời khắc Sputnik về tr&iacute; tuệ nh&acirc;n tạo của Trung Quốc</strong></span><br />\n<span style=\"color:#8f919e\">Năm 1957, Li&ecirc;n X&ocirc; l&uacute;c đ&oacute; ph&oacute;ng th&agrave;nh c&ocirc;ng vệ tinh Sputnik 1 v&agrave;o quỹ đạo tr&aacute;i đất, khiến nước Mỹ bất ngờ. Thuật ngữ &ldquo;thời khắc Sputnik&rdquo; từ đ&oacute; được sử dụng để m&ocirc; tả thời điểm khi một quốc gia hoặc x&atilde; hội nhận ra rằng họ đang bị đe dọa hoặc th&aacute;ch thức bởi quốc gia kh&aacute;c v&agrave; họ phải nỗ lực gấp đ&ocirc;i để bắt kịp v&agrave; vượt qua đối thủ. C&oacute; một sự kiện m&agrave; thế giới kh&ocirc;ng để &yacute;, xuất ph&aacute;t từ truyền thống văn h&oacute;a của Trung Quốc v&agrave; ra đời từ hơn 2.500 trước; cờ v&acirc;y được coi l&agrave; một trong tứ nghệ. Nguy&ecirc;n tắc chơi cơ bản c&oacute; thể t&oacute;m gọn trong vỏn vẹn 9 c&acirc;u, nhưng số lượng c&aacute;c thế trận khả dĩ tr&ecirc;n b&agrave;n cờ c&ograve;n nhiều hơn số lượng c&aacute;c nguy&ecirc;n tử trong vũ trụ quan s&aacute;t được, nhiều hơn cờ vua v&agrave; cờ tướng gấp nhiều lần. Ch&iacute;nh v&igrave; vậy, việc tr&iacute; tuệ nh&acirc;n tạo c&oacute; thể đ&aacute;nh thắng con người khi chơi cờ v&acirc;y được coi l&agrave; thử th&aacute;ch lớn nhất m&agrave; cộng đồng tr&iacute; tuệ nh&acirc;n tạo thế giới chưa vượt qua được trong v&agrave;i chục năm qua. V&agrave;o th&aacute;ng 3/2016, cỗ m&aacute;y tr&iacute; tuệ nh&acirc;n tạo AlphaGo của Google, với những đột ph&aacute; mới trong kỹ thuật học s&acirc;u, dựa tr&ecirc;n dữ liệu lớn, c&oacute; khả năng tiến h&oacute;a qua c&aacute;c lần học, đ&atilde; đ&aacute;nh bại kỳ thủ đương kim v&ocirc; địch cờ v&acirc;y giỏi nhất đại diện cho lo&agrave;i người. Trong chuỗi 5 trận đấu với kỳ thủ huyền thoại đương kim v&ocirc; địch thế giới, AlphaGo đ&atilde; thắng 4 v&aacute;n, h&ograve;a 1 v&aacute;n. Sự kiện tr&ecirc;n thu h&uacute;t được sự theo d&otilde;i của hơn 280 triệu kh&aacute;n giả Trung Quốc v&agrave; đ&atilde; ch&acirc;m một ngọn lửa tr&iacute; tuệ nh&acirc;n tạo v&agrave;o cộng đồng c&ocirc;ng nghệ Trung Quốc v&agrave; ngọn lửa ấy vẫn hừng hực ch&aacute;y suốt từ đ&oacute; tới nay, đưa Trung Quốc trở th&agrave;nh một trong những quốc gia đi đầu về tr&iacute; tuệ nh&acirc;n tạo.</span></div>\n\n<p>M&aacute;y học l&agrave; một nh&aacute;nh của tr&iacute; tuệ nh&acirc;n tạo, c&oacute; mục ti&ecirc;u l&agrave;m cho m&aacute;y m&oacute;c c&oacute; khả năng học tập như con người. Biết học l&agrave; sẽ tự c&oacute; được kiến thức mới. M&aacute;y học dựa tr&ecirc;n dữ liệu. Do dữ liệu ng&agrave;y c&agrave;ng nhiều, năng lực t&iacute;nh to&aacute;n ng&agrave;y c&agrave;ng mạnh, n&ecirc;n đ&atilde; tạo ra những ph&aacute;t triển đột ph&aacute; trong m&aacute;y học.</p>\n\n<p>Học s&acirc;u l&agrave; một hướng ph&aacute;t triển lớn, đột ph&aacute;, quan trọng của m&aacute;y học. Học s&acirc;u dựa tr&ecirc;n m&ocirc; phỏng cấu tr&uacute;c mạng nơ-ron v&agrave; hoạt động của bộ n&atilde;o con người để xử l&yacute;, ph&acirc;n t&iacute;ch dữ liệu lớn, bao gồm cả dữ liệu cấu tr&uacute;c v&agrave; phi cấu tr&uacute;c.</p>\n\n<p>C&oacute; thể v&iacute; tr&iacute; tuệ nh&acirc;n tạo như l&agrave; hệ thần kinh của con người.</p>\n\n<h3>Internet vạn vật l&agrave; g&igrave;?</h3>\n\n<p>Internet vạn vật (IoT) l&agrave; một c&ocirc;ng nghệ nền tảng của cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư. Nếu như Internet l&agrave; mạng lưới kết nối c&aacute;c thiết bị như m&aacute;y t&iacute;nh, điện thoại th&ocirc;ng minh&hellip; với nhau để trao đổi, chia sẻ dữ liệu, Internet vạn vật l&agrave; mạng lưới kết nối vạn vật với nhau để l&agrave;m việc tương tự. Nhờ c&oacute; c&aacute;c cảm biến th&ocirc;ng minh v&agrave; kết nối mạng, lần đầu ti&ecirc;n trong lịch sử nh&acirc;n loại, những vật v&ocirc; tri v&ocirc; gi&aacute;c, vật dụng gia đ&igrave;nh, như chiếc quạt điện, l&ograve; vi s&oacute;ng, hay c&agrave;nh c&acirc;y, ngọn cỏ &ldquo;cất tiếng n&oacute;i&rdquo; v&agrave; giao tiếp với nhau v&agrave; với con người. Internet vạn vật đ&oacute;ng vai tr&ograve; quan trọng trong việc kết nối giữa m&ocirc;i trường thực v&agrave; m&ocirc;i trường số.</p>\n\n<p>C&oacute; thể v&iacute; Internet vạn vật như l&agrave; c&aacute;c gi&aacute;c quan của con người.</p>\n\n<h3>Dữ liệu lớn l&agrave; g&igrave;?</h3>\n\n<p>Dữ liệu được sinh ra từ h&agrave;ng tỷ điện thoại th&ocirc;ng minh, thiết bị cảm biến kết nối vạn vật v&agrave; hoạt động của con người tr&ecirc;n m&ocirc;i trường mạng. Mỗi một ng&agrave;y dữ liệu sinh ra c&oacute; thể l&ecirc;n đến tương đương dữ liệu lưu trữ trong một tỷ đĩa DVD trước đ&acirc;y. Nếu c&ocirc;ng nghệ trước kia cần một thời gian rất d&agrave;i để xử l&yacute; dữ liệu như vậy th&igrave; c&ocirc;ng nghệ số hiện nay cho ph&eacute;p xử l&yacute;, ph&acirc;n t&iacute;ch trong khoảng thời gian ngắn hơn rất nhiều để tr&iacute;ch r&uacute;t ra th&ocirc;ng tin, tri thức hoặc đưa ra quyết định một c&aacute;ch ph&ugrave; hợp. Nếu c&ocirc;ng nghệ trước kia xử l&yacute; dữ liệu c&oacute; cấu tr&uacute;c th&igrave; c&ocirc;ng nghệ số hiện nay chủ yếu xử l&yacute; v&agrave; ph&acirc;n t&iacute;ch dữ liệu phi cấu tr&uacute;c. Dữ liệu phi cấu tr&uacute;c chiếm tới 70-80%, do vậy, chứa nhiều th&ocirc;ng tin hơn dữ liệu c&oacute; cấu tr&uacute;c.</p>\n\n<p>C&oacute; thể v&iacute; dữ liệu lớn như bộ n&atilde;o của con người.</p>\n\n<h3>Điện to&aacute;n đ&aacute;m m&acirc;y l&agrave; g&igrave;?</h3>\n\n<p>Điện to&aacute;n đ&aacute;m m&acirc;y l&agrave; c&ocirc;ng nghệ cho ph&eacute;p năng lực t&iacute;nh to&aacute;n nằm ở c&aacute;c m&aacute;y chủ ảo, gọi l&agrave; đ&aacute;m m&acirc;y tr&ecirc;n Internet của c&aacute;c nh&agrave; cung cấp thay v&igrave; trong m&aacute;y t&iacute;nh gia đ&igrave;nh v&agrave; văn ph&ograve;ng tr&ecirc;n mặt đất, để mọi người kết nối, sử dụng như l&agrave; dịch vụ khi họ cần. Một c&aacute;ch n&ocirc;m na, điện to&aacute;n đ&aacute;m m&acirc;y cũng giống như điện lưới. C&aacute; nh&acirc;n, hộ gia đ&igrave;nh, doanh nghiệp thay v&igrave; đầu tư m&aacute;y chủ t&iacute;nh to&aacute;n của ri&ecirc;ng m&igrave;nh, giống như m&aacute;y ph&aacute;t điện, th&igrave; sử dụng dịch vụ điện to&aacute;n đ&aacute;m m&acirc;y giống như điện lưới, sử dụng đến đ&acirc;u trả chi ph&iacute; đến đ&oacute; m&agrave; kh&ocirc;ng phải bận t&acirc;m tới việc vận h&agrave;nh, quản l&yacute;.</p>\n\n<p>C&oacute; thể v&iacute; điện to&aacute;n đ&aacute;m m&acirc;y như l&agrave; cơ bắp của con người.</p>\n\n<h3>Chuỗi khối l&agrave; g&igrave;?</h3>\n\n<p>Chuỗi khối, như t&ecirc;n gọi, l&agrave; một chuỗi dữ liệu ph&acirc;n t&aacute;n tr&ecirc;n mạng, gồm c&aacute;c khối th&ocirc;ng tin được li&ecirc;n kết với nhau bằng m&atilde; h&oacute;a v&agrave; mở rộng theo thời gian. V&igrave; m&atilde; h&oacute;a n&ecirc;n bảo mật. V&igrave; ph&acirc;n t&aacute;n n&ecirc;n kh&ocirc;ng ai c&oacute; thể kiểm so&aacute;t to&agrave;n bộ. V&igrave; li&ecirc;n kết n&ecirc;n bất cứ sự sửa đổi n&agrave;o đều để lại dấu vết, chống chối bỏ. V&igrave; tất cả yếu tố như vậy n&ecirc;n bảo đảm sự an to&agrave;n, tin cậy v&agrave; minh bạch.</p>\n\n<p>Với c&aacute;c đặc điểm như vậy, c&aacute;c giao dịch trong mạng chuỗi khối diễn ra tự động m&agrave; kh&ocirc;ng cần b&ecirc;n thứ ba chứng nhận. C&ocirc;ng nghệ chuỗi khối sẽ giảm dần v&agrave; x&oacute;a bỏ vai tr&ograve; của trung gian trong c&aacute;c giao dịch.</p>\n\n<div class=\"portlet-msg-info\"><span style=\"color:#323247\"><strong>Thung lũng chuỗi khối</strong></span><br />\n<span style=\"color:#8f919e\">Zug l&agrave; một thị trấn nhỏ ở Thụy Sĩ, c&oacute; lịch sử từ năm 1200, cho đến năm 2013 vẫn l&agrave; một trong số những thị trấn ngh&egrave;o ở Thụy Sĩ. Năm 2009, Zug đi ti&ecirc;n phong trong việc ứng dụng chuỗi khối v&agrave; năm 2013, Zug l&agrave; nơi đầu ti&ecirc;n tr&ecirc;n thế giới chấp nhận tiền kỹ thuật số dựa tr&ecirc;n ứng dụng chuỗi khối. V&agrave; v&igrave; l&agrave; nơi đầu ti&ecirc;n tr&ecirc;n thế giới, n&ecirc;n chỉ trong 3 năm, đến năm 2016, Zug đ&atilde; thu h&uacute;t được 600 doanh nghiệp l&agrave;m về chuỗi khối về Zug, trở th&agrave;nh thung lũng chuỗi khối của thế giới. C&acirc;u chuyện của Zug l&agrave; c&acirc;u chuyện đi đầu, d&aacute;m chấp nhận c&aacute;i mới, cho c&aacute;i mới một cơ hội v&agrave; v&igrave; thế ch&iacute;nh Zug c&oacute; cơ hội.</span></div>\n\n<h3>Kh&oacute; khăn v&agrave; th&aacute;ch thức lớn nhất của chuyển đổi số l&agrave; g&igrave;?</h3>\n\n<p>Kh&oacute; khăn lớn nhất của chuyển đổi số l&agrave; thay đổi th&oacute;i quen. Th&aacute;ch thức lớn nhất của chuyển đổi số l&agrave; c&oacute; nhận thức đ&uacute;ng.</p>\n\n<p>Lo&agrave;i người đ&atilde; quen với m&ocirc;i trường thực nhiều thế kỷ. Chuyển l&ecirc;n m&ocirc;i trường số l&agrave; thay đổi th&oacute;i quen. Thay đổi th&oacute;i quen l&agrave; việc kh&oacute;. Thay đổi th&oacute;i quen l&agrave; việc l&acirc;u d&agrave;i. Thay đổi th&oacute;i quen ở một tổ chức phụ thuộc chủ yếu v&agrave;o quyết t&acirc;m của người đứng đầu.</p>\n\n<p>Chuyển đổi số l&agrave; chuyện chưa c&oacute; tiền lệ, v&igrave; vậy, nhận thức đ&uacute;ng l&agrave; việc kh&oacute;. Nhận thức đ&uacute;ng về chuyển đổi số c&ograve;n phải đặt trong bối cảnh cụ thể của một tổ chức. Chuyển đổi số l&agrave; vấn đề nhận thức chứ kh&ocirc;ng phải chỉ l&agrave; vấn đề c&ocirc;ng nghệ, l&agrave; chuyện d&aacute;m l&agrave;m hay kh&ocirc;ng d&aacute;m l&agrave;m của người l&atilde;nh đạo.</p>\n\n<h3>Chuyển đổi số c&oacute; g&igrave; kh&ocirc;ng tốt?</h3>\n\n<p>Chuyển đổi số cũng giống như mọi thứ kh&aacute;c tr&ecirc;n đời, lu&ocirc;n lu&ocirc;n c&oacute; hai mặt, bởi v&igrave; c&ocirc;ng nghệ số l&agrave; cội nguồn của những điều tốt đẹp lớn lao v&agrave; cũng l&agrave; nguồn gốc của những t&aacute;c hại khủng khiếp tiềm t&agrave;ng. Ch&uacute;ng ta c&oacute; thể chưa h&igrave;nh dung hết được tất cả về những điều tốt đẹp v&agrave; điều khủng khiếp đ&oacute; ở thời điểm hiện nay.</p>\n\n<p>Việc chuyển đổi l&ecirc;n m&ocirc;i trường số đ&ograve;i hỏi mỗi người d&acirc;n tự trang bị cho m&igrave;nh những kỹ năng số cần thiết. Tương tự m&ocirc;i trường thực, lu&ocirc;n c&oacute; những đối tượng yếu thế tr&ecirc;n m&ocirc;i trường số, l&agrave; mục ti&ecirc;u nhắm đến của c&aacute;c đối tượng xấu. Đ&oacute; c&oacute; thể l&agrave; người gi&agrave;, trẻ em hay bất kỳ ai trong ch&uacute;ng ta.</p>\n\n<p>Những hệ lụy đến từ m&ocirc;i trường số c&oacute; thể kể ra như những chi&ecirc;u tr&ograve; lừa đảo, những vụ ăn hiếp, bắt nạt tr&ecirc;n mạng, những trang của c&aacute;c nh&oacute;m hận th&ugrave; v&agrave; những trang của c&aacute;c nh&oacute;m khủng bố.</p>\n\n<h3>Tại sao n&oacute;i chuyển đổi số l&agrave; thay đổi k&iacute;ch thước b&agrave;n ăn?</h3>\n\n<p>Chuyển đổi số, bắt đầu từ những đột ph&aacute; c&ocirc;ng nghệ số, nhưng chuyển đổi số kh&ocirc;ng phải chỉ l&agrave; c&ocirc;ng nghệ số, m&agrave; quan trọng hơn, chuyển đổi số l&agrave; chấp nhận c&aacute;i mới, do đ&oacute;, chuyển đổi số l&agrave; cuộc c&aacute;ch mạng về tư duy, nhận thức, thể chế, ch&iacute;nh s&aacute;ch nhiều hơn l&agrave; một cuộc c&aacute;ch mạng về c&ocirc;ng nghệ.</p>\n\n<p>V&iacute; dụ về thay đổi k&iacute;ch thước b&agrave;n ăn dưới đ&acirc;y xuất ph&aacute;t từ một &yacute; tưởng đơn giản, c&ocirc;ng nghệ khả thi. Tuy nhi&ecirc;n, thể hiện một c&aacute;ch thức tư duy v&agrave; h&agrave;nh động mới, đ&oacute; l&agrave; ra quyết định dựa tr&ecirc;n dữ liệu. Dữ liệu đ&uacute;ng, đủ v&agrave; kịp thời sẽ dẫn đến h&agrave;nh động đ&uacute;ng, kh&ocirc;ng cảm t&iacute;nh.</p>\n\n<div class=\"portlet-msg-info\"><span style=\"color:#323247\"><strong>Ph&acirc;n t&iacute;ch dữ liệu để ra quyết định tối ưu</strong></span><br />\n<span style=\"color:#8f919e\">Một c&ocirc;ng ty du lịch lớn ở Mỹ quyết định thực hiện một s&aacute;ng kiến chuyển đổi số bằng c&aacute;ch lắp đặt c&aacute;c camera c&ocirc;ng cộng v&agrave; c&aacute;c cảm biến thu thập dữ liệu c&ocirc;ng khai ở nh&agrave; ăn của c&ocirc;ng ty. Dữ liệu được truyền về hệ thống t&iacute;nh to&aacute;n để ph&acirc;n t&iacute;ch. Sau một thời gian, c&ocirc;ng ty nhận ra rằng nh&oacute;m những người thường xuy&ecirc;n ăn c&ugrave;ng nhau trong nh&oacute;m 4 người hoặc 8 người l&agrave; những người c&oacute; năng suất lao động cao hơn những người ăn một m&igrave;nh hoặc ăn theo nh&oacute;m đ&ocirc;ng hơn. Ch&iacute;nh v&igrave; vậy, c&ocirc;ng ty n&agrave;y đ&atilde; quyết định thay đổi k&iacute;ch thước b&agrave;n ăn trong nh&agrave; ăn của m&igrave;nh, chỉ gồm 2 loại b&agrave;n, d&agrave;nh cho 4 người v&agrave; d&agrave;nh cho 8 người.</span></div>\n</div>\n','[]',55,1,'2022-12-19 15:31:48','2022-12-19 15:31:48',1,'chuyen-doi-so-la-gi',NULL,NULL),(15,'Chuyển đổi số vì sao lại cần?',5,'Chuyển đổi số không chỉ giúp tăng năng suất, giảm chi phí mà còn mở ra không gian phát triển mới, tạo ra các giá trị mới ngoài các giá trị truyền thống vốn có.','<div class=\"article-content link-primary\">\n<h4>Chuyển đổi số quốc gia l&agrave; g&igrave;?</h4>\n\n<p>Chuyển đổi số quốc gia bao gồm 3 cấu phần ch&iacute;nh, lần lượt l&agrave; chuyển đổi số trong hoạt động của cơ quan nh&agrave; nước nhằm ph&aacute;t triển ch&iacute;nh phủ số, chuyển đổi số trong hoạt động của doanh nghiệp nhằm ph&aacute;t triển kinh tế số, chuyển đổi số trong hoạt động của người d&acirc;n nhằm ph&aacute;t triển x&atilde; hội số.</p>\n\n<h3>V&igrave; sao Việt Nam cần chuyển đổi số?</h3>\n\n<p>Chuyển đổi số mở ra cơ hội chưa từng c&oacute; cho Việt Nam. Ch&iacute;nh phủ số gi&uacute;p Ch&iacute;nh phủ hoạt động hiệu quả, hiệu lực hơn, minh bạch hơn, giảm tham nhũng. Kinh tế số th&uacute;c đẩy đổi mới s&aacute;ng tạo, tạo ra gi&aacute; trị mới, gi&uacute;p tăng năng suất lao động, tạo động lực tăng trưởng mới, tho&aacute;t bẫy thu nhập trung b&igrave;nh. X&atilde; hội số gi&uacute;p người d&acirc;n b&igrave;nh đẳng về cơ hội tiếp cận dịch vụ, đ&agrave;o tạo, tri thức, thu hẹp khoảng c&aacute;ch ph&aacute;t triển, giảm bất b&igrave;nh đẳng. C&aacute;c ng&agrave;nh, lĩnh vực được tối ưu, th&ocirc;ng minh h&oacute;a hướng đến n&acirc;ng cao trải nghiệm v&agrave; chất lượng cuộc sống của người d&acirc;n.</p>\n\n<h3>Việt Nam c&oacute; cơ hội chuyển đổi số kh&ocirc;ng?</h3>\n\n<p>Chuyển đổi số mở ra cơ hội cho tất cả c&aacute;c quốc gia. C&aacute;c nước đang ph&aacute;t triển thậm ch&iacute; c&oacute; thể tận dụng cơ hội để chuyển đổi số nhanh hơn. Đ&acirc;y l&agrave; cơ hội để Việt Nam vươn l&ecirc;n, thay đổi thứ hạng quốc gia. Lợi thế của Việt Nam l&agrave; dưới sự l&atilde;nh đạo của Đảng, Việt Nam c&oacute; thể c&oacute; những chủ trương lớn một c&aacute;ch nhanh ch&oacute;ng v&agrave; tập trung.</p>\n\n<p>Chuyển đổi số l&agrave; một sự thay đổi mang t&iacute;nh tổng thể v&agrave; to&agrave;n diện, từ Ch&iacute;nh phủ, đến từng doanh nghiệp, từng tổ chức, từng người d&acirc;n, trong mọi lĩnh vực. Văn h&oacute;a của người Việt Nam l&agrave; th&iacute;ch ứng nhanh với c&aacute;i mới, ham học hỏi c&aacute;i mới, s&aacute;ng tạo trong ứng dụng c&aacute;i mới. Việt Nam l&agrave; nước c&oacute; truyền thống l&acirc;u đời trong việc triển khai th&agrave;nh c&ocirc;ng c&aacute;c cuộc c&aacute;ch mạng to&agrave;n d&acirc;n.</p>\n\n<p>Nếu ch&uacute;ng ta cứ đi sau về c&aacute;i mới th&igrave; sẽ m&atilde;i m&atilde;i l&agrave; người đi sau, kh&ocirc;ng bao giờ thay đổi được thứ hạng quốc gia. Chỉ c&oacute; đi đầu về c&aacute;i mới th&igrave; mới c&oacute; cơ hội bứt ph&aacute; vươn l&ecirc;n th&agrave;nh nước ph&aacute;t triển.</p>\n\n<p>Chuyển đổi số th&igrave; Việt Nam với c&aacute;c nước đều mới như nhau. Cũng kh&ocirc;ng c&oacute; nhiều kinh nghiệm để học hỏi. C&aacute;c nước đ&atilde; ph&aacute;t triển th&igrave; thường lại kh&ocirc;ng hăng h&aacute;i với c&aacute;i mới v&igrave; họ đang ổn định trong c&aacute;i cũ. Chỉ c&oacute; những ai đang kh&oacute; khăn th&igrave; mới hăng h&aacute;i với c&aacute;i mới.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:16px\">V&iacute; di động gắn với SIM điện thoại</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Năm 2021, Việt Nam mới chấp nhận cho th&iacute; điểm mobile money, nghĩa l&agrave; thu&ecirc; bao di động được ph&eacute;p mở t&agrave;i khoản thanh to&aacute;n nhỏ ngay ch&iacute;nh tr&ecirc;n điện thoại di động của m&igrave;nh. Điều n&agrave;y c&oacute; thể gi&uacute;p đẩy nhanh việc phổ biến thanh to&aacute;n kh&ocirc;ng d&ugrave;ng tiền mặt, v&igrave; hiện nay, mới chỉ c&oacute; khoảng 30% d&acirc;n số c&oacute; t&agrave;i khoản ng&acirc;n h&agrave;ng, nhưng gần như 100% d&acirc;n số đ&atilde; c&oacute; điện thoại di động. Nhưng h&igrave;nh thức thanh to&aacute;n mobile money đ&atilde; được một nước ch&acirc;u Phi ngh&egrave;o l&agrave; Kenya ứng dụng c&aacute;ch đ&acirc;y 12 năm.</span></p>\n\n<p>Việt Nam ch&uacute;ng ta kh&ocirc;ng cần đợi ai, thấy hiệu quả l&agrave; sử dụng, nhanh nhạy với c&aacute;i mới để giải quyết c&aacute;c vấn đề của m&igrave;nh v&agrave; v&igrave; thế th&agrave;nh người đi đầu. V&igrave; đi đầu m&agrave; c&ocirc;ng nghệ mới sẽ chuyển dịch về Việt Nam, v&igrave; đi đầu m&agrave; nh&acirc;n lực c&ocirc;ng nghệ thế giới sẽ di chuyển đến Việt Nam. Bởi v&igrave; những nguồn lực n&agrave;y lu&ocirc;n di chuyển đến nơi đ&acirc;u c&oacute; thị trường.</p>\n\n<p>Bằng việc đi đầu về c&aacute;i mới m&agrave; ch&uacute;ng ta tạo ra thị trường về c&aacute;i mới. Thị trường lu&ocirc;n l&agrave; thỏi nam ch&acirc;m. Thị trường cũng l&agrave; nơi tạo ra c&ocirc;ng nghệ, ho&agrave;n thiện c&ocirc;ng nghệ. C&oacute; thị trường l&agrave; c&oacute; c&ocirc;ng nghệ, l&agrave; ph&aacute;t triển được c&ocirc;ng nghệ chứ kh&ocirc;ng phải như trước đ&acirc;y, c&oacute; c&ocirc;ng nghệ th&igrave; mới c&oacute; thị trường.</p>\n\n<p>Ngo&agrave;i ra, bằng việc đi đầu m&agrave; ch&uacute;ng ta trở th&agrave;nh người giỏi nhất v&agrave; thế giới sẽ phải đến Việt Nam học hỏi. Việt Nam trở th&agrave;nh trung t&acirc;m thế giới th&ocirc;ng qua việc cho ứng dụng c&aacute;i mới, d&ugrave;ng c&aacute;i mới để giải quyết c&aacute;c b&agrave;i to&aacute;n của m&igrave;nh, d&ugrave;ng c&aacute;i mới để ph&aacute;t triển đột ph&aacute;.</p>\n\n<h3>V&igrave; sao chuyển đổi số l&agrave; cơ hội lớn?</h3>\n\n<p>Chuyển đổi số l&agrave; cơ hội v&ocirc; gi&aacute; của ch&uacute;ng ta. Chuyển đổi số cũng l&agrave; cơ hội lớn của ch&uacute;ng ta trong v&ograve;ng một v&agrave;i thập kỷ tới. Những đột ph&aacute; về c&ocirc;ng nghệ số đều thai ngh&eacute;n trong nhiều chục năm mới phổ biến được v&agrave;i năm v&agrave; nhiều chục năm mới c&oacute; một lần.</p>\n\n<p>Ch&uacute;ng ta kh&ocirc;ng tiến khi người kh&aacute;c tiến l&agrave; ch&uacute;ng ta đ&atilde; tụt lại. Nếu bỏ lỡ cơ hội n&agrave;y, ch&uacute;ng ta sẽ bị tụt lại sau c&aacute;c quốc gia kh&aacute;c xa hơn nữa, do người đi trước thắng cuộc l&agrave; người lấy được tất cả</p>\n\n<h3>V&igrave; sao doanh nghiệp cần chuyển đổi số?</h3>\n\n<p>Bởi v&igrave;, thị trường lu&ocirc;n thay đổi, h&agrave;nh vi của người ti&ecirc;u d&ugrave;ng lu&ocirc;n thay đổi, lu&ocirc;n c&oacute; một lớp người d&ugrave;ng mới, nhu cầu mới, y&ecirc;u cầu những trải nghiệm mới.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:16px\">C&acirc;u chuyện của Kodak</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Năm 1975, kỹ sư của Kodak đ&atilde; ph&aacute;t minh ra chiếc m&aacute;y ảnh số, nhưng Kodak đ&atilde; giấu k&iacute;n v&igrave; lo sợ ph&aacute;t minh n&agrave;y sẽ khiến người ti&ecirc;u d&ugrave;ng kh&ocirc;ng mua phim v&agrave; thuốc rửa ảnh nữa. Thường th&igrave; doanh nghiệp c&agrave;ng lớn v&agrave; c&agrave;ng th&agrave;nh c&ocirc;ng th&igrave; lại c&agrave;ng chậm chuyển đổi. Kodak tiếp tục bỏ qua nhiều cơ hội chuyển đổi số kh&aacute;c. Nhưng Kodak kh&ocirc;ng l&agrave;m th&igrave; c&oacute; người kh&aacute;c l&agrave;m. Sự xuất hiện của iPhone năm 2007, rồi Instagram năm 2010 l&agrave; nguy&ecirc;n nh&acirc;n trực tiếp khiến Kodak ph&aacute; sản v&agrave;o năm 2012.</span></p>\n\n<h3>V&igrave; sao c&aacute; nh&acirc;n cần chuyển đổi số?</h3>\n\n<p>Cuộc sống kh&ocirc;ng ngừng vận động, biến đổi. Mỗi người cũng cần kh&ocirc;ng ngừng học tập, thay đổi, th&iacute;ch nghi, nếu kh&ocirc;ng sẽ bị bỏ lại ở ph&iacute;a sau.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:16px\">Tự t&ocirc;i, ng&agrave;y n&agrave;o cũng học</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Sinh thời, B&aacute;c Hồ đ&atilde; n&oacute;i: &ldquo;B&aacute;c thường nghe n&oacute;i c&oacute; đồng ch&iacute; 40 tuổi đ&atilde; cho m&igrave;nh l&agrave; gi&agrave; n&ecirc;n &iacute;t chịu kh&oacute; học tập. Nghĩ như vậy l&agrave; kh&ocirc;ng đ&uacute;ng, 40 tuổi chưa phải l&agrave; gi&agrave;. B&aacute;c đ&atilde; 76 tuổi nhưng vẫn cố gắng học th&ecirc;m. Ch&uacute;ng ta phải học v&agrave; hoạt động c&aacute;ch mạng suốt đời. C&ograve;n sống th&igrave; c&ograve;n phải học, c&ograve;n phải hoạt động c&aacute;ch mạng&rdquo;.</span></p>\n\n<h3>Tại sao n&oacute;i chuyển đổi số hay l&agrave; chết?</h3>\n\n<p>Trong lịch sử h&agrave;ng triệu năm của Tr&aacute;i đất, sự tuyệt chủng h&agrave;ng loạt v&agrave; sự h&igrave;nh th&agrave;nh c&aacute;c lo&agrave;i sau đ&oacute; diễn ra như một lẽ tự nhi&ecirc;n, do c&aacute;c t&aacute;c nh&acirc;n biến đổi l&agrave; sự thay đổi về kh&iacute; hậu hay điều kiện sống. Tương tự như vậy, h&agrave;ng loạt doanh nghiệp đ&atilde; bị ph&aacute; sản v&agrave;o những thập ni&ecirc;n đầu của thế kỷ XXI.</p>\n\n<p>Từ năm 2000 đến nay, 52% trong số c&aacute;c doanh nghiệp Fortune đ&atilde; bị mua lại, s&aacute;p nhập hoặc ph&aacute; sản. Người ta ước t&iacute;nh rằng 40% c&aacute;c doanh nghiệp tồn tại ng&agrave;y h&ocirc;m nay sẽ đ&oacute;ng cửa trong 10 năm tới. Căn nguy&ecirc;n ch&iacute;nh l&agrave; do chậm hoặc thất bại trong việc chuyển đổi số.</p>\n\n<p>Ai l&agrave;m chuyển đổi th&agrave;nh c&ocirc;ng từ m&ocirc;i trường thực sang m&ocirc;i trường số, người ấy sẽ tồn tại. Kh&ocirc;ng chuyển đổi, kh&ocirc;ng kịp chuyển đổi sẽ bị thay thế, đ&agrave;o thải. Do đ&oacute;, l&agrave; chuyển đổi số hay l&agrave; chết.</p>\n</div>\n','[]',1,1,'2022-12-19 15:32:02','2022-12-19 15:32:02',1,'chuyen-doi-so-vi-sao-lai-can',NULL,NULL),(16,'Chuyển đổi số khi nào?',5,'Chuyển đổi số là quá trình khách quan, muốn hay không thì chuyển đổi số vẫn xảy ra và đang diễn ra. Mỗi cá nhân, tổ chức có thể tham gia hoặc đứng ngoài quá trình đó. Nếu đứng ngoài, sẽ có khoảng cách lớn giữa các tổ chức, doanh nghiệp, lĩnh vực đã thực hiện và chưa thực hiện chuyển đổi số và khoảng cách đó sẽ dần được nới rộng theo cấp số nhân.','<div class=\"article-content link-primary\">\n<h4>Nh&igrave;n về đường d&agrave;i, th&igrave; mọi c&aacute; nh&acirc;n, tổ chức đều sẽ phải chuyển đổi số, kh&ocirc;ng ai c&oacute; thể đứng ngo&agrave;i cuộc. Tuy nhi&ecirc;n, t&aacute;c động của c&ocirc;ng nghệ số l&ecirc;n c&aacute;c tổ chức, c&aacute;c ng&agrave;nh nghề l&agrave; kh&aacute;c nhau, n&ecirc;n lộ tr&igrave;nh chuyển đổi sẽ kh&aacute;c nhau. Một doanh nghiệp sản xuất gạch c&oacute; thể &iacute;t bị t&aacute;c động bởi c&ocirc;ng nghệ số hơn l&agrave; một doanh nghiệp vận tải.</h4>\n\n<p>Nếu một doanh nghiệp lớn kh&ocirc;ng đổi mới, n&oacute; c&oacute; thể sẽ bị thay thế bởi những doanh nghiệp mới nổi, nhỏ v&agrave; linh hoạt hơn.</p>\n\n<p>Chuyển đổi số thiết lập lại mặt bằng cạnh tranh giữa c&aacute;c doanh nghiệp, chuyển từ &ldquo;c&aacute; to nuốt c&aacute; b&eacute;&rdquo; sang &ldquo;c&aacute; nhanh nuốt c&aacute; chậm&rdquo;. Trong chuyển đổi số, doanh nghiệp n&agrave;o đi nhanh sẽ c&oacute; lợi thế cạnh tranh hơn.</p>\n\n<h3>Chuyển đổi số trước khi khủng khoảng?</h3>\n\n<p>Trong bối cảnh b&aacute;o ch&iacute; to&agrave;n thế giới gặp kh&oacute; khăn khi nguồn thu quảng c&aacute;o li&ecirc;n tục sụt giảm, th&igrave; tờ Thời b&aacute;o New York (New York Times) tại Mỹ lại ghi nhận dấu mốc doanh thu ấn tượng. Qu&yacute; IV/2019, mảng kinh doanh số của t&ograve;a soạn n&agrave;y đ&atilde; vượt ngưỡng 800 triệu USD, trong đ&oacute; c&oacute; tới 420 triệu USD đến từ độc giả trả ph&iacute;, cao hơn nguồn thu từ mảng kinh doanh truyền thống l&agrave; quảng c&aacute;o. Đến hết năm 2020, số độc giả trả ph&iacute; cho c&aacute;c ấn phẩm in v&agrave; ấn phẩm số của The New York Times đ&atilde; đạt hơn 7,5 triệu. Trong đ&oacute;, đăng k&yacute; cho c&aacute;c ấn phẩm số đ&atilde; vượt 5 triệu lượt, chiếm 67%(1).</p>\n\n<p>Th&agrave;nh c&ocirc;ng của New York Times kh&ocirc;ng đến một c&aacute;ch ngẫu nhi&ecirc;n. Những l&atilde;nh đạo t&ograve;a soạn từ sớm đ&atilde; nh&igrave;n thấy ở Internet tiềm năng to lớn trong việc ph&aacute;t huy gi&aacute; trị cốt l&otilde;i của t&ograve;a soạn l&agrave; mang đến những c&acirc;u chuyện theo ti&ecirc;u chuẩn b&aacute;o ch&iacute; cao nhất đến với độc giả to&agrave;n thế giới. Sau nhiều năm ki&ecirc;n tr&igrave; đi theo con đường ri&ecirc;ng với trọng t&acirc;m ưu ti&ecirc;n ph&aacute;t triển doanh thu số, những nỗ lực của New York Times đ&atilde; được đền đ&aacute;p khi t&ograve;a soạn n&agrave;y hiện duy tr&igrave; vị tr&iacute; t&ograve;a soạn danh gi&aacute; nhất với 120 giải Pulitzer v&agrave; đồng thời cũng l&agrave; t&ograve;a soạn c&oacute; tỉ lệ ph&oacute;ng vi&ecirc;n biết lập tr&igrave;nh cao nhất trong l&agrave;ng b&aacute;o.</p>\n\n<h3>Hay khủng hoảng rồi mới chuyển đổi?</h3>\n\n<p>Được th&agrave;nh lập v&agrave;o năm 1986 bởi ba nh&agrave; b&aacute;o, tờ Độc lập (The Independent) của nước Anh đ&atilde; trở th&agrave;nh ng&ocirc;i nh&agrave; của những nh&agrave; b&aacute;o nổi tiếng như Robert Fisk, một ph&oacute;ng vi&ecirc;n l&acirc;u năm ở Trung Đ&ocirc;ng v&agrave; Andrew Marr, một ph&aacute;t thanh vi&ecirc;n nổi tiếng v&agrave; l&agrave; một sử gia. Một trong những người tham gia ban đầu c&ograve;n c&oacute; nh&agrave; văn Helen Fielding, l&agrave; t&aacute;c giả của cuốn s&aacute;ch b&aacute;n chạy Nhật k&yacute; tiểu thư Jones, viết cho chuy&ecirc;n mục ẩn danh của tờ Độc lập.</p>\n\n<p>V&agrave;o giai đoạn đỉnh cao, tờ Độc lập đ&atilde; từng xuất bản 400.000 bản mỗi ng&agrave;y, rồi sau đ&oacute;, c&ugrave;ng với sự ph&aacute;t triển của Internet, số lượng ấn bản giảm xuống chỉ c&ograve;n 40.000 bản mỗi ng&agrave;y v&agrave;o năm 2015 v&agrave; tuy&ecirc;n bố dừng ấn phẩm v&agrave;o năm 2016.</p>\n\n<p>Khi xuất hiện v&agrave; bước v&agrave;o giai đoạn đỉnh cao, tờ Độc lập được coi l&agrave; tờ b&aacute;o ti&ecirc;n phong trong lĩnh vực b&aacute;o ch&iacute;. V&agrave; rồi khi dừng lại, tờ Độc lập được coi l&agrave; tờ b&aacute;o bị đ&oacute;ng cửa bởi t&aacute;c động của Internet.</p>\n\n<h3>C&aacute;ch tốt nhất để dự đo&aacute;n tương lai l&agrave; g&igrave;?</h3>\n\n<p>Chuyển đổi số l&agrave; tạo ra c&aacute;i mới, l&agrave; c&aacute;i chưa c&oacute; tiền lệ. Kỷ nguy&ecirc;n chuyển đổi số l&agrave; kỷ nguy&ecirc;n của những điều kh&ocirc;ng thể trở th&agrave;nh c&oacute; thể v&agrave; ngược lại. V&agrave; c&aacute;ch tốt nhất để dự đo&aacute;n tương lai l&agrave; chủ động tạo ra n&oacute;.</p>\n\n<p>Chuyển đổi số quan trọng nhất l&agrave; chuyển đổi tư duy, nhận thức. Điều đ&oacute; c&oacute; thể l&agrave;m ngay lập tức, v&igrave; chỉ phụ thuộc v&agrave;o ch&iacute;nh mỗi người ch&uacute;ng ta.</p>\n</div>\n','[]',0,1,'2022-12-19 15:32:12','2022-12-19 15:32:12',1,'chuyen-doi-so-khi-nao',NULL,NULL),(17,'Chuyển đổi số như thế nào?',5,'Chuyển đổi số là một quá trình đa dạng, không có con đường và hình mẫu chung cho tất cả, và do vậy, từng tổ chức, từng cá nhân cần xác định lộ trình riêng, thích hợp với mình.','<div class=\"article-content link-primary\">\n<h4>Kinh nghiệm chỉ l&agrave; b&agrave;i học trong những chuyện đ&atilde; c&oacute; tiền lệ, kh&ocirc;ng c&oacute; kinh nghiệm cho những điều chưa xảy đến. Chuyển đổi số l&agrave; c&acirc;u chuyện chưa c&oacute; tiền lệ, v&igrave; vậy, tầm nh&igrave;n phải thay thế kinh nghiệm.</h4>\n\n<h3>C&oacute; một lộ tr&igrave;nh chung để tham khảo hay kh&ocirc;ng?</h3>\n\n<p>Để thực hiện chuyển đổi số, c&oacute; thể tham khảo một lộ tr&igrave;nh gợi &yacute; gồm ba bước chung nhất như sau:</p>\n\n<p>Bước 1: C&oacute; nhận thức v&agrave; tư duy đ&uacute;ng về chuyển đổi số. Với một tổ chức, nhận thức của l&atilde;nh đạo cao nhất l&agrave; điều kiện ti&ecirc;n quyết, để từ đ&oacute; truyền nhận thức, cảm hứng, kh&aacute;t vọng v&agrave; quyết t&acirc;m thay đổi tới c&aacute;c th&agrave;nh vi&ecirc;n.</p>\n\n<p>Bước 2: X&acirc;y dựng chiến lược v&agrave; kế hoạch h&agrave;nh động. X&aacute;c định trạng th&aacute;i hiện tại v&agrave; trạng th&aacute;i cần đến để định r&otilde; mục ti&ecirc;u, gồm cả việc x&aacute;c định m&ocirc; h&igrave;nh hoạt động, kinh doanh mới trong m&ocirc;i trường số, x&acirc;y dựng kế hoạch h&agrave;nh động với c&aacute;c giai đoạn hợp l&yacute;, nội dung cụ thể.</p>\n\n<p>Đối với một địa phương, v&igrave; chuyển đổi số l&agrave; mới, v&igrave; vậy Tỉnh ủy n&ecirc;n c&oacute; nghị quyết chuy&ecirc;n đề về chuyển đổi số, sau đ&oacute; UBND ban h&agrave;nh Kế hoạch h&agrave;nh động về chuyển đổi số.</p>\n\n<p>Bước 3: X&aacute;c định c&ocirc;ng nghệ số chủ yếu trong lĩnh vực hoạt động cũng như c&aacute;c nền tảng cần c&oacute; để hỗ trợ việc chuyển đổi. Từ đ&oacute;, x&acirc;y dựng năng lực số, gồm đ&agrave;o tạo nh&acirc;n lực số, x&acirc;y dựng th&oacute;i quen, h&igrave;nh th&agrave;nh văn h&oacute;a đổi mới với m&ocirc; h&igrave;nh hoạt động mới v&agrave; thực hiện chuyển đổi.</p>\n\n<h3>X&acirc;y dựng chiến lược chuyển đổi số như thế n&agrave;o?</h3>\n\n<p>Chuyển đổi số l&agrave; việc của ch&iacute;nh mỗi tổ chức. V&agrave; việc n&agrave;y cần phải xuất ph&aacute;t từ nhận thức, từ quyết t&acirc;m v&agrave; mức độ nắm r&otilde; t&igrave;nh h&igrave;nh của tổ chức. Mỗi tổ chức, c&aacute; nh&acirc;n l&agrave; kh&aacute;c nhau, do đ&oacute;, cần c&oacute; những chiến lược kh&aacute;c nhau. Chiến lược chuyển đổi số kh&ocirc;ng phải l&agrave; một bản chiến lược ứng dụng c&ocirc;ng nghệ số. Chiến lược chuyển đổi số kh&ocirc;ng phải l&agrave; một bản kế hoạch ngắn hạn, cũng kh&ocirc;ng phải l&agrave; một bản kế hoạch d&agrave;i hạn. Một bản chiến lược ph&ugrave; hợp thường hoạch định cho giai đoạn từ 3-5 năm.</p>\n\n<p>Chiến lược chuyển đổi số do người đứng đầu chỉ đạo x&acirc;y dựng v&agrave; phải lan tỏa, thấm nhuần tới từng th&agrave;nh vi&ecirc;n của tổ chức. Chiến lược chuyển đổi số bắt đầu từ tầm nh&igrave;n của người đứng đầu, nhưng khi thực thi cần kh&ocirc;ng ngừng đo lường, kiểm nghiệm thực tế xem điều g&igrave; đang đi đ&uacute;ng hướng v&agrave; điều g&igrave; kh&ocirc;ng, sau đ&oacute; nhanh ch&oacute;ng điều chỉnh theo thực tế.</p>\n\n<h3>Thu&ecirc; tư vấn chuyển đổi số như thế n&agrave;o?</h3>\n\n<p>Việc cho rằng c&aacute;i g&igrave; m&igrave;nh kh&ocirc;ng biết th&igrave; c&oacute; thể thu&ecirc; tư vấn l&agrave; một quan niệm thường dẫn đến sai lầm. M&agrave; ngược lại, c&aacute;i g&igrave; m&igrave;nh biết r&otilde; rồi v&agrave; thu&ecirc; tư vấn th&igrave; thường dẫn đến th&agrave;nh c&ocirc;ng. Chuyển đổi số cũng vậy. Chuyển đổi số l&agrave; c&acirc;u chuyện của ch&iacute;nh mỗi c&aacute; nh&acirc;n, tổ chức. Chuyển đổi số l&agrave; qu&aacute; tr&igrave;nh thay đổi tổng thể v&agrave; to&agrave;n diện. V&agrave; c&aacute;nh cửa của sự thay đổi n&agrave;y chỉ c&oacute; thể được mở từ b&ecirc;n trong. Nghĩa l&agrave;, chủ thể đưa ra tầm nh&igrave;n, quyết định chuyển đổi số l&agrave; ch&iacute;nh bạn, c&ograve;n chuy&ecirc;n gia tư vấn đưa ra một kế hoạch đầy đủ v&agrave; lộ tr&igrave;nh thực hiện mang t&iacute;nh chất tham khảo.</p>\n\n<h3>Nền tảng số l&agrave; g&igrave;?</h3>\n\n<p>Nền tảng số l&agrave; bước ph&aacute;t triển tiếp theo của hệ thống th&ocirc;ng tin, l&agrave; yếu tố tạo ra sự kh&aacute;c biệt căn bản giữa ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; chuyển đổi số. Nền tảng số c&oacute; khả năng đ&aacute;p ứng ngay nhu cầu của người sử dụng như l&agrave; dịch vụ, giống như dịch vụ điện, giống như dịch vụ nước v&agrave; c&agrave;ng nhiều người sử dụng th&igrave; chi ph&iacute; c&agrave;ng rẻ.</p>\n\n<p>V&iacute; dụ về nền tảng chuyển đổi số cấp x&atilde;. Thay v&igrave; ph&aacute;t triển 11.000 phần mềm cho 11.000 x&atilde; tr&ecirc;n to&agrave;n quốc, sử dụng một nền tảng chung cho 11.000 x&atilde; sẽ r&uacute;t ngắn thời gian triển khai v&agrave; tối ưu chi ph&iacute;.</p>\n\n<h3>Sử dụng nền tảng để chuyển đổi số như thế n&agrave;o?</h3>\n\n<p>Sử dụng nền tảng số được x&aacute;c định l&agrave; giải ph&aacute;p đột ph&aacute; để th&uacute;c đẩy chuyển đổi số nhanh hơn, giảm chi ph&iacute;, tăng hiệu quả. Nền tảng được tạo ra bởi những doanh nghiệp c&ocirc;ng nghệ số xuất sắc, cho ph&eacute;p c&aacute;c cơ quan, tổ chức, c&aacute; nh&acirc;n sử dụng ngay dưới dạng dịch vụ thay v&igrave; phải tự đầu tư, tự vận h&agrave;nh.</p>\n\n<p>V&iacute; dụ, Việt Nam c&oacute; h&agrave;ng ngh&igrave;n b&aacute;o v&agrave; tạp ch&iacute;, nếu từng cơ quan n&agrave;y phải đầu tư nền tảng số như tờ Thời b&aacute;o New York đ&atilde; n&oacute;i ở tr&ecirc;n th&igrave; sẽ rất tốn k&eacute;m, nhiều cơ quan kh&ocirc;ng c&oacute; đủ nguồn lực, cả về t&agrave;i ch&iacute;nh v&agrave; nh&acirc;n lực để đầu tư. Nhưng nếu c&oacute; sẵn một nền tảng để c&aacute;c cơ quan b&aacute;o ch&iacute; c&oacute; thể l&ecirc;n đ&oacute;, tiếp cận người đọc, tiếp cận quảng c&aacute;o th&igrave; h&agrave;ng ngh&igrave;n cơ quan b&aacute;o ch&iacute; c&oacute; thể chuyển đổi sang m&ocirc;i trường số rất nhanh.</p>\n\n<p>Tương tự như vậy, nếu MISA, một c&ocirc;ng ty c&ocirc;ng nghệ số chuy&ecirc;n về phần mềm kế to&aacute;n, đầu tư ph&aacute;t triển một nền tảng, để những người l&agrave;m kế to&aacute;n chuy&ecirc;n nghiệp c&oacute; thể l&agrave;m dịch vụ kế to&aacute;n cho c&aacute;c doanh nghiệp, để cung cấp dịch vụ kế to&aacute;n cho c&aacute;c doanh nghiệp si&ecirc;u nhỏ, c&aacute;c doanh nghiệp nhỏ v&agrave; vừa th&igrave; việc chuyển đổi số về kế to&aacute;n cho c&aacute;c doanh nghiệp sẽ diễn ra rất nhanh. Đồng thời, n&oacute; cũng k&iacute;ch th&iacute;ch c&aacute;c hộ kinh doanh chuyển sang doanh nghiệp, v&igrave; kh&ocirc;ng phải đi thu&ecirc; kế to&aacute;n vi&ecirc;n, vốn l&agrave; việc rất kh&oacute;, nhất l&agrave; ở n&ocirc;ng th&ocirc;n, v&ugrave;ng s&acirc;u, v&ugrave;ng xa.</p>\n\n<h3>Hiểu về Make in Việt Nam như thế n&agrave;o?</h3>\n\n<p>Make in Viet Nam l&agrave; thiết kế tại Việt Nam, l&agrave;m ra sản phẩm tại Việt Nam. K&ecirc;u gọi chuyển từ lắp r&aacute;p, gia c&ocirc;ng sang l&agrave;m sản phẩm Việt Nam. Kh&ocirc;ng l&agrave;m sản phẩm th&igrave; Việt Nam kh&ocirc;ng h&ugrave;ng cường, thịnh vượng được.</p>\n\n<p>Việt Nam đ&atilde; c&oacute; thiết bị 5G Việt Nam, c&oacute; mạng x&atilde; hội Việt Nam, c&oacute; sản phẩm an to&agrave;n, an ninh mạng Việt Nam v&agrave; rất nhiều phần mềm Việt Nam.</p>\n\n<p>Để th&uacute;c đẩy Make in Viet Nam, tất cả từ nh&agrave; nước đến doanh nghiệp, người d&acirc;n h&atilde;y đưa c&aacute;c b&agrave;i to&aacute;n, c&aacute;c vấn đề của đất nước, của x&atilde; hội tới cộng đồng doanh nghiệp c&ocirc;ng nghệ số Việt Nam để họ tham gia giải quyết.</p>\n\n<h3>H&igrave;nh th&agrave;nh văn h&oacute;a trong kỷ nguy&ecirc;n số như thế n&agrave;o?</h3>\n\n<p>Văn h&oacute;a được h&igrave;nh th&agrave;nh từ th&oacute;i quen được lặp đi lặp lại. V&igrave; vậy, để h&igrave;nh th&agrave;nh văn h&oacute;a, trước ti&ecirc;n phải h&igrave;nh th&agrave;nh v&agrave; duy tr&igrave; th&oacute;i quen. C&oacute; nhiều c&aacute;ch để h&igrave;nh th&agrave;nh v&agrave; duy tr&igrave; th&oacute;i quen, nhưng trong kỷ nguy&ecirc;n số, một trong những c&aacute;ch để h&igrave;nh th&agrave;nh v&agrave; duy tr&igrave; th&oacute;i quen của mỗi th&agrave;nh vi&ecirc;n trong tổ chức l&agrave; sử dụng ch&iacute;nh c&ocirc;ng nghệ số để tạo ra c&aacute;c nền tảng, c&aacute;c hệ thống quản l&yacute;, trong đ&oacute;, c&aacute;c th&oacute;i quen cần c&oacute; đ&atilde; được nh&uacute;ng v&agrave; &ldquo;cứng h&oacute;a&rdquo; trong ch&iacute;nh nền tảng v&agrave; hệ thống để mỗi th&agrave;nh vi&ecirc;n của tổ chức khi l&agrave;m việc buộc phải tu&acirc;n thủ.</p>\n\n<h3>L&agrave;m thế n&agrave;o để địa phương khởi động chuyển đổi số một c&aacute;ch dễ d&agrave;ng?</h3>\n\n<p>Trước đ&acirc;y, triển khai ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin kh&ocirc;ng phải l&agrave; một việc qu&aacute; dễ d&agrave;ng. V&igrave; phải triển khai, vận h&agrave;nh hệ thống, phải đ&agrave;o tạo người d&ugrave;ng. Những việc n&agrave;y lại phải l&agrave;m ở từng đơn vị, từng x&atilde;, từng huyện, từng tỉnh n&ecirc;n rất thiếu c&aacute;n bộ c&ocirc;ng nghệ th&ocirc;ng tin. Với c&ocirc;ng nghệ số th&igrave; kh&ocirc;ng c&oacute; c&aacute;c b&agrave;i to&aacute;n n&agrave;y. Tất cả l&agrave; tr&ecirc;n nền tảng, c&aacute;c x&atilde;, c&aacute;c huyện chỉ cần đăng k&yacute; v&agrave;o để d&ugrave;ng, kh&ocirc;ng c&ograve;n hệ thống ở từng x&atilde;, từng huyện nữa. Dễ d&ugrave;ng đến mức kh&ocirc;ng cần đ&agrave;o tạo. C&aacute;c quy tr&igrave;nh, c&aacute;ch l&agrave;m đ&atilde; được đưa v&agrave;o nền tảng số, người d&ugrave;ng kh&ocirc;ng cần nhớ, kh&ocirc;ng cần học.</p>\n\n<p>Đối với những b&agrave;i to&aacute;n mới th&igrave; l&atilde;nh đạo tỉnh chỉ cần đặt ra b&agrave;i to&aacute;n, đặt ra mục ti&ecirc;u cần đạt được, lượng h&oacute;a gi&aacute; trị cần tạo ra để biết chi ph&iacute; tối đa c&oacute; thể chi. L&atilde;nh đạo cũng phải tạo ra thể chế cho c&aacute;i mới được ph&eacute;p vận h&agrave;nh. L&atilde;nh đạo kh&ocirc;ng cần qu&aacute; quan t&acirc;m đến việc l&agrave;m thế n&agrave;o. Việc n&agrave;y h&atilde;y để cho doanh nghiệp l&agrave;m, đ&acirc;y l&agrave; nghề của họ, v&agrave; họ giỏi hơn l&atilde;nh đạo. L&atilde;nh đạo khi bị quấn v&agrave;o b&agrave;n bạc c&aacute;ch l&agrave;m, nhất l&agrave; c&aacute;ch l&agrave;m một việc m&agrave; m&igrave;nh kh&ocirc;ng c&oacute; chuy&ecirc;n m&ocirc;n như chuyển đổi số, th&igrave; sẽ kh&ocirc;ng ra được quyết định l&agrave;m hay kh&ocirc;ng l&agrave;m v&agrave; v&igrave; vậy m&agrave; nhiều việc sẽ bị treo ở đ&oacute;.</p>\n\n<p>C&oacute; một c&aacute;ch tiếp cận nữa cũng rất hiệu quả l&agrave; h&atilde;y để cho người d&acirc;n, doanh nghiệp đề xuất với tỉnh những việc, những gi&aacute; trị m&agrave; họ c&oacute; thể tạo ra cho tỉnh. Tỉnh xem x&eacute;t nếu thấy gi&aacute; trị tạo ra cho tỉnh lớn hơn chi ph&iacute; th&igrave; cho l&agrave;m. Đ&acirc;y l&agrave; c&aacute;ch đưa to&agrave;n d&acirc;n v&agrave;o tham gia đổi mới s&aacute;ng tạo cho tỉnh. To&agrave;n d&acirc;n tham gia ph&aacute;t triển tỉnh. Kh&ocirc;ng n&ecirc;n coi c&aacute;c &yacute; tưởng ph&aacute;t triển tỉnh nh&agrave; l&agrave; độc quyền của c&ocirc;ng chức nh&agrave; nước. Thường th&igrave; những đột ph&aacute;, những gi&aacute; trị lớn bất ngờ l&agrave; xuất ph&aacute;t từ s&aacute;ng tạo của tr&iacute; tuệ nh&acirc;n d&acirc;n.</p>\n</div>\n','[]',0,1,'2022-12-19 15:32:22','2022-12-19 15:32:22',1,'chuyen-doi-so-nhu-the-nao',NULL,NULL),(18,'Chuyển đổi số đem lại lợi ích gì?',5,'Chuyển đổi số giúp người dân bình đẳng về cơ hội tiếp cận dịch vụ, đào tạo, tri thức, qua đó giúp thu hẹp khoảng cách số thông qua việc phát triển chính phủ số, kinh tế số và xã hội số.','<div class=\"article-content link-primary\">\n<h4>Ch&iacute;nh phủ số đem lại lợi &iacute;ch g&igrave; cho người d&acirc;n?</h4>\n\n<p>Ch&iacute;nh phủ số nhờ dữ liệu số v&agrave; c&ocirc;ng nghệ số thấu hiểu người d&acirc;n hơn, v&igrave; vậy, cung cấp dịch vụ số tốt hơn, chăm s&oacute;c người d&acirc;n tốt hơn.</p>\n\n<p>Một đứa trẻ khi sinh ra được cấp một m&atilde; định danh duy nhất, đến kỳ th&igrave; gia đ&igrave;nh nhận được th&ocirc;ng b&aacute;o đi ti&ecirc;m ph&ograve;ng từ ch&iacute;nh quyền, đến tuổi đi học th&igrave; ch&iacute;nh quyền dựa tr&ecirc;n số liệu d&acirc;n cư để quyết định ph&acirc;n bổ cơ sở vật chất của c&aacute;c cơ sở gi&aacute;o dục, tr&aacute;nh nơi bị thừa, nơi lại thiếu, đến tuổi trưởng th&agrave;nh th&igrave; tự động nhận được căn cước c&ocirc;ng d&acirc;n. Khi dịch bệnh b&ugrave;ng ph&aacute;t th&igrave; kịp thời nhận được cảnh b&aacute;o, chăm s&oacute;c y tế.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:14px\">An ninh v&agrave; trật tự an to&agrave;n x&atilde; hội</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Khoảng 17h00 ng&agrave;y 21/8/2020, một b&eacute; trai hơn 2 tuổi ở Bắc Ninh khi đi chơi c&ocirc;ng vi&ecirc;n c&ugrave;ng gia đ&igrave;nh đ&atilde; bị kẻ gian lợi dụng sơ hở để bắt c&oacute;c. Bằng c&ocirc;ng nghệ số l&agrave; camera gi&aacute;m s&aacute;t th&ocirc;ng minh v&agrave; ph&acirc;n t&iacute;ch dữ liệu, cơ quan chức năng nhanh ch&oacute;ng khoanh v&ugrave;ng, x&aacute;c định v&agrave; truy vết đối tượng. Ng&agrave;y 22/8/2020, chưa đầy 24h sau, cơ quan chức năng đ&atilde; nhanh ch&oacute;ng đuổi theo đối tượng đến Tuy&ecirc;n Quang, bắt gọn v&agrave; trả ch&aacute;u b&eacute; trở lại gia đ&igrave;nh. Nhờ c&ocirc;ng nghệ số, ch&iacute;nh quyền hoạt động hiệu lực, hiệu quả v&agrave; minh bạch hơn c&ograve;n gi&uacute;p bảo vệ người d&acirc;n tốt hơn.</span></p>\n\n<h3>Kinh tế số đem lại lợi &iacute;ch g&igrave; cho người d&acirc;n?</h3>\n\n<p>Kinh tế số cho ph&eacute;p mỗi người d&acirc;n c&oacute; thể tiếp cận to&agrave;n bộ thị trường một c&aacute;ch nhanh ch&oacute;ng theo c&aacute;ch chưa từng c&oacute;. Nếu như trước đ&acirc;y, người d&acirc;n mang h&agrave;ng ra chợ b&aacute;n th&igrave; chỉ tiếp cận được v&agrave;i chục đến v&agrave;i trăm người trong khu vực địa l&yacute; hạn chế của m&igrave;nh. C&ograve;n hiện nay, với thương mại điện tử, người d&acirc;n c&oacute; thể b&aacute;n h&agrave;ng cho h&agrave;ng triệu người, tr&ecirc;n to&agrave;n thế giới.</p>\n\n<p>Mỗi người d&acirc;n với một chiếc điện thoại th&ocirc;ng minh, mỗi hộ gia đ&igrave;nh một đường c&aacute;p quang l&agrave; c&oacute; thể trở th&agrave;nh một doanh nghiệp, l&agrave; c&oacute; thể tiếp cận cả thế giới.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:14px\">Người d&acirc;n l&ecirc;n s&agrave;n giao dịch</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Trước đ&acirc;y, ngư d&acirc;n vẫn thường mang số c&aacute; họ đ&aacute;nh bắt được h&agrave;ng ng&agrave;y ra chợ b&aacute;n v&agrave; ngồi nh&igrave;n ch&uacute;ng hư thối khi kh&ocirc;ng c&oacute; người mua, giờ đ&acirc;y, họ c&oacute; thể giữ c&aacute; ở dưới s&ocirc;ng v&agrave; đợi kh&aacute;ch đặt. Khi nhận được đơn h&agrave;ng qua mạng, c&aacute; được lấy ra khỏi nước v&agrave; chuẩn bị chuyển cho kh&aacute;ch h&agrave;ng. Hệ thống bưu ch&iacute;nh, chuyển ph&aacute;t sẽ giao h&agrave;ng tới tận tay người mua. Kh&ocirc;ng cần phải mua một chiếc tủ đ&ocirc;ng lạnh, kh&ocirc;ng c&ograve;n rủi ro b&aacute;n c&aacute; ươn với gi&aacute; thấp c&oacute; thể g&acirc;y ngộ độc cho kh&aacute;ch h&agrave;ng v&agrave; sẽ giảm cả t&igrave;nh trạng đ&aacute;nh bắt c&aacute; qu&aacute; mức cần thiết.</span></p>\n\n<h3>X&atilde; hội số đem lại lợi &iacute;ch g&igrave; cho người d&acirc;n?</h3>\n\n<p>Chuyển đổi số c&oacute; thể gi&uacute;p x&oacute;a nh&ograve;a khoảng c&aacute;ch địa l&yacute;, mang đến cơ hội b&igrave;nh đẳng cho người d&acirc;n về tiếp cận dịch vụ, mang lại một loạt những tiến bộ lớn về chất lượng cuộc sống. Người d&acirc;n c&oacute; thể sống khỏe mạnh hơn nhờ c&aacute;c h&igrave;nh thức chăm s&oacute;c y tế kịp thời, vui vẻ hơn với c&aacute;c h&igrave;nh thức giải tr&iacute; đa dạng v&agrave; an to&agrave;n hơn.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:14px\">Y tế v&agrave; gi&aacute;o dục</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Một người lao động Việt Nam ở nước ngo&agrave;i c&oacute; thể nhận được những &yacute; kiến tư vấn, chăm s&oacute;c y tế từ xa bởi những b&aacute;c sĩ giỏi nhất ở Bệnh viện Thủ Đức qua ứng dụng VOVBacsi24 bằng tiếng Việt, với gi&aacute; th&agrave;nh dịch vụ rẻ hơn gấp nhiều lần so với sử dụng dịch vụ tư vấn y tế tại nước sở tại. Một học sinh THPT ở H&agrave; Giang c&oacute; thể được học &ocirc;n thi đại học trực tuyến với những thầy gi&aacute;o giỏi nhất ở H&agrave; Nội giống như học sinh THPT ở H&agrave; Nội qua nền tảng Viettel Study hay VnEdu.</span></p>\n</div>\n','[]',0,1,'2022-12-19 15:32:32','2022-12-19 15:32:32',1,'chuyen-doi-so-dem-lai-loi-ich-gi',NULL,NULL),(19,'Chuyển đổi số là việc của ai?',5,'Chuyển đổi số là quá trình thay đổi tổng thể và toàn diện. Với một tổ chức, vì là sự thay đổi, nên trước tiên đó là việc của lãnh đạo, của người đứng đầu, vì nếu không thì không ai dám làm và có thể làm. Vì là tổng thể và toàn diện nên đó là việc của tất cả mọi thành viên trong tổ chức.','<div class=\"article-content link-primary\">\n<h4>Nh&agrave; l&atilde;nh đạo chuyển đổi số l&agrave; ai?</h4>\n\n<p>Nh&agrave; l&atilde;nh đạo chuyển đổi số l&agrave; người đứng đầu tổ chức, c&oacute; tầm nh&igrave;n, thiết lập được sứ mệnh cho tổ chức, c&oacute; niềm tin l&agrave; c&ocirc;ng nghệ số, chuyển đổi số gi&uacute;p giải quyết những vấn đề nhức nhối của tổ chức m&igrave;nh v&agrave; ki&ecirc;n định với mục ti&ecirc;u đặt ra. Nh&agrave; l&atilde;nh đạo chuyển đổi số kh&ocirc;ng nhất thiết v&agrave; kh&ocirc;ng cần phải hiểu về c&ocirc;ng nghệ số. Điều quan trọng nhất đối với nh&agrave; l&atilde;nh đạo l&agrave; biết đặt ra b&agrave;i to&aacute;n. Nh&agrave; l&atilde;nh đạo chuyển đổi số l&agrave; người c&oacute; kh&aacute;t vọng thay đổi, l&agrave; người d&aacute;m chấp nhận c&aacute;i mới v&agrave; d&aacute;m cho c&aacute;i mới một cơ hội.</p>\n\n<h3>Chuy&ecirc;n gia c&ocirc;ng nghệ số l&agrave; ai?</h3>\n\n<p>Chuy&ecirc;n gia c&ocirc;ng nghệ số c&oacute; thể l&agrave; người b&ecirc;n trong hoặc b&ecirc;n ngo&agrave;i của tổ chức. Chuy&ecirc;n gia b&ecirc;n trong của tổ chức l&agrave; người nhận b&agrave;i to&aacute;n từ l&atilde;nh đạo v&agrave; chuyển h&oacute;a th&agrave;nh y&ecirc;u cầu, l&agrave; người ra đầu b&agrave;i th&ocirc;ng th&aacute;i. Chuy&ecirc;n gia b&ecirc;n ngo&agrave;i của tổ chức l&agrave; những người chuy&ecirc;n nghiệp, trong c&aacute;c doanh nghiệp c&ocirc;ng nghệ số, d&ugrave;ng c&ocirc;ng nghệ số để giải quyết b&agrave;i to&aacute;n đặt ra. Tr&ecirc;n thực tế, nhiều người lầm tưởng chuy&ecirc;n gia chỉ n&oacute;i những c&acirc;u chuyện chuy&ecirc;n m&ocirc;n phức tạp, v&igrave; thế, nhiều người tự nhận l&agrave; chuy&ecirc;n gia bằng c&aacute;ch biến c&acirc;u chuyện đơn giản th&agrave;nh c&acirc;u chuyện phức tạp. Nhưng kh&ocirc;ng phải vậy, chuy&ecirc;n gia l&agrave; người c&oacute; khả năng l&agrave;m ngược lại, biến c&acirc;u chuyện phức tạp th&agrave;nh c&acirc;u chuyện đơn giản.</p>\n\n<h3>Người tham gia chuyển đổi số l&agrave; ai?</h3>\n\n<p>Mọi th&agrave;nh vi&ecirc;n trong tổ chức đều tham gia. Nhưng c&oacute; thể ph&acirc;n loại kh&aacute;i qu&aacute;t th&agrave;nh hai loại th&agrave;nh vi&ecirc;n. Một loại tham gia nghi&ecirc;m chỉnh v&agrave; tu&acirc;n thủ theo chỉ đạo, định hướng, quy chế. Một loại tham gia đối ph&oacute; v&agrave; lu&ocirc;n t&igrave;m l&yacute; do để kh&ocirc;ng thay đổi. V&igrave; vậy, nh&agrave; l&atilde;nh đạo chuyển đổi số cần ki&ecirc;n định.</p>\n\n<h2 style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:16px\">H&agrave;nh tr&igrave;nh trong phim T&acirc;y Du K&yacute; về bản chất giống qu&aacute; tr&igrave;nh chuyển đổi số</span></h2>\n\n<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:14px\">Nếu coi qu&aacute; tr&igrave;nh đi T&acirc;y Tr&uacute;c thỉnh kinh l&agrave; một qu&aacute; tr&igrave;nh chuyển đổi số, th&igrave; Đường Tăng l&agrave; một nh&agrave; l&atilde;nh đạo chuyển đổi số xuất sắc. &Ocirc;ng x&aacute;c lập tầm nh&igrave;n đ&uacute;ng, c&oacute; niềm tin tuyệt đối v&agrave; lu&ocirc;n ki&ecirc;n định với mục ti&ecirc;u đ&atilde; đặt ra. &Ocirc;ng c&oacute; quan hệ tốt với nhiều lực lượng v&agrave; lu&ocirc;n được gi&uacute;p đỡ khi gặp kh&oacute; khăn. T&ocirc;n Ngộ Kh&ocirc;ng l&agrave; một chuy&ecirc;n gia c&ocirc;ng nghệ số xuất sắc với 72 ph&eacute;p thần th&ocirc;ng biến h&oacute;a. Sa Tăng l&agrave; th&agrave;nh vi&ecirc;n tu&acirc;n thủ mẫn c&aacute;n, c&ograve;n Trư B&aacute;t Giới l&agrave; th&agrave;nh vi&ecirc;n lu&ocirc;n t&igrave;m c&aacute;ch đối ph&oacute;, muốn kết th&uacute;c chuyến đi để quay lại Cao l&atilde;o trang với vợ.</span></p>\n\n<h3>Chuyển đổi số c&oacute; phải l&agrave; chuyện ri&ecirc;ng của c&aacute;c doanh nghiệp c&ocirc;ng nghệ số?</h3>\n\n<p>Kh&ocirc;ng phải. Chuyển đổi số l&agrave; c&acirc;u chuyện chung của tất cả c&aacute;c doanh nghiệp, kh&ocirc;ng ph&acirc;n biệt ng&agrave;nh, nghề. Chuyển đổi số l&agrave; s&acirc;n chơi của tất cả doanh nghiệp m&agrave; ở đ&oacute; bất kỳ ai đủ nhanh nhạy đều c&oacute; thể nắm được thời cơ. Doanh nghiệp c&ocirc;ng nghệ số chỉ l&agrave; một phần nhỏ, l&agrave; những người tạo ra c&ocirc;ng nghệ số hoặc tư vấn ứng dụng c&ocirc;ng nghệ số. Những doanh nghiệp kh&aacute;c d&ugrave;ng c&ocirc;ng nghệ số để thực hiện chuyển đổi số mới l&agrave; phần lớn v&agrave; l&agrave; phần quan trọng nhất.</p>\n\n<h3>C&oacute; những loại h&igrave;nh doanh nghiệp c&ocirc;ng nghệ số n&agrave;o?</h3>\n\n<p>Chương tr&igrave;nh Chuyển đổi số quốc gia x&aacute;c định tập trung th&uacute;c đẩy ph&aacute;t triển bốn loại h&igrave;nh doanh nghiệp c&ocirc;ng nghệ số.</p>\n\n<p>C&aacute;c tập đo&agrave;n thương mại, dịch vụ lớn trong c&aacute;c lĩnh vực kinh tế - x&atilde; hội chuyển hướng hoạt động sang lĩnh vực c&ocirc;ng nghệ số, đầu tư nghi&ecirc;n cứu c&ocirc;ng nghệ l&otilde;i.</p>\n\n<p>C&aacute;c doanh nghiệp c&ocirc;ng nghệ th&ocirc;ng tin đ&atilde; khẳng định được thương hiệu đảm nhận c&aacute;c sứ mệnh ti&ecirc;n phong nghi&ecirc;n cứu, ph&aacute;t triển, l&agrave;m chủ c&ocirc;ng nghệ số v&agrave; chủ động sản xuất.</p>\n\n<p>C&aacute;c doanh nghiệp tư vấn ứng dụng c&ocirc;ng nghệ số để tạo ra c&aacute;c sản phẩm, dịch vụ mới trong c&aacute;c ng&agrave;nh, lĩnh vực kinh tế - x&atilde; hội.</p>\n\n<p>C&aacute;c doanh nghiệp khởi nghiệp đổi mới, s&aacute;ng tạo về c&ocirc;ng nghệ số.</p>\n\n<h3>Tại sao n&oacute;i chuyển đổi số l&agrave; cuộc c&aacute;ch mạng to&agrave;n d&acirc;n?</h3>\n\n<p>Trong bối cảnh C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư, c&ocirc;ng nghệ số kh&ocirc;ng phải vấn đề quan trọng nhất, m&agrave; sự v&agrave;o cuộc của cả hệ thống ch&iacute;nh trị, h&agrave;nh động đồng bộ ở c&aacute;c cấp v&agrave; sự tham gia của to&agrave;n d&acirc;n mới l&agrave; yếu tố đảm bảo sự th&agrave;nh c&ocirc;ng của chuyển đổi số, thay đổi thứ hạng quốc gia.</p>\n\n<p>X&eacute;t cho c&ugrave;ng, chuyển đổi số l&agrave; phục vụ người d&acirc;n. Mỗi người d&acirc;n trở th&agrave;nh c&ocirc;ng d&acirc;n số th&igrave; chuyển đổi số mới th&agrave;nh c&ocirc;ng. Do vậy, chuyển đổi số mang &yacute; nghĩa của một cuộc c&aacute;ch mạng to&agrave;n d&acirc;n.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:16px\">Khi nh&acirc;n d&acirc;n đứng dậy, họ sẽ t&igrave;m ra vũ kh&iacute;</span></h2>\n\n<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:14px\">Năm 1940, khi chuẩn bị rời Quế L&acirc;m trở về Việt Nam đấu tranh, nhiều đồng ch&iacute; đ&atilde; lo ngại lực lượng của ta c&ograve;n qu&aacute; yếu, kh&ocirc;ng c&oacute; vũ kh&iacute;, về b&acirc;y giờ l&agrave;m sao c&oacute; thể kh&aacute;ng chiến, L&atilde;nh tụ Hồ Ch&iacute; Minh đ&atilde; trả lời v&agrave; để lại cho thế hệ sau một b&agrave;i học qu&yacute; b&aacute;u: Vũ kh&iacute; kh&ocirc;ng phải vấn đề quan trọng nhất của c&aacute;ch mạng. Nếu ch&uacute;ng ta c&oacute; vũ kh&iacute; b&acirc;y giờ, ai sẽ l&agrave; người cầm vũ kh&iacute;. V&igrave; vậy, ch&uacute;ng ta phải về nước v&agrave; động vi&ecirc;n quần ch&uacute;ng. Khi nh&acirc;n d&acirc;n đứng dậy, họ sẽ t&igrave;m ra vũ kh&iacute;.</span></p>\n\n<p style=\"text-align:justify\">Khi to&agrave;n d&acirc;n c&ugrave;ng tham gia, họ sẽ t&igrave;m ra c&ocirc;ng nghệ số ph&ugrave; hợp, sẽ t&igrave;m ra c&aacute;ch giải ph&ugrave; hợp v&agrave; v&igrave; thế chuyển đổi số sẽ th&agrave;nh c&ocirc;ng.</p>\n</div>\n','[]',1,1,'2022-12-19 15:32:41','2022-12-19 15:32:41',1,'chuyen-doi-so-la-viec-cua-ai',NULL,NULL),(20,'Chuyển đổi số những gì?',5,'Chuyển đổi số là quá trình thay đổi tổng thể và toàn diện. Chuyển đổi số chỉ thành công khi trở thành chiến lược cốt lõi, thay vì là nỗ lực riêng biệt, chuyển đổi số phải bao trùm lên mọi hoạt động, mọi bước đi của tổ chức.','<div class=\"article-content link-primary\">\n<h4>Việc chọn c&aacute;i g&igrave; để chuyển đổi trước th&igrave; c&oacute; thể theo thứ tự sau: C&aacute;i n&agrave;o bắt buộc phải l&agrave;m, kh&ocirc;ng hối tiếc khi chuyển đổi th&igrave; l&agrave;m trước. V&iacute; dụ, c&aacute;i bắt buộc phải l&agrave;m như l&agrave; học trực tuyến thời gi&atilde;n c&aacute;ch x&atilde; hội, c&aacute;i kh&ocirc;ng hối tiếc l&agrave; c&aacute;i đang kh&oacute; khăn nhất của tổ chức m&agrave; chưa c&oacute; c&aacute;ch giải quyết.</h4>\n\n<p>Tiếp đến l&agrave; c&aacute;i n&agrave;o l&ecirc;n m&ocirc;i trường số th&igrave; hiệu quả vượt trội, v&iacute; dụ, cơ quan nh&agrave; nước lựa chọn chuyển đổi số trong việc cung cấp dịch vụ c&ocirc;ng trực tuyến trước.</p>\n\n<p>V&agrave; sau đến l&agrave; c&aacute;i n&agrave;o l&ecirc;n m&ocirc;i trường số th&igrave; c&oacute; thể tạo ra c&aacute;c dịch vụ, gi&aacute; trị mới, v&iacute; dụ s&agrave;n giao dịch thương mại điện tử kh&ocirc;ng chỉ b&aacute;n nải chuối như trước đ&acirc;y m&agrave; c&ograve;n c&oacute; thể b&aacute;n trước cả c&acirc;y chuối trong vườn của người n&ocirc;ng d&acirc;n bằng c&aacute;ch gắn cảm biến IoT, cho ph&eacute;p người mua lựa chọn c&acirc;y từ khi c&ograve;n b&eacute;, theo d&otilde;i qu&aacute; tr&igrave;nh chăm s&oacute;c, đến l&uacute;c thu hoạch quả.</p>\n\n<p>N&oacute;i c&aacute;ch kh&aacute;c, chọn việc g&igrave; để l&agrave;m trước th&igrave; chọn c&aacute;i g&igrave; thấy kh&oacute; nhất, hay gọi l&agrave; &ldquo;nỗi đau&rdquo; lớn nhất, vấn đề g&igrave; tồn tại l&acirc;u rồi, c&ograve;n gọi l&agrave; b&agrave;i to&aacute;n thi&ecirc;n ni&ecirc;n kỷ, hoặc vấn đề g&igrave; đang n&oacute;ng nhất, người d&acirc;n đang bức x&uacute;c nhất th&igrave; mang ra để xem c&ocirc;ng nghệ số c&oacute; giải quyết được kh&ocirc;ng.</p>\n\n<h3>Trước ti&ecirc;n tiến h&agrave;nh c&aacute;c thử nghiệm quy m&ocirc; nhỏ c&oacute; được kh&ocirc;ng?</h3>\n\n<p>Một số doanh nghiệp lựa chọn chuyển đổi số bằng việc tiến h&agrave;nh c&aacute;c thử nghiệm quy m&ocirc; nhỏ mang t&iacute;nh ngắn hạn trước, theo c&aacute;ch từ dưới l&ecirc;n. C&aacute;c thử nghiệm th&agrave;nh c&ocirc;ng c&oacute; thể gi&uacute;p khẳng định c&aacute;c &yacute; tưởng mới, kh&aacute;m ph&aacute; c&aacute;c xu hướng tương lai, thay đổi nhận thức.</p>\n\n<p>Tuy nhi&ecirc;n, l&agrave;m nhiều thử nghiệm m&agrave; kh&ocirc;ng c&oacute; chiến lược v&agrave; phương hướng sẽ tạo ảo tưởng th&agrave;nh c&ocirc;ng trong ngắn hạn m&agrave; kh&ocirc;ng tạo được thay đổi l&acirc;u d&agrave;i. C&aacute;c &yacute; tưởng nở rộ, thử nghiệm tr&ecirc;n quy m&ocirc; nhỏ, nhưng kh&ocirc;ng được hiện thực h&oacute;a tr&ecirc;n quy m&ocirc; lớn sẽ g&acirc;y l&atilde;ng ph&iacute; thời gian v&agrave; nguồn lực.</p>\n\n<h3>Trước ti&ecirc;n ứng dụng c&ocirc;ng nghệ số để giảm chi ph&iacute; c&oacute; được kh&ocirc;ng?</h3>\n\n<p>Tăng năng suất v&agrave; giảm chi ph&iacute; n&ecirc;n l&agrave; c&aacute;c mục ti&ecirc;u trong mọi tổ chức, doanh nghiệp. Nhưng nếu chỉ hướng đến mục ti&ecirc;u đ&oacute;, nghĩa l&agrave; đang chấp nhận sẽ kh&ocirc;ng để c&ocirc;ng nghệ thay đổi tận gốc tổ chức, doanh nghiệp m&igrave;nh. Chẳng hạn, nếu chấp nhận như vậy th&igrave; c&aacute;c h&atilde;ng taxi truyền thống vẫn n&ecirc;n hoạt động như cũ v&agrave; sẽ kh&ocirc;ng bị ảnh hưởng nhiều từ c&aacute;c h&atilde;ng cung cấp nền tảng như Grab hay Uber. Tuy nhi&ecirc;n, thực tế lại kh&ocirc;ng phải như vậy.</p>\n\n<h3>L&agrave;m nhanh, l&agrave;m nhiều l&agrave; tốt?</h3>\n\n<p>L&agrave;m nhanh, l&agrave;m nhiều kh&ocirc;ng đồng nghĩa với hiệu quả. Nh&agrave; l&atilde;nh đạo chuyển đổi số cần một c&aacute;i đầu lạnh để b&igrave;nh tĩnh t&igrave;m ra những kh&oacute; khăn của tổ chức, thấu hiểu m&ocirc; h&igrave;nh hoạt động/kinh doanh, bối cảnh cũng như t&acirc;m l&yacute; người d&acirc;n/kh&aacute;ch h&agrave;ng khi c&acirc;n nhắc thực hiện chuyển đổi số.</p>\n\n<p>Chuyển đổi số c&oacute; thể bắt đầu từ những việc nhỏ, nhưng tư duy định hướng phải l&agrave; tư duy đổi mới tổng thể v&agrave; to&agrave;n diện.</p>\n</div>\n','[]',0,1,'2022-12-19 15:32:52','2022-12-19 15:32:52',1,'chuyen-doi-so-nhung-gi',NULL,NULL),(21,'Chuyển đổi số tốn bao nhiêu tiền?',5,'Chuyển đổi số không phải là một khoản chi phí, mà chuyển đổi số là giải pháp để tối ưu hóa chi phí và tạo ra những giá trị mới. Chuyển đổi số không phải là mua sắm công nghệ số mới nhất, hiện đại nhất.','<div class=\"article-content link-primary\">\n<h4>Bởi vậy, c&acirc;u hỏi đ&uacute;ng ở đ&acirc;y l&agrave; &ldquo;Chuyển đổi số tạo ra bao nhi&ecirc;u tiền?&rdquo;, thay v&igrave; &ldquo;Chuyển đổi số tốn bao nhi&ecirc;u tiền?&rdquo;.</h4>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:14px\">Bảo tr&igrave; ti&ecirc;n đo&aacute;n trong ng&agrave;nh Điện lực</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Engie l&agrave; một c&ocirc;ng ty điện lực của Ph&aacute;p. Engie thực hiện chuyển đổi số v&agrave;o c&ocirc;ng t&aacute;c bảo tr&igrave;, chuyển từ bảo tr&igrave; theo c&aacute;ch truyền thống sang bảo tr&igrave; ti&ecirc;n đo&aacute;n. Theo c&aacute;ch truyền thống, bảo tr&igrave; thực hiện định kỳ hoặc khi c&oacute; hỏng h&oacute;c. Bảo tr&igrave; ti&ecirc;n đo&aacute;n l&agrave; dự b&aacute;o trước hỏng h&oacute;c, từ đ&oacute;, tối ưu h&oacute;a kế hoạch bảo tr&igrave;. Engie c&oacute; 1,2 triệu ki-l&ocirc;-m&eacute;t đường d&acirc;y, gồm nhiều loại t&agrave;i sản như trạm điện, đường d&acirc;y, m&aacute;y biến &aacute;p, c&ocirc;ng tơ, cảm biến..., dữ liệu thu thập về theo thời gian thực h&agrave;ng petabyte (1 triệu GB) mỗi ng&agrave;y, kết hợp với cả c&aacute;c dữ liệu li&ecirc;n quan kh&aacute;c, như thời tiết. Nền tảng ph&acirc;n t&iacute;ch dữ liệu lớn bảo tr&igrave; ti&ecirc;n đo&aacute;n sử dụng tr&iacute; tuệ nh&acirc;n tạo với hơn 1000 m&ocirc; h&igrave;nh m&aacute;y học, cho ph&eacute;p cung cấp 140.000 lượt đo&aacute;n mỗi ng&agrave;y, chu kỳ 10 ph&uacute;t/lần, gi&uacute;p tiết kiệm chi ph&iacute; hơn 100 triệu EUR/năm. Đặc biệt, tr&iacute; tuệ nh&acirc;n tạo cho ph&eacute;p tận dụng v&agrave; m&ocirc; h&igrave;nh h&oacute;a được tri thức t&iacute;ch lũy hơn 30 năm của một số chuy&ecirc;n gia l&agrave;nh nghề. Hiệu quả c&oacute; thể giảm đến 30% chi ph&iacute; bảo tr&igrave; v&agrave; 75% thời gian gi&aacute;n đoạn dịch vụ.</span></p>\n\n<h3>Chuyển đổi số gi&uacute;p tạo ra th&ecirc;m bao nhi&ecirc;u doanh thu?</h3>\n\n<p>Với doanh nghiệp, doanh thu v&agrave; lợi nhuận lu&ocirc;n nằm trong số những vấn đề cần quan t&acirc;m. Chuyển đổi số chỉ th&agrave;nh c&ocirc;ng nếu tạo ra th&ecirc;m doanh thu, tạo ra th&ecirc;m lợi nhuận cho doanh nghiệp.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:14px\">Chăm s&oacute;c kh&aacute;ch h&agrave;ng</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Viettel l&agrave; một tập đo&agrave;n c&ocirc;ng nghiệp - viễn th&ocirc;ng của Việt Nam. Trong nửa đầu năm 2020, do diễn biến phức tạp của COVID-19, Viettel đẩy mạnh tương t&aacute;c kh&aacute;ch h&agrave;ng tr&ecirc;n k&ecirc;nh số thay cho k&ecirc;nh truyền thống. C&aacute;c dịch vụ mới c&oacute; tỷ lệ tương t&aacute;c với kh&aacute;ch h&agrave;ng tr&ecirc;n 90% tr&ecirc;n k&ecirc;nh số. Nhờ đ&atilde; chuyển đổi số hoạt động chăm s&oacute;c kh&aacute;ch h&agrave;ng, tỷ lệ h&agrave;i l&ograve;ng của kh&aacute;ch h&agrave;ng đối với dịch vụ viễn th&ocirc;ng đ&atilde; tăng từ 87,92% (năm 2019) l&ecirc;n 90,02% (năm 2020). Viettel chủ động chuyển tương t&aacute;c kh&aacute;ch h&agrave;ng l&ecirc;n hệ thống Chatbot My Viettel tới gần 25 ngh&igrave;n lượt/ng&agrave;y (đạt tỷ lệ 95%). B&ecirc;n cạnh đ&oacute;, Viettel cũng tập trung ph&aacute;t triển đối t&aacute;c trực tuyến phục vụ kh&aacute;ch h&agrave;ng mua sắm tại nh&agrave;. Kết quả doanh thu 6 th&aacute;ng đầu năm của Viettel đạt 120.000 tỷ đồng, tăng hơn 10.000 tỷ so với c&ugrave;ng kỳ năm 2019.</span></p>\n\n<h3>Chi cho chuyển đổi số thế n&agrave;o cho ph&ugrave; hợp?</h3>\n\n<p>Mức trung b&igrave;nh của thế giới v&agrave;o khoảng 2-3%. Ở một số nước như Singapore, tỷ lệ n&agrave;y c&oacute; thể l&ecirc;n đến 4-5%. Mức trung b&igrave;nh của Việt Nam hiện nay đang l&agrave; 0,3%.</p>\n\n<p>Mỗi cơ quan, tổ chức căn cứ v&agrave;o chiến lược chuyển đổi số của m&igrave;nh để c&oacute; mức chi ph&ugrave; hợp. Đối với c&aacute;c cơ quan nh&agrave; nước cấp bộ, cấp tỉnh, tỷ lệ chi cho chuyển đổi số n&ecirc;n ở mức 1% tổng chi ng&acirc;n s&aacute;ch nh&agrave; nước h&agrave;ng năm. Nếu muốn đẩy nhanh, nhất l&agrave; những năm đầu cần l&agrave;m ngay c&aacute;c nền tảng, th&igrave; c&oacute; thể chi 2%.</p>\n\n<p>Chuyển đổi số l&agrave; chuyển hoạt động từ m&ocirc;i trường thực sang m&ocirc;i trường số. Bảo đảm an to&agrave;n, an ninh mạng l&agrave; điều kiện ti&ecirc;n quyết để chuyển đổi số. Mỗi tổ chức, doanh nghiệp cần d&agrave;nh &iacute;t nhất 10% mức chi cho chuyển đổi số để chi cho c&aacute;c hoạt động bảo đảm an to&agrave;n, an ninh mạng.</p>\n\n<h3>C&oacute; c&aacute;ch n&agrave;o để biết chi cho chuyển đổi số c&oacute; hiệu quả hay kh&ocirc;ng?</h3>\n\n<p>Khi chuyển đổi số th&igrave; phải t&iacute;nh được gi&aacute; trị do chuyển đổi số mang lại, th&iacute; dụ tiết kiệm lao động, giờ lao động, tiết kiệm chi ph&iacute; thường xuy&ecirc;n, gi&aacute; trị mới do chuyển đổi số mang lại. Những gi&aacute; trị v&ocirc; h&igrave;nh, d&agrave;i hạn th&igrave; vẫn phải t&igrave;m c&aacute;ch lượng h&oacute;a. Gi&aacute; trị tạo ra m&agrave; lớn hơn chi ph&iacute; chuyển đổi số tức l&agrave; hiệu quả. Chuyển đổi số kh&ocirc;ng phải l&agrave; một chi ph&iacute; tăng th&ecirc;m m&agrave; phải l&agrave; một gi&aacute; trị tăng th&ecirc;m. Để tr&aacute;nh c&aacute;c tai nạn đ&atilde; từng xảy ra trước đ&acirc;y với c&ocirc;ng nghệ th&ocirc;ng tin th&igrave; lu&ocirc;n phải coi chi cho chuyển đổi số như một dự &aacute;n đầu tư, phải lu&ocirc;n t&iacute;nh thu, chi. Dự &aacute;n đầu tư phải dương th&igrave; mới đầu tư, mới cho l&agrave;m. Cũng c&oacute; một c&aacute;ch l&agrave;m mới l&agrave; đặt ra b&agrave;i to&aacute;n để doanh nghiệp t&igrave;m lời giải.</p>\n\n<h2 style=\"margin-left:40px\"><span style=\"font-size:14px\">Du lịch v&agrave; lưu tr&uacute;</span></h2>\n\n<p style=\"margin-left:40px\"><span style=\"font-size:14px\">Tỉnh B&agrave; Rịa - Vũng T&agrave;u mỗi năm c&oacute; 16 triệu kh&aacute;ch du lịch, trung b&igrave;nh mỗi người ở lại đ&acirc;y 1,1 ng&agrave;y, rất ngắn. Nếu c&oacute; một doanh nghiệp c&oacute; c&aacute;ch để kh&aacute;ch du lịch ở lại th&ecirc;m 0,5 ng&agrave;y, tức l&agrave; tạo th&ecirc;m 8 triệu ng&agrave;y kh&aacute;ch du lịch ở lại Vũng T&agrave;u. Nếu gi&aacute; trị tăng th&ecirc;m mỗi người/ng&agrave;y l&agrave; 500.000 đồng, th&igrave; gi&aacute; trị tăng th&ecirc;m mỗi năm l&agrave; 4.000 tỷ đồng, tỉnh c&oacute; thể trả cho doanh nghiệp 1% l&agrave; 40 tỷ đồng/năm, hoặc 5% l&agrave; 200 tỷ đồng/năm.</span></p>\n\n<p>B&agrave;i to&aacute;n n&agrave;y đặt ra c&ocirc;ng khai th&igrave; chắc chắn sẽ c&oacute; doanh nghiệp l&agrave;m được. Tương tự như vậy, tỉnh c&oacute; thể đặt ra c&aacute;c b&agrave;i to&aacute;n kh&aacute;c tr&ecirc;n tinh thần n&agrave;y. Với c&aacute;ch l&agrave;m n&agrave;y th&igrave; tỉnh c&oacute; thể ph&aacute;t triển vượt bậc m&agrave; cũng tạo ra sự ph&aacute;t triển vượt bậc cho doanh nghiệp. Hai b&ecirc;n c&ugrave;ng thắng. M&agrave; l&agrave; thắng lớn. Bởi vậy m&agrave; thời chuyển đổi số c&oacute; một c&ocirc;ng thức th&agrave;nh c&ocirc;ng l&agrave;: việc g&igrave; m&agrave; m&igrave;nh thấy kh&oacute;, l&agrave;m m&atilde;i kh&ocirc;ng được th&igrave; c&ocirc;ng khai b&agrave;i to&aacute;n, vấn đề của m&igrave;nh ra cho x&atilde; hội l&agrave;m. Ở ngo&agrave;i kia sẽ c&oacute; ai đấy giải được.</p>\n\n<p style=\"margin-left:40px\">&nbsp;</p>\n</div>\n','[]',0,1,'2022-12-19 15:33:02','2022-12-19 15:33:02',1,'chuyen-doi-so-ton-bao-nhieu-tien',NULL,NULL),(22,'Hơn 90% doanh nghiệp Việt xem nhân viên là tài sản giá trị nhất khi chuyển đổi số',1,'Hơn 90% doanh nghiệp Việt xem nhân viên là tài sản giá trị nhất khi chuyển đổi số','Du lịch tp HHCM','[{\"file\":\"/images/20221219/vnnic.jpg\",\"extension\":\"jpg\"}]',3,1,'2022-12-19 15:10:31','2022-12-22 12:14:13',1,'hon-90-doanh-nghiep-viet-xem-nhan-vien-la-tai-san-gia-tri-nhat-khi-chuyen-doi-so-21','abc','dan tri'),(23,'Xây dựng vùng chuyên canh trồng lúa chất lượng cao 1 triệu ha.',3,'Ngành lúa gạo phát triển mạnh mẽ không chỉ đảm bảo vững chắc an ninh lương thực trong nước mà còn góp phần nâng cao, khẳng định vị thế chính trị của Việt Nam trên trường quốc tế.','<h1><em><a href=\"https://thanhnien.vn/trong-lua-chay-theo-san-luong-khong-dong-nghia-giup-nong-dan-tang-thu-nhap-post1522702.html\" title=\"Trồng lúa chạy theo ‘sản lượng’ không đồng nghĩa giúp nông dân tăng thu nhập\">Ng&agrave;nh l&uacute;a gạo</a>&nbsp;ph&aacute;t triển mạnh mẽ kh&ocirc;ng chỉ đảm bảo vững chắc an ninh lương thực trong nước m&agrave; c&ograve;n g&oacute;p phần n&acirc;ng cao, khẳng định vị thế ch&iacute;nh trị của Việt Nam tr&ecirc;n trường quốc tế.</em></h1>\n\n<p><em>&nbsp;</em></p>\n\n<p><em>&nbsp;</em></p>\n\n<p>Đ&oacute; l&agrave; khẳng định từ b&agrave; Trần Thị H&ograve;a, Ph&oacute; cục trưởng&nbsp;<a href=\"https://thanhnien.vn/chua-co-khieu-kien-ve-cuoc-thi-gao-ngon-viet-nam-post1520274.html\" title=\"Chưa có khiếu kiện về cuộc thi Gạo ngon Việt Nam\">Cục Trồng trọt&nbsp;</a>(Bộ NN-PTNT) khi chia sẻ tại hội thảo: Ph&aacute;t triển ĐBSCL - giải ph&aacute;p từ c&acirc;y l&uacute;a&quot; do B&aacute;o&nbsp;<em>Thanh Ni&ecirc;n</em>&nbsp;phối hợp UBND tỉnh&nbsp;<a href=\"https://thanhnien.vn/dong-thap/\" rel=\"noopener noreferrer\" target=\"_blank\" title=\"Đồng Tháp\">Đồng Th&aacute;p</a>&nbsp;diễn ra s&aacute;ng nay 18.11, do&nbsp;<a href=\"https://thanhnien.vn/trong-lua-chay-theo-san-luong-khong-dong-nghia-giup-nong-dan-tang-thu-nhap-post1522702.html\" title=\"Trồng lúa chạy theo ‘sản lượng’ không đồng nghĩa giúp nông dân tăng thu nhập\">B&iacute; thư Tỉnh ủy Đồng Th&aacute;p L&ecirc; Quốc Phong</a>&nbsp;v&agrave; Nh&agrave; b&aacute;o, Tổng bi&ecirc;n tập B&aacute;o&nbsp;<em>Thanh Ni&ecirc;n</em>&nbsp;Nguyễn Ngọc To&agrave;n chủ tr&igrave;.</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Xây dựng vùng chuyên canh trồng lúa chất lượng cao 1 triệu ha - ảnh 1\" src=\"https://image.thanhnien.vn/w2048/Uploaded/2022/wohuohb/2022_11_18/hoa-9240.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>B&agrave; Trần Thị H&ograve;a, Ph&oacute; cục trưởng Cục Trồng trọt (Bộ NNN-PTNT) chia sẻ về Đề &aacute;n 1 triệu ha l&uacute;a chất lượng cao tại hội thảo</p>\n\n			<p>ĐỘC LẬP</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Xuất khẩu gạo v&agrave;o Mỹ, EU tăng mạnh</p>\n\n<p>Điểm lại lịch sử&nbsp;<a href=\"https://thanhnien.vn/trong-lua-chay-theo-san-luong-khong-dong-nghia-giup-nong-dan-tang-thu-nhap-post1522702.html\" title=\"Trồng lúa chạy theo ‘sản lượng’ không đồng nghĩa giúp nông dân tăng thu nhập\">ng&agrave;nh l&uacute;a gạo</a>, b&agrave; Trần Thị H&ograve;a cho rằng, nếu năm 1989 Việt Nam lần đầu ti&ecirc;n xuất khẩu gạo, chất dứt thời kỳ thiếu gạo v&agrave; chuyển sang xuất khẩu nhưng hơn 30 năm sau (1989 - 2021), hạt gạo Việt Nam đ&atilde; c&oacute; mặt ở hơn 172 nước v&agrave; v&ugrave;ng l&atilde;nh thổ. Việt Nam đ&atilde; trở th&agrave;nh 1/3 quốc gia xuất khẩu gạo lớn tr&ecirc;n&nbsp;<a href=\"https://thanhnien.vn/the-gioi/\" rel=\"noopener noreferrer\" target=\"_blank\" title=\"thế giới\">thế giới</a>. Sản lượng tăng từ 1,4 triệu tấn năm 1989 l&ecirc;n 6,24 triệu tấn năm 2021. Gi&aacute; trị tăng từ 321,8 triệu USD năm 1989 l&ecirc;n 3,28 tỉ USD năm 2021.</p>\n\n<p>B&agrave; H&ograve;a cũng th&ocirc;ng tin, 10 th&aacute;ng đầu năm nay, xuất khẩu gạo của Việt Nam ước đạt 6 triệu tấn, đạt khoảng 93 - 96% kế hoạch xuất khẩu đề ra. Dự b&aacute;o xuất khẩu gạo Việt Nam năm nay c&oacute; thể vượt kế hoạch 6,3 - 6,5 triệu tấn, cao hơn 100.000 - 200.000 tấn so với năm 2021.</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Xây dựng vùng chuyên canh trồng lúa chất lượng cao 1 triệu ha - ảnh 2\" src=\"https://image.thanhnien.vn/w2048/Uploaded/2022/wohuohb/2022_11_18/anh-chup-man-hinh-2022-11-18-luc-100305-9303.png\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Rất nhiều &yacute; kiến t&acirc;m huyết, &yacute; tưởng ph&aacute;t triển ng&agrave;nh h&agrave;ng l&uacute;a gạo ở ĐBSCL được chia sẻ tại hội thảo</p>\n\n			<p>Đ&Ocirc;C LẬP</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Đặc biệt, xuất khẩu gạo đang c&oacute; sự chuyển dịch từ ph&acirc;n kh&uacute;c thấp sang&nbsp;<a href=\"https://thanhnien.vn/vua-gao-st25-dang-lam-tuong-trinh-ve-cuoc-thi-gao-ngon-viet-nam-2022-post1518559.html\" title=\"‘Vua gạo ST25’ đang \'làm tường trình\' về cuộc thi Gạo ngon Việt Nam 2022\">gạo chất lượng cao</a>. Khi thống k&ecirc; trong 8 th&aacute;ng đầu năm nay, xuất khẩu gạo sang c&aacute;c thị trường kh&oacute; t&iacute;nh ghi nhận mức tăng trưởng mạnh như Mỹ 8 tăng 84,8%; thị trường EU tăng 82,2%.</p>\n\n<p>Theo đ&oacute;, b&agrave; Trần Thị H&ograve;a khẳng định, sự ph&aacute;t triển mạnh mẽ của ng&agrave;nh h&agrave;ng l&uacute;a gạo kh&ocirc;ng chỉ đảm bảo vững chắc an ninh lương thực, x&oacute;a đ&oacute;i giảm ngh&egrave;o, ổn định kinh tế x&atilde; hội đưa<a href=\"https://thanhnien.vn/lam-the-nao-de-nang-cao-thu-nhap-nguoi-trong-lua-dbscl-post1522689.html\" title=\"Làm thế nào để nâng cao thu nhập người trồng lúa ĐBSCL?\">&nbsp;l&uacute;a gạo</a>&nbsp;trở th&agrave;nh một n&ocirc;ng sản xuất khẩu chủ lực của Việt Nam m&agrave; c&ograve;n g&oacute;p phần ph&aacute;t triển hợp t&aacute;c quốc tế, khẳng định vị thế ch&iacute;nh trị của Việt Nam tr&ecirc;n thế giới.</p>\n\n<p>1 triệu ha l&uacute;a chất lượng cao gi&uacute;p n&ocirc;ng d&acirc;n tăng thu nhập</p>\n\n<p>Đại diện l&atilde;nh đạo Cục Trồng trọt cũng cho rằng,&nbsp;<a href=\"https://thanhnien.vn/gao-huong-loi-lua-duoc-mua-trung-gia-post1519725.html\" title=\"Gạo hưởng lợi, lúa được mùa trúng giá\">sản xuất l&uacute;a gạo</a>&nbsp;đang đứng trước nhiều cơ hội như nhu cầu nhập khẩu gạo thế giới vẫn c&ograve;n tiếp tục tăng l&ecirc;n trong những năm tới. Ng&agrave;nh l&uacute;a gạo c&oacute; cơ hội rất lớn để mở rộng thị trường xuất khẩu khi Việt Nam tham gia nhiều hiệp định thương mại mới như TPP, li&ecirc;n minh thuế quan&hellip;</p>\n\n<p>Nhưng theo b&agrave; H&ograve;a, cũng phải thẳng thắn nh&igrave;n nhận, sản xuất l&uacute;a gạo ĐBSCL vẫn c&ograve;n nhiều tồn tại v&agrave; hạn chế như chất lượng, hiệu quả, gi&aacute; trị gia tăng v&agrave; t&iacute;nh cạnh tranh thấp; thu nhập của n&ocirc;ng d&acirc;n sản xuất l&uacute;a thấp v&agrave; kh&ocirc;ng tương xứng so với thu nhập của t&aacute;c nh&acirc;n trong&nbsp;<a href=\"https://thanhnien.vn/tai-chinh-kinh-doanh/\" rel=\"noopener noreferrer\" target=\"_blank\" title=\"kinh doanh\">kinh doanh</a>, xuất khẩu gạo. Sản xuất l&uacute;a thiếu t&iacute;nh bền vững, t&aacute;c động ti&ecirc;u cực đến m&ocirc;i trường.</p>\n\n<p>B&agrave; Trần Thị H&ograve;a cho rằng, ĐBSCL c&oacute; nhiều lợi thế cạnh tranh trong sản xuất n&ocirc;ng nghiệp v&agrave; ng&agrave;nh l&uacute;a gạo sẽ vẫn giữ vị tr&iacute; quan trọng trong ng&agrave;nh trồng trọt của Việt Nam. Nhưng ng&agrave;nh l&uacute;a gạo cần được thay đổi để từ vai tr&ograve; l&agrave; một ng&agrave;nh sản xuất v&igrave; mục ti&ecirc;u an ninh lương thực l&agrave; chủ yếu trở th&agrave;nh một ng&agrave;nh kinh tế năng động v&agrave; hiệu quả, đem lại lợi &iacute;ch cho người sản xuất v&agrave;&nbsp;<a href=\"https://thanhnien.vn/tai-chinh-kinh-doanh/tieu-dung/\" rel=\"noopener noreferrer\" target=\"_blank\" title=\"người tiêu dùng\">người ti&ecirc;u d&ugrave;ng</a>&nbsp;trong nước v&agrave; c&oacute; t&iacute;nh cạnh tranh cao trong xuất khẩu.</p>\n\n<p>B&agrave; H&ograve;a cũng chia sẻ, từ Nghị quyết số 78/NQ-CP ng&agrave;y 18.6.2022 của Ch&iacute;nh phủ ban h&agrave;nh chương tr&igrave;nh h&agrave;nh độn của ch&iacute;nh phủ thực hiện Nghị quyết số 13-NQ/TW ng&agrave;y 2.4.2022 của Bộ Ch&iacute;nh trị về phương hướng&nbsp;<a href=\"https://thanhnien.vn/kinh-doanh/chinh-sach-phat-trien/\" rel=\"noopener noreferrer\" target=\"_blank\" title=\"phát triển kinh tế\">ph&aacute;t triển kinh tế</a>&nbsp;- x&atilde; hội v&agrave; bảo đảm quốc ph&ograve;ng, an ninh v&ugrave;ng ĐBSCL đến năm 2030, tầm nh&igrave;n đến năm 2045. Bộ NN-PTNT đ&atilde; giao Cục Trồng trọt x&acirc;y dựng:&nbsp;<a href=\"https://thanhnien.vn/xay-dung-nen-nong-nghiep-thich-ung-voi-bien-doi-khi-hau-post1521689.html\" title=\"Xây dựng nền nông nghiệp thích ứng với biến đổi khí hậu\">Đề &aacute;n sản xuất bền vững 1 triệu ha chuy&ecirc;n canh l&uacute;a&nbsp;</a>chất lượng cao v&ugrave;ng ĐBSCL nhằm n&acirc;ng cao gi&aacute; trị, thu nhập của người d&acirc;n, bảo đảm an ninh lương thực v&agrave; phục vụ chế biến, xuất khẩu.</p>\n\n<p>Đề &aacute;n tập trung v&agrave;o 2 nội dung ch&iacute;nh gồm đ&aacute;nh gi&aacute; hiện trạng sản xuất l&uacute;a v&ugrave;ng Đồng bằng s&ocirc;ng Cửu Long với vai tr&ograve;, kết quả sản xuất, chế biến v&agrave; ti&ecirc;u thụ l&uacute;a gạo của v&ugrave;ng. Đ&aacute;nh gi&aacute; kết quả v&agrave; hiệu quả chuyển đổi c&aacute;c m&ocirc; h&igrave;nh sản xuất tr&ecirc;n đất trồng l&uacute;a v&ugrave;ng theo v&ugrave;ng sinh th&aacute;i nguồn nước; hiệu quả kinh tế chuỗi gi&aacute; trị ng&agrave;nh h&agrave;ng l&uacute;a gạo v&ugrave;ng (sản xuất - thu mua - chế biến, bảo quản -ti&ecirc;u thụ).</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Xây dựng vùng chuyên canh trồng lúa chất lượng cao 1 triệu ha - ảnh 3\" src=\"https://image.thanhnien.vn/w2048/Uploaded/2022/wohuohb/2022_11_18/anh-chup-man-hinh-2022-11-18-luc-100113-9574.png\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Hội thảo c&oacute; sự tham gia của nhiều chuy&ecirc;n gia kinh tế trong ng&agrave;nh h&agrave;ng l&uacute;a gạo</p>\n\n			<p>ĐỘC LẬP</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Đề &aacute;n đặt mục ti&ecirc;u x&acirc;y dựng v&ugrave;ng chuy&ecirc;n canh sản xuất bền vững 1 triệu ha l&uacute;a chất lượng cao v&ugrave;ng ĐBSCL; đ&aacute;nh gi&aacute; tiềm năng, lợi thế v&agrave; x&aacute;c định địa b&agrave;n bố tr&iacute; v&ugrave;ng chuy&ecirc;n canh sản xuất l&uacute;a chất lượng cao; x&acirc;y dựng phương &aacute;n sản xuất, chế biến, ti&ecirc;u thụ v&agrave; giải ph&aacute;p ph&aacute;t triển bền vững v&ugrave;ng chuy&ecirc;n canh sản xuất l&uacute;a chất lượng cao.</p>\n\n<p>&ldquo;Đề &aacute;n đặt mục ti&ecirc;u n&acirc;ng cao hiệu quả sản xuất v&agrave;&nbsp;<a href=\"https://thanhnien.vn/yeu-cau-cac-ngan-hang-xem-xet-giam-lai-suat-cho-vay-thu-mua-lua-gao-post831256.html\" title=\"Yêu cầu các ngân hàng xem xét giảm lãi suất cho vay thu mua lúa gạo\">kinh doanh l&uacute;a gạo</a>, đảm bảo lợi &iacute;ch b&igrave;nh đẳng v&agrave; tương xứng cho c&aacute;c t&aacute;c nh&acirc;n tham gia ng&agrave;nh l&uacute;a gạo; bảo đảm an ninh lương thực v&agrave; gạo chất lượng cao phục vụ ti&ecirc;u d&ugrave;ng trong nước.&nbsp;<a href=\"https://thanhnien.vn/nong-san-viet-xuat-qua-trung-quoc-nhieu-nhat-post1516232.html\" title=\" Nông sản Việt xuất qua Trung Quốc nhiều nhất\">Xuất khẩu gạo</a>&nbsp;c&oacute; t&iacute;nh cạnh tranh tr&ecirc;n thị trường thế giới v&agrave; c&oacute; gi&aacute; trị gia tăng cao; đảm bảo ph&aacute;t triển bền vững đối với m&ocirc;i trường, bảo vệ t&agrave;i nguy&ecirc;n, th&iacute;ch ứng với biến đổi kh&iacute; hậu&rdquo;, b&agrave; H&ograve;a th&ocirc;ng tin.</p>\n','[{\"file\":\"/images/20221222/a51b0407_689f_4270_ac02_6efa0367d18a_hoa_9240.jpg\",\"extension\":\"jpg\"}]',15,1,'2022-12-22 15:40:44','2022-12-22 15:40:44',1,'xay-dung-vung-chuyen-canh-trong-lua-chat-luong-cao-1-trieu-ha',NULL,NULL),(24,'Nông dân trồng lúa thắng lợi vượt qua tác động biến đổi khí hậu',3,'CẦN THƠ Nhờ bản tin thời tiết nông vụ, nông dân trồng lúa thắng lợi vượt qua tác động biến đổi khí hậu. Từ đó, ngành trồng trọt muốn nhân rộng bản tin trong thời gian tới.','<p><em>CẦN THƠ&nbsp;Nhờ bản tin thời tiết n&ocirc;ng vụ, n&ocirc;ng d&acirc;n trồng l&uacute;a thắng lợi vượt qua t&aacute;c động biến đổi kh&iacute; hậu. Từ đ&oacute;, ng&agrave;nh trồng trọt muốn nh&acirc;n rộng bản tin trong thời gian tới.</em></p>\n\n<p>Giảm đ&aacute;ng kể chi ph&iacute; sản xuất, gia tăng lợi nhuận</p>\n\n<p>Nằm trong khu&ocirc;n khổ Dự &aacute;n &ldquo;&Aacute;p dụng dự b&aacute;o kh&iacute; hậu thời hạn m&ugrave;a v&agrave; c&aacute;c giải ph&aacute;p bảo hiểm ti&ecirc;n tiến v&agrave;o quản l&yacute; rủi ro kh&iacute; hậu trong ng&agrave;nh n&ocirc;ng nghiệp ở Đ&ocirc;ng Nam &Aacute;&rdquo; (gọi tắt l&agrave; dự &aacute;n DeRISK SE Asia). Tại Việt Nam, từ năm 2020, Cục Trồng trọt (Bộ NN-PTNT) v&agrave; Trung t&acirc;m N&ocirc;ng nghiệp Nhiệt đới Quốc tế (CIAT) đ&atilde; triển khai&nbsp;<a href=\"https://nongnghiep.vn/mo-rong-ung-dung-ban-tin-thoi-tiet-nong-vu-d318253.html\" rel=\"noopener\" target=\"_blank\">bản tin thời tiết n&ocirc;ng vụ</a>&nbsp;th&iacute; điểm tại tỉnh Tiền Giang v&agrave; nh&acirc;n rộng ra th&ecirc;m 6 tỉnh, th&agrave;nh v&ugrave;ng ĐBSCL.</p>\n\n<p>Bản tin với mục đ&iacute;ch hướng đến hỗ trợ n&ocirc;ng d&acirc;n trồng l&uacute;a c&oacute; những th&ocirc;ng tin dự b&aacute;o về kh&iacute; tượng thủy văn v&agrave; c&aacute;c khuyến c&aacute;o, hướng dẫn n&ocirc;ng d&acirc;n chăm s&oacute;c, ph&ograve;ng trừ s&acirc;u bệnh trong trồng trọt v&agrave; bảo vệ thực vật (BVTV), trước những t&aacute;c động của biến đổi kh&iacute; hậu (BĐKH) đến sản xuất.</p>\n\n<p>Qua thời gian triển khai,&nbsp;<a href=\"https://nongnghiep.vn/mo-rong-ung-dung-ban-tin-thoi-tiet-nong-vu-d318253.html\" rel=\"noopener\" target=\"_blank\">bản tin thời tiết n&ocirc;ng vụ</a>&nbsp;đ&atilde; thật sự lan tỏa, nh&acirc;n rộng v&agrave; nhận được sự đồng thuận cao của b&agrave; con n&ocirc;ng d&acirc;n. Đ&oacute; l&agrave; khẳng định của &ocirc;ng L&ecirc; Thanh T&ugrave;ng, Ph&oacute; Cục trưởng Cục Trồng Trọt tại Hội thảo &ldquo;Đ&aacute;nh gi&aacute; kết quả nh&acirc;n rộng Bản tin thời tiết n&ocirc;ng vụ ở ĐBSCL&rdquo;, diễn ra ng&agrave;y 28/10 tại TP Cần Thơ.</p>\n\n<p>&nbsp;</p>\n\n<p><img alt=\"Hội thảo \'Đánh giá kết quả nhân rộng Bản tin thời tiết nông vụ ở ĐBSCL\'. Ảnh: Kim Anh.\" src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/10/28/anh-1-160245_822.jpeg\" title=\"anh-1-160245_822.jpeg\" /></p>\n\n<p>Hội thảo &ldquo;Đ&aacute;nh gi&aacute; kết quả nh&acirc;n rộng Bản tin thời tiết n&ocirc;ng vụ ở ĐBSCL&rdquo;. Ảnh:&nbsp;<em>Kim Anh.</em></p>\n\n<p>Ph&acirc;n t&iacute;ch về chi ph&iacute; sản xuất l&uacute;a của n&ocirc;ng d&acirc;n khi &aacute;p dụng bản tin thời tiết n&ocirc;ng vụ tại tỉnh Tiền Giang, b&agrave; L&ecirc; Thị Tầm, điều phối vi&ecirc;n dự &aacute;n DeRISK SE Asia tại Việt Nam cho biết, n&ocirc;ng d&acirc;n tiếp cận v&agrave; &aacute;p dụng bản tin thời tiết n&ocirc;ng vụ giảm được nhiều chi ph&iacute; thuốc BVTV, ph&acirc;n b&oacute;n.</p>\n\n<p>Để đưa ra kết luận n&agrave;y, dự &aacute;n đ&atilde; thực hiện một khảo s&aacute;t với 111 n&ocirc;ng d&acirc;n đang &aacute;p dụng bản tin thời tiết n&ocirc;ng vụ tại c&aacute;c huyện C&aacute;i B&egrave;, Cai Lậy, G&ograve; C&ocirc;ng Đ&ocirc;ng, G&ograve; C&ocirc;ng T&acirc;y v&agrave; T&acirc;n Phước của tỉnh Tiền Giang. Kết quả khả quan, khi gần 40% n&ocirc;ng d&acirc;n cho biết giảm hơn 1,3 triệu đồng/ha chi ph&iacute; thuốc BVTV sau khi &aacute;p dụng bản tin. Việc &aacute;p dụng bản tin cũng g&oacute;p phần gi&uacute;p b&agrave; con tăng năng suất l&uacute;a b&igrave;nh qu&acirc;n khoảng 266 kg/ha v&agrave; tăng lợi nhuận r&ograve;ng trung b&igrave;nh khoảng 2,7 triệu đồng/ha, cao hơn đ&aacute;ng kể so với nh&oacute;m kh&ocirc;ng &aacute;p dụng.</p>\n\n<p>Cần xem x&eacute;t nh&acirc;n rộng bản tin thời tiết n&ocirc;ng vụ</p>\n\n<p>Năm 2022 l&agrave; thời điểm kết th&uacute;c Dự &aacute;n DeRISK SE Asia. &Ocirc;ng L&ecirc; Thanh T&ugrave;ng cho rằng, sự kết th&uacute;c của dự &aacute;n kh&ocirc;ng c&oacute; nghĩa l&agrave; bản tin thời tiết n&ocirc;ng vụ kết th&uacute;c theo. Từ những hiệu quả bước đầu m&agrave; bản tin thời tiết n&ocirc;ng vụ mang lại, bản tin cần được duy tr&igrave; trong qu&aacute; tr&igrave;nh sản xuất. B&ecirc;n cạnh đ&oacute;, bản tin cần cập nhật th&ecirc;m một số th&ocirc;ng tin về thủy sản, nước sạch n&ocirc;ng th&ocirc;n, chuyển đổi cơ cấu c&acirc;y trồng&hellip; từ đ&oacute; cải tiến bản tin sinh động v&agrave; gần gũi hơn với b&agrave; con n&ocirc;ng d&acirc;n.</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><img alt=\"Ông Lê Thanh Tùng, Phó Cục Trưởng Cục Trồng trọt mong muốn bản tin thời tiết nông vụ có thể nhân rộng ra 13 tỉnh thành ĐBSCL. Ảnh: Kim Anh.\" src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/10/28/anh-2-160106_147.jpeg\" title=\"anh-2-160106_147.jpeg\" /></p>\n\n<p>&Ocirc;ng L&ecirc; Thanh T&ugrave;ng, Ph&oacute; Cục Trưởng Cục Trồng trọt mong muốn bản tin thời tiết n&ocirc;ng vụ c&oacute; thể nh&acirc;n rộng ra 13 tỉnh th&agrave;nh ĐBSCL. Ảnh:&nbsp;<em>Kim Anh.</em></p>\n\n<p>&Ocirc;ng T&ugrave;ng mong muốn, thời gian tới&nbsp;<a href=\"https://nongnghiep.vn/mo-rong-ung-dung-ban-tin-thoi-tiet-nong-vu-d318253.html\" rel=\"noopener\" target=\"_blank\">bản tin thời tiết n&ocirc;ng vụ</a>&nbsp;c&oacute; thể nh&acirc;n rộng ra 13 tỉnh th&agrave;nh ĐBSCL. Cục Trồng trọt sẽ x&acirc;y dựng kế hoạch tr&igrave;nh Bộ NN-PTNT để triển khai bản tin tr&ecirc;n diện rộng. Th&ocirc;ng qua, c&aacute;c nguồn lực, c&aacute;c chương tr&igrave;nh, dự &aacute;n quốc tế th&iacute;ch ứng với BĐKH đ&atilde; v&agrave; đang triển khai tại v&ugrave;ng ĐBSCL, tạo tiền đề để mở rộng bản tin n&agrave;y.</p>\n\n<p>&Ocirc;ng T&ugrave;ng cho biết định hướng sắp tới, ng&agrave;nh trồng trọt sẽ nghi&ecirc;n cứu, t&iacute;ch hợp bản tin thời tiết n&ocirc;ng vụ th&agrave;nh một ứng dụng tr&ecirc;n điện thoại di động với sự hỗ trợ về mặt chuy&ecirc;n m&ocirc;n của Đ&agrave;i Kh&iacute; tượng thủy văn, c&aacute;c cơ quan chuy&ecirc;n m&ocirc;n như Cục Bảo vệ thực vật, Tổng Cục Thủy sản, Tổng cục Thủy lợi&hellip;</p>\n\n<p>&ldquo;Những th&ocirc;ng tin đưa v&agrave;o bản tin thiết thực cho từng địa phương sẽ mang lại hiệu quả cao. Trong hoạt động tổ chức sản xuất, đ&acirc;y l&agrave; giải ph&aacute;p, c&aacute;nh tay nối d&agrave;i trong sự chỉ đạo của Sở NN-PTNT gần gũi hơn với b&agrave; con n&ocirc;ng d&acirc;n&rdquo;, &ocirc;ng T&ugrave;ng đ&aacute;nh gi&aacute;.</p>\n\n<p>Tiến sĩ Kees Swaans, Trưởng dự &aacute;n DeRISK SE Asia, Li&ecirc;n minh Tổ chức đa dạng sinh học quốc tế v&agrave; Trung t&acirc;m N&ocirc;ng nghiệp nhiệt đới quốc tế CIAT nh&igrave;n nhận, BĐKH hiện nay đang ảnh hưởng lớn tới những n&ocirc;ng d&acirc;n sản xuất nhỏ. Một trong những v&iacute; dụ điển h&igrave;nh, năm 2016, hạn h&aacute;n x&acirc;m nhập mặn diễn ra trầm trọng ở ĐBSCL g&acirc;y ảnh hưởng đến 400.000 ha đất trồng trọt ở 11/13 tỉnh th&agrave;nh của v&ugrave;ng, g&acirc;y thiệt hại kinh tế l&ecirc;n đến 200 triệu USD, thu nhập của n&ocirc;ng d&acirc;n đ&atilde; giảm tới 75%.</p>\n\n<p>&nbsp;</p>\n\n<p><img alt=\"Tiến sĩ Kees Swaans, Trưởng dự án DeRISK SE Asia định hướng mở rộng bản tin thời tiết nông vụ trên đối tượng sản xuất khác. Ảnh: Kim Anh.\" src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/10/28/anh-3-160140_314.jpeg\" title=\"anh-3-160140_314.jpeg\" /></p>\n\n<p>Tiến sĩ Kees Swaans, Trưởng dự &aacute;n DeRISK SE Asia định hướng mở rộng bản tin thời tiết n&ocirc;ng vụ tr&ecirc;n đối tượng sản xuất kh&aacute;c. Ảnh:&nbsp;<em>Kim Anh.</em></p>\n\n<p>Trước th&aacute;ch thức của BĐKH, Tiến sĩ Kees Swaans cho rằng, bản tin dự b&aacute;o thời tiết n&ocirc;ng vụ đ&oacute;ng vai tr&ograve; quan trọng trong hoạt động sản xuất của n&ocirc;ng d&acirc;n. Qua đ&oacute; gi&uacute;p n&ocirc;ng d&acirc;n chủ động điều chỉnh sản xuất dựa tr&ecirc;n dự b&aacute;o của địa phương v&agrave; ng&agrave;nh chuy&ecirc;n m&ocirc;n.</p>\n\n<p>Hiện nay, bản tin thời tiết n&ocirc;ng vụ đ&atilde; được thực hiện tại 342 x&atilde;, với nhiều bản tin đa dạng được đưa ra như bản tin m&ugrave;a, bản tin th&aacute;ng, bản tin 10 ng&agrave;y bằng nhiều h&igrave;nh thức tuy&ecirc;n truyền. Quan trọng nhất l&agrave; c&aacute;c bản tin c&oacute; gi&aacute; trị thiết thực với n&ocirc;ng d&acirc;n. C&aacute;c bản tin n&agrave;y kh&ocirc;ng chỉ t&aacute;c động đến hoạt động sản xuất n&ocirc;ng nghiệp m&agrave; c&ograve;n quản l&yacute; nguồn nước.</p>\n\n<blockquote>\n<p>Tiếp nối Dự &aacute;n DeRISK SE Asia, một kế hoạch mới đ&atilde; được h&igrave;nh th&agrave;nh trong năm 2022 v&agrave; sẽ triển khai s&acirc;u rộng trong những năm tới l&agrave; s&aacute;ng kiến CGIAR (2022-2030) về Bảo vệ hệ thống lương thực tại c&aacute;c v&ugrave;ng đồng bằng lớn của Ch&acirc;u &Aacute; để đảm bảo sinh kế v&agrave; th&iacute;ch ứng với kh&iacute; hậu (AMD).</p>\n\n<p>Trong giai đoạn 2022 &ndash; 2024, dự &aacute;n sẽ tập trung v&agrave;o c&aacute;c hoạt động gi&uacute;p n&ocirc;ng d&acirc;n x&acirc;y dựng khả năng chống chịu trước ảnh hưởng của BĐKH. Đặc biệt l&agrave; c&aacute;c dịch vụ số, chuỗi gi&aacute; trị, kết nối dịch vụ tư vấn n&ocirc;ng nghiệp. V&agrave; bản tin thời tiết n&ocirc;ng vụ sẽ được lồng gh&eacute;p trong c&aacute;c chương tr&igrave;nh của cấp cao hơn. Tiến sĩ Kees Swaans cũng định hướng mở rộng bản tin thời tiết n&ocirc;ng vụ tr&ecirc;n đối tượng sản xuất kh&aacute;c như: t&ocirc;m, tr&aacute;i c&acirc;y&hellip;</p>\n</blockquote>\n','[{\"file\":\"/images/20221219/thu_tuong_pmc_551.jpg\",\"extension\":\"jpg\"}]',2,1,'2022-12-22 15:43:46','2022-12-22 15:43:46',1,'nong-dan-trong-lua-thang-loi-vuot-qua-tac-dong-bien-doi-khi-hau',NULL,NULL),(25,'Năm quốc gia đứng đầu thế giới về công nghệ nông nghiệp',2,'Trước vấn đề mất an ninh lương thực đang rình rập và các xu hướng vào năm 2023, hãy tham khảo năm quốc gia tiên tiến nhất thế giới về công nghệ nông nghiệp.','<p><img alt=\"Khởi nghiệp Indigo (Mỹ) đã triển khai vườn ươm Flagship Pioneering, giúp nông dân sử dụng vi khuẩn thực vật để cải thiện sức khỏe và năng suất cây trồng, tập trung vào bông, lúa mì, ngô, đậu nành và gạo, với mục tiêu giúp cây trồng sử dụng nước hiệu quả hơn. Ảnh: TechCrunch\" data-src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/20/c4k38pzxuaanfcc800-110440_653.jpg\" data-was-processed=\"true\" height=\"453\" src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/20/c4k38pzxuaanfcc800-110440_653.jpg\" title=\"c4k38pzxuaanfcc800-110440_653.jpg\" width=\"680\" /></p>\n\n<p>Khởi nghiệp Indigo (Mỹ) đ&atilde; triển khai vườn ươm Flagship Pioneering, gi&uacute;p n&ocirc;ng d&acirc;n sử dụng vi khuẩn thực vật để cải thiện sức khỏe v&agrave; năng suất c&acirc;y trồng, tập trung v&agrave;o b&ocirc;ng, l&uacute;a m&igrave;, ng&ocirc;, đậu n&agrave;nh v&agrave; gạo, với mục ti&ecirc;u gi&uacute;p c&acirc;y trồng sử dụng nước hiệu quả hơn. Ảnh:&nbsp;<em>TechCrunch</em></p>\n\n<h2>1/ Trung Quốc: Số lượng c&ocirc;ng ty&nbsp;<a href=\"https://nongnghiep.vn/khoi-nghiep-cong-nghe-nong-nghiep-tag160866/\" rel=\"follow\">khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp</a>: 368</h2>\n\n<p>Trung Quốc l&agrave; một trong những nh&agrave; sản xuất n&ocirc;ng sản lớn nhất thế giới. Ng&agrave;nh n&ocirc;ng nghiệp của quốc gia n&agrave;y đ&atilde; đ&oacute;ng g&oacute;p đ&aacute;ng kể khi đạt 7% v&agrave;o tổng sản phẩm quốc nội (GDP) của đất nước v&agrave;o năm 2021. Đ&acirc;y cũng l&agrave; quốc gia đ&ocirc;ng d&acirc;n số nhất thế giới, với d&acirc;n số tr&ecirc;n 1,4 tỷ người.</p>\n\n<p>Đất nước phải nu&ocirc;i sống hơn một tỷ người trong bối cảnh n&ocirc;ng nghiệp ng&agrave;y c&agrave;ng kh&oacute; khăn, điều n&agrave;y đ&atilde; khiến Trung Quốc phải đầu tư đ&aacute;ng kể v&agrave;o c&ocirc;ng nghệ n&ocirc;ng nghiệp, một phần l&agrave; do tầm nh&igrave;n v&agrave; chủ trương của đất nước l&agrave; tự cung tự cấp về an ninh lương thực.&nbsp;</p>\n\n<p>Tr&ecirc;n thực tế, thị trường n&ocirc;ng nghiệp th&ocirc;ng minh của Trung Quốc đ&atilde; tăng nhanh ch&oacute;ng từ 14 tỷ USD v&agrave;o năm 2015 l&ecirc;n 27 tỷ USD v&agrave;o năm 2020. Theo số liệu từ h&atilde;ng thống k&ecirc; Statista, xu thế n&agrave;y đ&atilde; dẫn đến sự xuất hiện của c&aacute;c c&ocirc;ng ty khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp kiểu như FJ Dynamics- nh&agrave; sản xuất hệ thống tự động cho nhiều ng&agrave;nh, bao gồm cả n&ocirc;ng nghiệp.</p>\n\n<p>Tuy nhi&ecirc;n, điều đ&aacute;ng n&oacute;i l&agrave; c&oacute; rất &iacute;t c&ocirc;ng ty khởi nghiệp trong hệ sinh th&aacute;i c&ocirc;ng nghệ n&ocirc;ng nghiệp (agritech) của Trung Quốc vẫn đang cố gắng giải quyết vấn đề sản xuất n&ocirc;ng nghiệp kh&ocirc;ng bền vững, do hầu hết đều tập trung v&agrave;o việc sử dụng c&ocirc;ng nghệ để đạt hiệu quả v&agrave; mở rộng quy m&ocirc; kinh doanh.</p>\n\n<h4>2/ Australia: Số lượng c&ocirc;ng ty khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp: 526</h4>\n\n<p>Australia l&agrave; một quốc gia đại dương kh&aacute;c trong danh s&aacute;ch c&aacute;c quốc gia ti&ecirc;n tiến nhất về c&ocirc;ng nghệ n&ocirc;ng nghiệp. Xứ sở chuột t&uacute;i hiện c&oacute; tổng cộng 526 c&ocirc;ng ty khởi nghiệp agritech cung cấp c&aacute;c sản phẩm v&agrave; dịch vụ n&ocirc;ng nghiệp th&ocirc;ng minh.</p>\n\n<p>Quốc gia n&agrave;y hiện đang tập trung triển khai nhiều mảng c&ocirc;ng nghệ kh&aacute;c nhau như n&ocirc;ng nghiệp ch&iacute;nh x&aacute;c, tưới ti&ecirc;u th&ocirc;ng minh để tiết kiệm nước, th&ocirc;ng qua c&aacute;c c&ocirc;ng ty như Farmbot, Goanna AG v&agrave; Stacked Farm.</p>\n\n<h3>3/ Canada: Số lượng c&ocirc;ng ty khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp: 632</h3>\n\n<p>Canada c&oacute; 632 c&ocirc;ng ty khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp đang cố gắng th&uacute;c đẩy đất nước hướng tới nền n&ocirc;ng nghiệp bền vững v&agrave; hiệu quả. Đ&acirc;y được coi l&agrave; một trong những quốc gia ti&ecirc;n tiến nhất về c&ocirc;ng nghệ n&ocirc;ng nghiệp.</p>\n\n<p>Thị phần n&ocirc;ng nghiệp ch&iacute;nh x&aacute;c ở Canada đ&atilde; đạt gần 0,9 tỷ USD v&agrave;o năm 2021, cho thấy mức tăng trưởng 11,5% h&agrave;ng năm. Trong đ&oacute; một số c&ocirc;ng ty đ&aacute;ng ch&uacute; &yacute; nhất hoạt động trong ng&agrave;nh n&ocirc;ng nghiệp th&ocirc;ng minh bao gồm Semios, Goodleaf Farms v&agrave; 80 Acres Farms. Xuất khẩu h&agrave;ng h&oacute;a n&ocirc;ng nghiệp năm 2021 của Canada l&ecirc;n tới 82 tỷ USD.</p>\n\n<h2>4/ Vương quốc Anh: Số lượng c&ocirc;ng ty khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp: 689</h2>\n\n<p>Vương quốc Anh c&oacute; 689 c&ocirc;ng ty khởi nghiệp trong hệ sinh th&aacute;i c&ocirc;ng nghệ n&ocirc;ng nghiệp v&agrave; n&ocirc;ng nghiệp th&ocirc;ng minh. Xu thế n&agrave;y diễn ra trong bối cảnh nhiều v&ugrave;ng đất rộng lớn ở đất nước n&agrave;y đ&atilde; dần bị tho&aacute;i h&oacute;a trong suốt 200 năm qua do sản xuất n&ocirc;ng nghiệp th&acirc;m canh.</p>\n\n<p>V&agrave;o năm 2017, &ocirc;ng Michael Gove, khi đ&oacute; l&agrave; Bộ trưởng M&ocirc;i trường của đất nước, đ&atilde; cảnh b&aacute;o rằng vương quốc Anh c&ograve;n 30 đến 40 năm nữa mới c&oacute; thể giải quyết cơ bản vấn đề độ ph&igrave; nhi&ecirc;u của đất ở nhiều v&ugrave;ng miền của đất nước.</p>\n\n<p>Cuộc khủng hoảng n&agrave;y đ&atilde; khiến Vương quốc Anh đầu tư mạnh v&agrave;o c&aacute;c giải ph&aacute;p c&ocirc;ng nghệ n&ocirc;ng nghiệp, dẫn đến sự xuất hiện của ng&agrave;nh n&ocirc;ng nghiệp th&ocirc;ng minh được định gi&aacute; 17 tỷ USD v&agrave;o năm 2018.</p>\n\n<p>Theo b&aacute;o c&aacute;o của Business Weekly, v&agrave;o năm 2021, ng&agrave;nh n&ocirc;ng nghiệp nước n&agrave;y đ&atilde; bổ sung th&ecirc;m 1,6 tỷ USD v&agrave;o nỗ lực n&agrave;y th&ocirc;ng qua c&aacute;c giao dịch quy m&ocirc; lớn về đầu tư mạo hiểm v&agrave; huy động vốn từ cộng đồng. Theo đ&oacute;, một số c&ocirc;ng ty khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp đ&aacute;ng ch&uacute; &yacute; trong nước bao gồm FarmDrop, giải ph&aacute;p canh t&aacute;c theo chiều dọc Eider v&agrave; giải ph&aacute;p tăng trưởng th&ocirc;ng minh.</p>\n\n<h3>5/ Mỹ: Số lượng c&ocirc;ng ty khởi nghiệp c&ocirc;ng nghệ n&ocirc;ng nghiệp: 3.475</h3>\n\n<p><img alt=\"Trang trại thẳng đứng trong nhà thế hệ tiếp theo của Plenty có tên là Tigris của Mỹ được tỷ phú công nghệ Jeff Bezos hậu thuẫn. Ảnh: Getty Images\" data-src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/20/plenty-110517_916.jpg\" data-was-processed=\"true\" height=\"680\" src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/20/plenty-110517_916.jpg\" title=\"plenty-110517_916.jpg\" width=\"680\" /></p>\n\n<p>Trang trại thẳng đứng trong nh&agrave; thế hệ tiếp theo của Plenty c&oacute; t&ecirc;n l&agrave; Tigris của Mỹ được tỷ ph&uacute; c&ocirc;ng nghệ Jeff Bezos hậu thuẫn. Ảnh:&nbsp;<em>Getty Images</em></p>\n\n<p>Mỹ l&agrave; quốc gia đứng ở vị tr&iacute; đầu ti&ecirc;n về số lượng c&aacute;c c&ocirc;ng ty đang hoạt động trong lĩnh vực c&ocirc;ng nghệ n&ocirc;ng nghiệp, khi bỏ xa tất cả số lượng c&aacute;c c&ocirc;ng ty khởi nghiệp của c&aacute;c quốc gia kh&aacute;c với một bi&ecirc;n độ rất lớn, ở mức 3.475 c&ocirc;ng ty. Trong đ&oacute;, một số c&aacute;i t&ecirc;n đ&aacute;ng ch&uacute; &yacute; trong ng&agrave;nh bao gồm Apeel, Plenty, Indigo, GrubMarket v&agrave; Ginkgo Biowork.</p>\n\n<p>Đặc biệt nền kinh tế số một thế giới đang sở hữu nhiều c&ocirc;ng nghệ được triển khai trong lĩnh vực n&ocirc;ng nghiệp của đất nước như n&ocirc;ng nghiệp ch&iacute;nh x&aacute;c v&agrave; canh t&aacute;c thẳng đứng trong nh&agrave;.</p>\n','[{\"file\":\"/images/20221219/1670224351037thu_tuong_pham_minh_chinh_techfest_2022.jpg\",\"extension\":\"jpg\"}]',0,1,'2022-12-22 15:47:08','2022-12-22 15:47:08',1,'nam-quoc-gia-dung-dau-the-gioi-ve-cong-nghe-nong-nghiep',NULL,NULL),(26,'Sản xuất phân bón không phát thải carbon',2,'Giải pháp này được coi là cấp thiết, không chỉ để bảo vệ mục tiêu khí hậu mà còn giảm sự phụ thuộc vào khí đốt tự nhiên và tăng cường an ninh lương thực.','<p><img alt=\"Phân đạm có nguồn gốc từ than sinh học làm giảm phát thải khí nhà kính và tăng năng suất ngô 21% so với urê. Nguồn: Sciencedirect\" data-src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/21/1-s20-s0048969719353677-ga1_lrg-091656_228.jpg\" data-was-processed=\"true\" height=\"308\" src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/21/1-s20-s0048969719353677-ga1_lrg-091656_228.jpg\" title=\"1-s20-s0048969719353677-ga1_lrg-091656_228.jpg\" width=\"680\" /></p>\n\n<p>Ph&acirc;n đạm c&oacute; nguồn gốc từ than sinh học l&agrave;m giảm ph&aacute;t thải kh&iacute; nh&agrave; k&iacute;nh v&agrave; tăng năng suất ng&ocirc; 21% so với ur&ecirc;. Nguồn:&nbsp;<em>Sciencedirect</em></p>\n\n<p>Tr&ecirc;n thực tế, n&ocirc;ng nghiệp th&acirc;m canh chỉ c&oacute; thể khả thi nếu đất được b&oacute;n ph&acirc;n đạm, l&acirc;n v&agrave; kali. Trong khi phốt pho v&agrave; kali c&oacute; thể được khai th&aacute;c dưới dạng muối, th&igrave; ph&acirc;n đạm phải được sản xuất một c&aacute;ch c&ocirc;ng phu từ nitơ trong kh&ocirc;ng kh&iacute; v&agrave; từ hydro. Tuy nhi&ecirc;n việc sản xuất hydro cực kỳ tốn năng lượng, hiện đang cần một lượng lớn kh&iacute; đốt tự nhi&ecirc;n hoặc như ở Trung Quốc l&agrave; than đ&aacute;. B&ecirc;n cạnh việc tạo ra lượng kh&iacute; thải carbon lớn tương ứng, sản xuất ph&acirc;n đạm dễ bị ảnh hưởng bởi c&aacute;c c&uacute; sốc gi&aacute; tr&ecirc;n thị trường nhi&ecirc;n liệu h&oacute;a thạch.</p>\n\n<p>Paolo Gabrielli, nh&agrave; khoa học cấp cao tại Ph&ograve;ng th&iacute; nghiệm tại ETH Zurich v&agrave; cộng sự Lorenzo Rosa, tại Viện Khoa học Carnegie ở Stanford (Mỹ) đ&atilde; phối hợp nghi&ecirc;n cứu phương ph&aacute;p&nbsp;<a href=\"https://nongnghiep.vn/chi-phi-san-xuat-phan-bon-tang-tag145449/\" rel=\"follow noopener\" target=\"_blank\">sản xuất ph&acirc;n b&oacute;n</a>&nbsp;nitơ kh&ocirc;ng g&acirc;y ra kh&iacute; thải carbon.</p>\n\n<p>Trong một nghi&ecirc;n cứu đăng tr&ecirc;n tạp ch&iacute;&nbsp;<em>Environmental Research Letters,</em>&nbsp;hai nh&agrave; nghi&ecirc;n cứu kết luận rằng c&oacute; thể c&oacute; sự chuyển đổi trong sản xuất nitơ v&agrave; sự chuyển đổi như vậy cũng c&oacute; thể l&agrave;m tăng an ninh lương thực. Tuy nhi&ecirc;n, c&aacute;c phương ph&aacute;p sản xuất thay thế đều c&oacute; ưu điểm v&agrave; nhược điểm. Cụ thể, hai nh&agrave; nghi&ecirc;n cứu đ&atilde; kiểm tra ba lựa chọn thay thế:</p>\n\n<p>Sản xuất hydro cần thiết bằng c&aacute;ch sử dụng nhi&ecirc;n liệu h&oacute;a thạch như trong hoạt động kinh doanh th&ocirc;ng thường, chỉ thay v&igrave; thải kh&iacute; nh&agrave; k&iacute;nh CO2 v&agrave;o kh&iacute; quyển, n&oacute; được thu giữ trong c&aacute;c nh&agrave; m&aacute;y sản xuất v&agrave; lưu trữ vĩnh viễn dưới l&ograve;ng đất. Điều n&agrave;y kh&ocirc;ng chỉ đ&ograve;i hỏi một cơ sở hạ tầng để thu giữ, vận chuyển v&agrave; lưu trữ CO2 m&agrave; c&ograve;n cần nhiều năng lượng hơn tương ứng. Mặc d&ugrave; vậy, n&oacute; l&agrave; một phương ph&aacute;p sản xuất tương đối hiệu quả, tuy nhi&ecirc;n quy tr&igrave;nh n&agrave;y kh&ocirc;ng li&ecirc;n quan đến vấn đề giảm sự phụ thuộc v&agrave;o nhi&ecirc;n liệu h&oacute;a thạch.</p>\n\n<p>Điện kh&iacute; h&oacute;a sản xuất ph&acirc;n b&oacute;n bằng c&aacute;ch sử dụng điện ph&acirc;n nước để sản xuất hydro. Điều n&agrave;y đ&ograve;i hỏi nguồn năng lượng trung b&igrave;nh gấp 25 lần so với phương ph&aacute;p sản xuất ng&agrave;y nay sử dụng kh&iacute; đốt tự nhi&ecirc;n, do đ&oacute; sẽ cần một lượng điện khổng lồ từ c&aacute;c nguồn trung h&ograve;a carbon. Đối với c&aacute;c quốc gia c&oacute; nguồn năng lượng mặt trời hoặc năng lượng gi&oacute; dồi d&agrave;o, đ&acirc;y c&oacute; thể l&agrave; một c&aacute;ch tiếp cận hấp dẫn v&agrave; tiềm năng. Tuy nhi&ecirc;n, với c&aacute;c kế hoạch điện kh&iacute; h&oacute;a c&aacute;c lĩnh vực kh&aacute;c của nền kinh tế dưới danh nghĩa h&agrave;nh động v&igrave; kh&iacute; hậu, n&oacute; c&oacute; thể dẫn đến sự cạnh tranh về điện năng bền vững.</p>\n\n<p>Qu&aacute; tr&igrave;nh tổng hợp hydro để sản xuất ph&acirc;n b&oacute;n từ sinh khối đ&ograve;i hỏi nhiều đất canh t&aacute;c v&agrave; nước. Trớ tr&ecirc;u thay, phương thức sản xuất n&agrave;y lại cạnh tranh với sản xuất lương thực. Nhưng c&aacute;c t&aacute;c giả của nghi&ecirc;n cứu chỉ ra rằng sẽ hợp l&yacute; nếu nguy&ecirc;n liệu đầu v&agrave;o l&agrave; sinh khối phế thải, v&iacute; dụ như tận dụng nguồn phế phụ phẩm c&acirc;y trồng.</p>\n\n<p>C&aacute;c nh&agrave; khoa học cho rằng, ch&igrave;a kh&oacute;a th&agrave;nh c&ocirc;ng c&oacute; thể l&agrave; sự kết hợp của tất cả c&aacute;c phương ph&aacute;p n&agrave;y t&ugrave;y thuộc v&agrave;o từng quốc gia, điều kiện cụ thể của địa phương v&agrave; c&aacute;c nguồn lực sẵn c&oacute;. Trong bất cứ trường hợp n&agrave;o, n&ocirc;ng nghiệp bắt buộc phải sử dụng ph&acirc;n b&oacute;n nitơ hiệu quả hơn, đ&uacute;ng như quan điểm m&agrave; chuy&ecirc;n gia Lorenzo Rosa nhấn mạnh: &quot;Giải quyết c&aacute;c vấn đề như b&oacute;n ph&acirc;n qu&aacute; mức v&agrave; l&atilde;ng ph&iacute; thực phẩm cũng l&agrave; một c&aacute;ch để giảm nhu cầu ph&acirc;n b&oacute;n.&quot;</p>\n\n<h3>Nguy cơ đối với Ấn Độ v&agrave; Trung Quốc</h3>\n\n<p>Trong nghi&ecirc;n cứu, c&aacute;c nh&agrave; khoa học cũng t&igrave;m c&aacute;ch x&aacute;c định c&aacute;c quốc gia tr&ecirc;n thế giới m&agrave; an ninh lương thực hiện đang chịu rủi ro, nhất l&agrave; khi phải phụ thuộc v&agrave;o nhập khẩu nitơ hoặc kh&iacute; đốt tự nhi&ecirc;n. Theo đ&oacute;, c&aacute;c quốc gia sau đ&acirc;y (đặc biệt dễ bị tổn thương trước những c&uacute; sốc gi&aacute; tr&ecirc;n thị trường kh&iacute; đốt tự nhi&ecirc;n v&agrave; nitơ) gồm Ấn Độ, Brazil, Trung Quốc, Ph&aacute;p, Thổ Nhĩ Kỳ v&agrave; Đức.</p>\n\n<p>Sản xuất ph&acirc;n b&oacute;n khử carbon trong nhiều trường hợp sẽ l&agrave;m giảm lỗ hổng n&agrave;y v&agrave; tăng cường an ninh lương thực. &Iacute;t nhất, điện kh&iacute; h&oacute;a th&ocirc;ng qua năng lượng t&aacute;i tạo hoặc sử dụng sinh khối sẽ l&agrave;m giảm sự phụ thuộc v&agrave;o nguồn nhập khẩu kh&iacute; đốt tự nhi&ecirc;n. Tuy nhi&ecirc;n, c&aacute;c nh&agrave; nghi&ecirc;n cứu đưa ra quan điểm: Tất cả c&aacute;c phương ph&aacute;p sản xuất ph&acirc;n đạm kh&ocirc;ng ph&aacute;t thải carbon đều ti&ecirc;u tốn nhiều năng lượng hơn so với phương ph&aacute;p sử dụng nhi&ecirc;n liệu h&oacute;a thạch hiện tại. N&oacute;i c&aacute;ch kh&aacute;c, sẽ vẫn dễ bị tổn thương trước những c&uacute; sốc gi&aacute; nhất định mặc d&ugrave; kh&ocirc;ng phải trực tiếp tr&ecirc;n thị trường kh&iacute; đốt tự nhi&ecirc;n, m&agrave; c&oacute; lẽ tr&ecirc;n thị trường điện.</p>\n\n<p>Điều n&agrave;y buộc c&aacute;c nh&agrave; sản xuất nitơ phải đối mặt với sự thay đổi. C&aacute;c nh&agrave; khoa học chỉ ra trong nghi&ecirc;n cứu của họ rằng, qu&aacute; tr&igrave;nh khử carbon c&oacute; khả năng thay đổi danh s&aacute;ch c&aacute;c quốc gia sản xuất v&agrave; xuất khẩu ph&acirc;n b&oacute;n nitơ lớn như Nga, Trung Quốc, Ai Cập, Qatar v&agrave; Ả Rập Saudi. Ngoại trừ Trung Quốc phải nhập khẩu kh&iacute; đốt tự nhi&ecirc;n, tất cả c&aacute;c quốc gia n&agrave;y đều c&oacute; thể sử dụng nguồn dự trữ kh&iacute; đốt tự nhi&ecirc;n của m&igrave;nh.</p>\n\n<p>Trong tương lai, c&aacute;c quốc gia c&oacute; khả năng được hưởng lợi từ qu&aacute; tr&igrave;nh khử carbon sẽ l&agrave; những quốc gia tạo ra nhiều năng lượng mặt trời v&agrave; gi&oacute;, đồng thời c&oacute; đủ nguồn t&agrave;i nguy&ecirc;n đất v&agrave; nước, chẳng hạn như Canada v&agrave; Mỹ.</p>\n\n<blockquote>\n<p>&Ocirc;ng Gabrielli n&oacute;i: &ldquo;Thực tế l&agrave; ch&uacute;ng ta cần phải l&agrave;m cho nhu cầu nitơ trong sản xuất n&ocirc;ng nghiệp trở n&ecirc;n bền vững hơn trong tương lai, để đ&aacute;p ứng cả hai mục ti&ecirc;u kh&iacute; hậu v&agrave; an ninh lương thực. Cuộc xung đột ở Ukraine đang ảnh hưởng đến thị trường lương thực to&agrave;n cầu kh&ocirc;ng chỉ v&igrave; quốc gia n&agrave;y thường xuất khẩu nhiều ngũ cốc m&agrave; c&ograve;n v&igrave; n&oacute; đ&atilde; đẩy gi&aacute; kh&iacute; đốt tự nhi&ecirc;n tăng cao, khiến gi&aacute; ph&acirc;n b&oacute;n nitơ tăng l&ecirc;n. Hệ quả l&agrave; một số nh&agrave; sản xuất ph&acirc;n b&oacute;n đ&atilde; buộc phải tạm ngừng sản xuất v&igrave; chi ph&iacute; kh&iacute; đốt qu&aacute; cao khiến cho hoạt động sản xuất, kinh doanh trở n&ecirc;n kh&ocirc;ng c&oacute; hiệu quả kinh tế.</p>\n</blockquote>\n','[{\"file\":\"/images/20221219/thu_tuong_pmc_551.jpg\",\"extension\":\"jpg\"}]',1,1,'2022-12-22 15:49:21','2022-12-22 15:49:21',1,'san-xuat-phan-bon-khong-phat-thai-carbon',NULL,NULL),(27,'686,5 triệu tấn: Trung Quốc lập kỳ tích mới về sản lượng lương thực',2,'Bất chấp các loại hình thời tiết bất lợi, ngành nông nghiệp Trung Quốc vẫn đạt kỷ lục mới về sản lượng trong năm 2022, với 686,53 triệu tấn, tăng 0,5% so với năm 2021.','<p><img alt=\"Ảnh chụp từ trên cao một chiếc máy gặt đập liên hợp của nông dân tỉnh Giang Tây đang thu hoạch lúa mùa sớm. Ảnh: VCG\" data-src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/14/99a936a7-2820-4679-bbf0-2450387f4c6b-153037_599.jpeg\" data-was-processed=\"true\" height=\"408\" src=\"https://i.ex-cdn.com/nongnghiep.vn/files/content/2022/12/14/99a936a7-2820-4679-bbf0-2450387f4c6b-153037_599.jpeg\" title=\"99a936a7-2820-4679-bbf0-2450387f4c6b-153037_599.jpeg\" width=\"680\" /></p>\n\n<p>Ảnh chụp từ tr&ecirc;n cao một chiếc m&aacute;y gặt đập li&ecirc;n hợp của n&ocirc;ng d&acirc;n tỉnh Giang T&acirc;y đang thu hoạch l&uacute;a m&ugrave;a sớm. Ảnh:&nbsp;<em>VCG</em></p>\n\n<p>C&aacute;c chuy&ecirc;n gia cho biết, kỳ t&iacute;ch kh&oacute; khăn n&agrave;y chứng minh cho phản ứng c&oacute; mục ti&ecirc;u v&agrave; hiệu quả của đất nước trước một loạt c&aacute;c cơn gi&oacute; ngược, khi an ninh lương thực được đặt l&ecirc;n h&agrave;ng đầu trong chương tr&igrave;nh nghị sự của ch&iacute;nh phủ.</p>\n\n<p>Với việc đạt được sản lượng n&agrave;y, Trung Quốc kh&ocirc;ng những cung cấp đủ lương thực cho gần 1/5 d&acirc;n số thế giới trong mọi ho&agrave;n cảnh, m&agrave; c&ograve;n được kỳ vọng sẽ tiếp tục l&agrave; nền tảng của an ninh lương thực v&agrave; ổn định kinh tế to&agrave;n cầu.</p>\n\n<p>Theo Cục Thống k&ecirc; Quốc gia Trung Quốc, sản lượng lương thực to&agrave;n quốc năm 2022 đạt mức kỷ lục mới l&agrave; 686,53 tỷ kg, tăng 0,5% so với năm trước. Con số n&agrave;y đ&aacute;nh dấu năm thứ t&aacute;m li&ecirc;n tiếp tổng sản lượng ngũ cốc của nước n&agrave;y vượt qu&aacute; 650 triệu tấn.</p>\n\n<p>Năm nay tổng iện t&iacute;ch&nbsp;<a href=\"https://nongnghiep.vn/dat-canh-tac-cua-trung-quoc-tag160708/\" rel=\"follow\">đất canh t&aacute;c của Trung Quốc</a>&nbsp;đạt 1,775 tỷ mu (118,33 triệu ha), tăng 0,6% so với năm ngo&aacute;i. T&iacute;nh trung b&igrave;nh, mỗi đơn vị sản lượng ngũ cốc (mu) đạt 387 kg.</p>\n\n<p>&Ocirc;ng Wang Guirong, một quan chức của Cục Thống k&ecirc; cho biết, vụ thu hoạch bội thu năm nay diễn ra bất chấp c&aacute;c điều kiện thời tiết khắc nghiệt trong nước, bao gồm lũ lụt ở miền bắc, hạn h&aacute;n k&eacute;o d&agrave;i v&agrave; nhiệt độ cao ở miền nam, b&ecirc;n cạnh sự gi&aacute;n đoạn do dịch COVID-19. Ngo&agrave;i ra, gi&aacute; ph&acirc;n b&oacute;n tăng đột biến trong bối cảnh căng thẳng ở Ukraine đ&atilde; g&acirc;y &aacute;p lực lớn l&ecirc;n thị trường lương thực trong nước.</p>\n\n<p>Giới chuy&ecirc;n gia cho rằng, đằng sau con số kỷ lục n&agrave;y l&agrave; một loạt c&aacute;c biện ph&aacute;p đồng bộ được triển khai tr&ecirc;n nhiều mặt để bảo vệ sản xuất n&ocirc;ng nghiệp, gi&uacute;p n&ocirc;ng d&acirc;n quản l&yacute; c&acirc;y trồng tốt, tr&aacute;nh khỏi những t&aacute;c động xấu của thời tiết.</p>\n\n<p>Trong nhiều năm qua, giới l&atilde;nh đạo Trung Quốc đ&atilde; m&ocirc; tả an ninh lương thực l&agrave; một trong những lợi &iacute;ch cơ bản nhất của đất nước, v&agrave; đ&atilde; nhiều lần nhấn mạnh đến việc bảo vệ v&agrave; cải thiện đất n&ocirc;ng nghiệp, ph&aacute;t triển ng&agrave;nh c&ocirc;ng nghiệp hạt giống, thu thập v&agrave; bảo vệ nguồn gen cũng như hỗ trợ n&ocirc;ng d&acirc;n v&agrave; ng&agrave;nh n&ocirc;ng nghiệp bằng mọi c&aacute;ch c&oacute; thể.</p>\n\n<p>&ldquo;L&agrave; nh&agrave; nhập khẩu r&ograve;ng n&ocirc;ng sản, nhập khẩu n&ocirc;ng sản của Trung Quốc tăng 9,4% so với c&ugrave;ng kỳ l&ecirc;n 20,62 tỷ USD trong th&aacute;ng 11, trong khi xuất khẩu tăng 4,6% l&ecirc;n 8,83 tỷ USD&rdquo;, theo thống k&ecirc; của hải quan Trung Quốc.</p>\n\n<p>He Xiurong, gi&aacute;o sư tại Đại học N&ocirc;ng nghiệp Trung Quốc cho biết, c&aacute;c mặt h&agrave;ng nhập khẩu của Trung Quốc chủ yếu bao gồm đậu tương, chiếm hơn 70% tổng lượng nhập khẩu, trong khi nhập khẩu r&ograve;ng ngũ cốc của nước n&agrave;y chỉ chiếm khoảng 3% tổng sản lượng ngũ cốc trong 5 năm qua.</p>\n\n<p>C&aacute;c nh&agrave; quan s&aacute;t cho biết, trong khi những hậu quả của căng thẳng địa ch&iacute;nh trị c&oacute; thể vẫn l&agrave; lực cản đối với an ninh lương thực to&agrave;n cầu v&agrave;o năm tới, th&igrave; khả năng ứng ph&oacute; tốt với c&aacute;c trường hợp khẩn cấp v&agrave; c&aacute;ch tiếp cận c&oacute; tr&aacute;ch nhiệm của Trung Quốc trong việc cung cấp lương thực cho d&acirc;n số khổng lồ của m&igrave;nh sẽ khiến nước n&agrave;y trở th&agrave;nh nền tảng bền vững của an ninh lương thực to&agrave;n cầu.</p>\n\n<p>Đại diện Cục Thống k&ecirc; cho biết, sản lượng bội thu năm nay đ&atilde; tạo nền tảng vững chắc để Trung Quốc đối ph&oacute; với m&ocirc;i trường quốc tế phức tạp v&agrave; đầy biến động, vượt qua rủi ro v&agrave; th&aacute;ch thức, đồng thời ổn định tăng trưởng kinh tế v&agrave; đảm bảo nền kinh tế mở trong phạm vi hợp l&yacute;.</p>\n\n<p>V&agrave;o cuối th&aacute;ng 9 năm nay, Quỹ Tiền tệ quốc tế (IMF) cho biết cuộc khủng hoảng Ukraine &quot;đ&atilde; l&agrave;m trầm trọng th&ecirc;m t&igrave;nh trạng mất an ninh lương thực vốn đ&atilde; gia tăng trong nửa thập kỷ qua&quot;. Theo định chế n&agrave;y, c&aacute;c quốc gia c&oacute; thu nhập thấp đang chịu ảnh hưởng nặng nề nhất, khi nhận định rằng &quot;c&uacute; sốc gi&aacute; lương thực v&agrave; ph&acirc;n b&oacute;n sẽ l&agrave;m đội th&ecirc;m 9 tỷ USD v&agrave;o h&oacute;a đơn nhập khẩu của 48 quốc gia bị ảnh hưởng nặng nề nhất trong 2 năm 2022 v&agrave; 2023&quot;.</p>\n\n<p>C&aacute;c nh&agrave; ph&acirc;n t&iacute;ch cho biết, Trung Quốc được dự đo&aacute;n sẽ tiếp tục đ&oacute;ng vai tr&ograve; l&agrave; nước ổn định then chốt đối với an ninh lương thực to&agrave;n cầu, nhất l&agrave; tại một số quốc gia ch&acirc;u Phi v&agrave; Mỹ Latinh, c&oacute; thể phải đối mặt với viễn cảnh tồi tệ hơn khi căng thẳng địa ch&iacute;nh trị sẽ tiếp tục k&eacute;o d&agrave;i đến năm 2023.</p>\n','[{\"file\":\"/images/20221219/vnnic.jpg\",\"extension\":\"jpg\"}]',8,1,'2022-12-22 15:50:25','2022-12-22 15:50:25',1,'6865-trieu-tan-trung-quoc-lap-ky-tich-moi-ve-san-luong-luong-thuc',NULL,NULL);
