CREATE TABLE `coursegroups` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `courseGroupsName` varchar(500) DEFAULT NULL COMMENT 'tên sản phẩm',
  `image` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`image`)),
  `courseGroupsCode` varchar(45) DEFAULT NULL,
  `type` int(11) DEFAULT 0,
  `descriptions` text DEFAULT NULL,
  `countUsers` int(11) DEFAULT 0,
  `money` int(11) DEFAULT 0,
  `userCreatorsId` bigint(20) DEFAULT NULL COMMENT 'người tạo/Người bán',
  `status` int(11) DEFAULT NULL COMMENT 'trạng thái:\\\\nFalse: ẩn sản phẩm\\\\nTrue: hiển thị sản phẩm',
  `dateCreated` datetime DEFAULT current_timestamp() COMMENT 'ngày tạo',
  `dateUpdated` datetime DEFAULT current_timestamp(),
  `courseTypesId` bigint(20) DEFAULT NULL,
  `level` int(11) DEFAULT 1 COMMENT '1: dễ ,2 trung bình , 3 nâng cao',
  `countCourseLevels` int(11) DEFAULT 0,
  `countCoursesStatus` int(1) DEFAULT 0,
  `age` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `courseGroups_status_IDX` (`status`) USING BTREE,
  KEY `courseGroups_courseTypesId_IDX` (`courseTypesId`) USING BTREE,
  KEY `courseGroups_countCoursesStatus_IDX` (`countCoursesStatus`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='khóa học';
